import { useParams } from "react-router-dom";
import books from "../data/books.json";
import { Link } from "react-router-dom";
import { useState } from "react";
import SearchBar from "../components/SearchBar"; // ‚¨ÖÔ∏è ÿ™ÿ£ŸÉÿØ ÿ£ŸÜŸá ŸÖŸàÿ¨ŸàÿØ

export default function Category() {
  const { name } = useParams();
  const [searchKeyword, setSearchKeyword] = useState("");

  // ŸÅŸÑÿ™ÿ±ÿ© ÿ®ÿ≠ÿ≥ÿ® ÿßŸÑÿ™ÿµŸÜŸäŸÅ
  const categoryBooks = books.filter(
    (book) =>
      book.category &&
      name &&
      book.category.toLowerCase() === name.toLowerCase()
  );

  // ŸÅŸÑÿ™ÿ±ÿ© ÿ®ÿ≠ÿ≥ÿ® ÿßŸÑÿ®ÿ≠ÿ´
  const filteredBooks = searchKeyword
    ? categoryBooks.filter((book) =>
        book.title.toLowerCase().includes(searchKeyword.toLowerCase())
      )
    : categoryBooks;

  return (
    <div className="max-w-6xl mx-auto px-4 py-10 text-right space-y-6">
      <h1 className="text-3xl font-bold text-blue-800">üìö ÿ™ÿµŸÜŸäŸÅ: {name}</h1>

      {/* ÿ¥ÿ±Ÿäÿ∑ ÿßŸÑÿ®ÿ≠ÿ´ */}
      <SearchBar onSearch={setSearchKeyword} />

      {/* ÿπÿ±ÿ∂ ÿßŸÑŸÉÿ™ÿ® */}
      {filteredBooks.length === 0 ? (
        <p className="text-gray-600">ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÉÿ™ÿ® ŸÅŸä Ÿáÿ∞ÿß ÿßŸÑÿ™ÿµŸÜŸäŸÅ ÿ≠ÿßŸÑŸäÿßŸã.</p>
      ) : (
        <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
          {filteredBooks.map((book) => (
            <div
              key={book.id}
              className="bg-white rounded-lg shadow hover:shadow-md transition overflow-hidden"
            >
              <img
                src={book.image || "/placeholder.png"}
                alt={book.title}
                className="w-full h-56 object-cover"
              />
              <div className="p-4 space-y-2">
                <h3 className="text-xl font-bold text-blue-800">{book.title}</h3>
                <p className="text-gray-600">‚úçÔ∏è {book.author}</p>
                <p className="text-sm text-gray-700">üí∞ {book.price}</p>
                <Link
                  to={`/book/${book.id}`}
                  className="inline-block mt-2 text-sm text-white bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded"
                >
                  ÿπÿ±ÿ∂ ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ
                </Link>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
