import { Link } from "react-router-dom";
import { ShoppingCart } from "lucide-react";

export default function Navbar() {
  return (
    <header className="w-full bg-white shadow px-4 py-3 flex items-center justify-between">
      {/* ✅ يسار الموبايل: المنيو + السلة */}
      <div className="flex items-center gap-3 order-2 md:order-2 md:ml-auto">
        {/* زر المنيو (يظهر بس على الموبايل) */}
        <button className="p-2 rounded-md bg-gray-100 hover:bg-gray-200 md:hidden">
          ☰
        </button>

        {/* أيقونة السلة */}
        <Link to="/cart" className="relative">
          <ShoppingCart className="w-6 h-6 text-blue-700" />
          <span className="absolute -top-2 -right-2 bg-red-600 text-white text-xs px-1 rounded-full">
            3
          </span>
        </Link>
      </div>

      {/* ✅ الشعار */}
      <Link
        to="/"
        className="font-bold text-xl text-blue-800 order-1 md:order-1 md:mr-auto"
      >
        مكتبة الحرافيش
      </Link>

      {/* ✅ القائمة الكبيرة */}
      <nav className="hidden md:flex gap-6 text-gray-700 order-3">
        <Link to="/">الرئيسية</Link>
        <Link to="/category">التصنيفات</Link>
        <Link to="/about">من نحن</Link>
      </nav>
    </header>
  );
}
