// ‚úÖ src/pages/Category.jsx
import { useParams, Link } from "react-router-dom";
import { motion } from "framer-motion";
import { useEffect, useState } from "react";
import { ref, get } from "firebase/database";
import { db } from "../firebase";
import BookCard from "../components/BookCard";
import CategoryCard from "../components/CategoryCard";
import ScrollTopButton from "../components/ScrollTopButton";

const categories = ["ÿ™ÿßÿ±ŸäÿÆ", "ÿ±ŸàÿßŸäÿ©", "ŸÅŸÑÿ≥ŸÅÿ©", "ŸÖÿ™ÿ±ÿ¨ŸÖ", "ÿπÿ±ÿ®Ÿä"];

export default function Category() {
    const { name } = useParams();
    const [books, setBooks] = useState([]);
    const [visibleBooks, setVisibleBooks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [loadingMore, setLoadingMore] = useState(false);
    const [error, setError] = useState("");
    const [showSold, setShowSold] = useState(false); // ŸáŸÑ ÿ®ÿØÿ£ŸÜÿß ÿ®ÿπÿ±ÿ∂ ÿßŸÑŸÉÿ™ÿ® ÿßŸÑŸÖÿ®ÿßÿπÿ©
    const PAGE_SIZE = 20;

    // üì¶ ÿ™ÿ≠ŸÖŸäŸÑ ŸÉŸÑ ÿßŸÑŸÉÿ™ÿ® ŸÖÿ±ÿ© Ÿàÿßÿ≠ÿØÿ© (ÿ´ŸÖ ÿ™ŸÇÿ≥ŸäŸÖŸáÿß ŸÖÿ≠ŸÑŸäŸãÿß)
    useEffect(() => {
        const fetchAllBooks = async () => {
            try {
                setLoading(true);
                const snapshot = await get(ref(db, "books"));
                if (snapshot.exists()) {
                    const data = Object.values(snapshot.val());

                    // ‚úÖ ÿ™ÿ±ÿ™Ÿäÿ® ÿßŸÑŸÉÿ™ÿ® ÿ®ÿßŸÑÿ£ÿ≠ÿØÿ´
                    const sorted = data.sort((a, b) => b.id - a.id);

                    // ‚úÖ ŸÅÿµŸÑ ÿßŸÑŸÉÿ™ÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© ŸàÿßŸÑŸÖÿ®ÿßÿπÿ©
                    const available = sorted.filter((b) => b.status === "available");
                    const sold = sorted.filter((b) => b.status === "sold");

                    // ‚úÖ ÿ¨ŸÖÿπŸáŸÖ ÿ®ÿ≠Ÿäÿ´ ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© ÿ£ŸàŸÑŸãÿß
                    const ordered = [...available, ...sold];
                    setBooks(ordered);

                    // ‚úÖ ÿπÿ±ÿ∂ ÿ£ŸàŸÑ ÿØŸÅÿπÿ© ŸÖŸÜ ÿßŸÑŸÉÿ™ÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© ŸÅŸÇÿ∑
                    setVisibleBooks(ordered.slice(0, PAGE_SIZE));
                } else {
                    setBooks([]);
                }
            } catch (err) {
                console.error("‚ùå ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ¨ŸÑÿ® ÿßŸÑŸÉÿ™ÿ®:", err);
                setError("ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™.");
            } finally {
                setLoading(false);
            }
        };

        fetchAllBooks();
    }, []);

    // üîΩ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÖÿ≤ŸäÿØ ŸÖŸÜ ÿßŸÑŸÉÿ™ÿ® (ÿ≠ÿ≥ÿ® ÿßŸÑŸÖÿ±ÿ≠ŸÑÿ©)
    const handleLoadMore = () => {
        if (loadingMore) return;
        setLoadingMore(true);

        setTimeout(() => {
            const currentCount = visibleBooks.length;
            const nextBatch = books.slice(currentCount, currentCount + PAGE_SIZE);
            setVisibleBooks((prev) => [...prev, ...nextBatch]);

            // ‚úÖ ÿ•ÿ∞ÿß ÿßŸÜÿ™Ÿáÿ™ ÿßŸÑŸÉÿ™ÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© ŸÜÿ®ÿØÿ£ ÿ®ÿπÿ±ÿ∂ ÿßŸÑŸÖÿ®ÿßÿπÿ©
            if (nextBatch.some((b) => b.status === "sold")) {
                setShowSold(true);
            }

            setLoadingMore(false);
        }, 500);
    };

    // üîç ŸÅŸÑÿ™ÿ±ÿ© ÿ≠ÿ≥ÿ® ÿßŸÑÿ™ÿµŸÜŸäŸÅ
    const filteredBooks = name
        ? books.filter((book) => {
            const cat = book.category?.toLowerCase() || "";
            const type = book.type?.toLowerCase() || "";
            const n = name.toLowerCase();

            if (n === "ŸÖÿ™ÿ±ÿ¨ŸÖ") return ["ÿ£ÿ¨ŸÜÿ®Ÿä", "ÿπÿßŸÑŸÖŸä", "ŸÖÿ™ÿ±ÿ¨ŸÖ"].includes(type);
            if (n === "ÿπÿ±ÿ®Ÿä") return type.includes("ÿπÿ±ÿ®Ÿä");
            if (n === "ÿ∫Ÿäÿ± ÿ±Ÿàÿßÿ¶Ÿä") return !cat.includes("ÿ±ŸàÿßŸäÿ©");
            return cat.includes(n);
        })
        : books;

    // ‚úÖ ÿπÿ±ÿ∂ ŸÅŸÇÿ∑ ÿßŸÑÿØŸÅÿπÿßÿ™ ÿßŸÑÿ≠ÿßŸÑŸäÿ© ÿ®ÿπÿØ ÿßŸÑŸÅŸÑÿ™ÿ±ÿ©
    const displayedBooks = name
        ? filteredBooks.slice(0, visibleBooks.length)
        : visibleBooks;

    return (
        <div className="max-w-7xl mx-auto px-4 py-10 text-right font-sans space-y-10">
            {/* üè∑Ô∏è ÿπŸÜŸàÿßŸÜ ÿßŸÑÿµŸÅÿ≠ÿ© */}
            <motion.h1
                initial={{ opacity: 0, x: 40 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.5 }}
                className="text-3xl font-extrabold text-blue-800 mb-2"
            >
                üõçÔ∏è ÿßŸÑÿ™ÿµŸÜŸäŸÅÿßÿ™
            </motion.h1>

            {/* üß≠ ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ™ÿµŸÜŸäŸÅÿßÿ™ */}
            <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.2 }}
                className="flex flex-wrap gap-3 justify-end"
            >
                <Link to="/category">
                    <CategoryCard title="ÿßŸÑŸÉŸÑ" />
                </Link>
                {categories.map((category) => (
                    <Link key={category} to={`/category/${category}`}>
                        <CategoryCard title={category} />
                    </Link>
                ))}
            </motion.div>

            {/* üìö ÿπŸÜŸàÿßŸÜ ÿßŸÑŸÇÿ≥ŸÖ */}
            <motion.h2
                initial={{ opacity: 0, x: 40 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.6, delay: 0.3 }}
                className="text-2xl font-semibold text-blue-700 border-r-4 border-blue-600 pr-3"
            >
                üìñ {name ? `ÿ™ÿµŸÜŸäŸÅ: ${name}` : "ÿ¨ŸÖŸäÿπ ÿßŸÑŸÉÿ™ÿ®"}
            </motion.h2>

            {/* ‚è≥ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ / ÿπÿ±ÿ∂ ÿßŸÑŸÉÿ™ÿ® */}
            {loading ? (
                <div className="grid gap-8 sm:grid-cols-2 lg:grid-cols-3">
                    {[...Array(PAGE_SIZE)].map((_, i) => (
                        <div
                            key={i}
                            className="h-80 bg-gray-200 animate-pulse rounded-lg shadow-sm"
                        ></div>
                    ))}
                </div>
            ) : error ? (
                <p className="text-center text-red-600 py-10">{error}</p>
            ) : displayedBooks.length === 0 ? (
                <p className="text-gray-600 text-lg text-center py-10">
                    üì≠ ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÉÿ™ÿ® ÿ∂ŸÖŸÜ Ÿáÿ∞ÿß ÿßŸÑÿ™ÿµŸÜŸäŸÅ ÿ≠ÿßŸÑŸäŸãÿß.
                </p>
            ) : (
                <>
                    <motion.div
                        initial="hidden"
                        animate="visible"
                        variants={{
                            hidden: { opacity: 0 },
                            visible: { opacity: 1, transition: { staggerChildren: 0.1 } },
                        }}
                        className="grid gap-8 sm:grid-cols-2 lg:grid-cols-3"
                    >
                        {displayedBooks.map((book) => (
                            <motion.div
                                key={book.id}
                                variants={{
                                    hidden: { opacity: 0, y: 30 },
                                    visible: { opacity: 1, y: 0 },
                                }}
                            >
                                <BookCard book={book} />
                            </motion.div>
                        ))}
                    </motion.div>

                    {/* üîΩ ÿ≤ÿ± ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÖÿ≤ŸäÿØ */}
                    {visibleBooks.length < books.length && (
                        <div className="text-center mt-8">
                            <button
                                onClick={handleLoadMore}
                                disabled={loadingMore}
                                className={`px-6 py-2 rounded-lg text-white transition ${loadingMore
                                    ? "bg-gray-400 cursor-not-allowed"
                                    : "bg-blue-600 hover:bg-blue-700"
                                    }`}
                            >
                                {loadingMore
                                    ? "ÿ¨ÿßÿ± ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ..."
