// src/pages/AdminDashboard.jsx
import { useState } from "react";
import { ref, push } from "firebase/database";
import { db } from "../../firebase";
import toast, { Toaster } from "react-hot-toast";

export default function AdminDashboard() {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [transl, setTransl] = useState("");
  const [type, setType] = useState("ÿπÿ±ÿ®Ÿä");
  const [category, setCategory] = useState("");
  const [price, setPrice] = useState("");
  const [HPaper, setHPaper] = useState("");
  const [description, setDescription] = useState("");
  const [folder, setFolder] = useState(""); // ÿßÿ≥ŸÖ ŸÖÿ¨ŸÑÿØ ÿßŸÑŸäŸàŸÖ ÿØÿßÿÆŸÑ public/images
  const [images, setImages] = useState([]); // ÿ±Ÿàÿßÿ®ÿ∑ ÿßŸÑÿµŸàÿ± ÿßŸÑŸÜŸáÿßÿ¶Ÿäÿ©
  const [loading, setLoading] = useState(false);

  // ÿπŸÜÿØ ÿßÿÆÿ™Ÿäÿßÿ± ÿßŸÑÿµŸàÿ± ŸÖŸÜ ÿßŸÑÿ¨Ÿáÿßÿ≤
  const handleImageSelect = (files) => {
    if (!folder.trim()) {
      toast.error("‚ö†Ô∏è Ÿäÿ±ÿ¨Ÿâ ŸÉÿ™ÿßÿ®ÿ© ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ¨ŸÑÿØ ÿØÿßÿÆŸÑ public/images ÿ£ŸàŸÑÿßŸã (ŸÖÿ´ŸÑ 10-30)");
      return;
    }

    const urls = Array.from(files).map((file) => `/images/${folder}/${file.name}`);
    setImages(urls);
    toast.success(`‚úÖ ÿ™ŸÖ ÿßÿÆÿ™Ÿäÿßÿ± ${urls.length} ÿµŸàÿ±ÿ©`);
  };

  // ÿπŸÜÿØ ÿßŸÑÿ•ÿ±ÿ≥ÿßŸÑ
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!title || !author || !category || images.length === 0) {
      toast.error("‚ö†Ô∏è Ÿäÿ±ÿ¨Ÿâ ŸÖŸÑÿ° ÿ¨ŸÖŸäÿπ ÿßŸÑÿ≠ŸÇŸàŸÑ ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ© Ÿàÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿµŸàÿ±!");
      return;
    }

    setLoading(true);

    const newBook = {
      id: Date.now(),
      isNew: true,
      title,
      author,
      transl,
      type,
      category,
      images, // /images/10-30/name.jpg
      price: price || "ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ",
      HPaper: HPaper || "",
      description: description || "",
      status: "available",
      createdAt: Date.now(),
    };

    try {
      await push(ref(db, "books"), newBook);
      toast.success("üìö ÿ™ŸÖ ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑŸÉÿ™ÿßÿ® ÿ®ŸÜÿ¨ÿßÿ≠ ÿ•ŸÑŸâ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™!");
      // ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™ŸáŸäÿ¶ÿ©
      setTitle("");
      setAuthor("");
      setTransl("");
      setType("ÿπÿ±ÿ®Ÿä");
      setCategory("");
      setPrice("");
      setHPaper("");
      setDescription("");
      setImages([]);
      setFolder("");
    } catch (err) {
      toast.error("‚ùå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ•ÿ∂ÿßŸÅÿ©: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white shadow-md rounded-lg mt-10">
      <Toaster />
      <h2 className="text-2xl font-bold mb-6 text-center text-blue-700">üìò ŸÑŸàÿ≠ÿ© ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÉÿ™ÿ®</h2>

      <form onSubmit={handleSubmit} className="space-y-4">
        {/* ÿßÿ≥ŸÖ ŸÖÿ¨ŸÑÿØ ÿßŸÑÿµŸàÿ± */}
        <div>
          <label className="block text-sm font-medium mb-1">üìÅ ÿßÿ≥ŸÖ ŸÖÿ¨ŸÑÿØ ÿßŸÑÿµŸàÿ± (ÿØÿßÿÆŸÑ public/images)</label>
          <input
            type="text"
            placeholder="ŸÖÿ´ÿßŸÑ: 10-30"
            value={folder}
            onChange={(e) => setFolder(e.target.value)}
            className="w-full p-3 border rounded-md"
          />
          <p className="text-xs text-gray-500 mt-1">
            ÿ£ÿØÿÆŸÑ ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ¨ŸÑÿØ ÿßŸÑÿ∞Ÿä Ÿäÿ≠ÿ™ŸàŸä ÿπŸÑŸâ ÿßŸÑÿµŸàÿ± ŸÖÿ´ŸÑ <code>10-30</code>
          </p>
        </div>

        {/* ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÉÿ™ÿßÿ® */}
        <input
          type="text"
          placeholder="ÿπŸÜŸàÿßŸÜ ÿßŸÑŸÉÿ™ÿßÿ®"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="w-full p-3 border rounded-md"
        />
        <input
          type="text"
          placeholder="ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ§ŸÑŸÅ"
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
          className="w-full p-3 border rounded-md"
        />
        <input
          type="text"
          placeholder="ÿßŸÑŸÖÿ™ÿ±ÿ¨ŸÖ (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)"
          value={transl}
          onChange={(e) => setTransl(e.target.value)}
          className="w-full p-3 border rounded-md"
        />

        <select
          value={type}
          onChange={(e) => setType(e.target.value)}
          className="w-full p-3 border rounded-md"
        >
          <option value="ÿπÿ±ÿ®Ÿä">ÿπÿ±ÿ®Ÿä</option>
          <option value="ÿπÿßŸÑŸÖŸä">ÿπÿßŸÑŸÖŸä</option>
        </select>

        <select
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          className="w-full p-3 border rounded-md"
        >
          <option value="">ÿßÿÆÿ™ÿ± ÿßŸÑŸÅÿ¶ÿ©</option>
          <option value="ÿ±ŸàÿßŸäÿ©">ÿ±ŸàÿßŸäÿ©</option>
          <option value="ÿ™ÿßÿ±ŸäÿÆ">ÿ™ÿßÿ±ŸäÿÆ</option>
          <option value="ÿ¥ÿπÿ±">ÿ¥ÿπÿ±</option>
          <option value="ŸÖÿ≥ÿ±ÿ≠Ÿäÿ©">ŸÖÿ≥ÿ±ÿ≠Ÿäÿ©</option>
