// src/pages/Category.jsx
import { useParams, Link } from "react-router-dom";
import { useCart } from "../context/CartContext";
import BookCard from "../components/BookCard";
import ScrollTopButton from "../components/ScrollTopButton";
import CategoryCard from "../components/CategoryCard";
import { motion } from "framer-motion";
import { useQuery } from "@tanstack/react-query";

// ÿßŸÑÿ™ÿµŸÜŸäŸÅÿßÿ™ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©
const categories = ["ÿ±ŸàÿßŸäÿ©", "ÿ∫Ÿäÿ± ÿ±Ÿàÿßÿ¶Ÿä", "ŸÖÿ™ÿ±ÿ¨ŸÖ", "ÿπÿ±ÿ®Ÿä"];

export default function Category() {
  const { name } = useParams();
  const { addToCart, removeFromCart, isInCart } = useCart();

  // ‚úÖ ÿ¨ŸÑÿ® ÿßŸÑŸÉÿ™ÿ® ŸÖŸÜ JSON ÿπÿ®ÿ± React Query
  const { data: books = [], isLoading, error } = useQuery({
    queryKey: ["books"],
    queryFn: () => fetch("/books.json").then((res) => res.json()),
  });

  // ‚úÖ ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿÆÿ∑ÿ£ ÿ£Ÿà ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ
  if (error)
    return (
      <p className="text-center text-red-600 py-10">
        ‚ùå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      </p>
    );

  // ‚úÖ ÿ™ÿ±ÿ™Ÿäÿ® ÿßŸÑŸÉÿ™ÿ®: ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© ÿ£ŸàŸÑŸãÿßÿå ÿ´ŸÖ ÿßŸÑŸÖÿ®ÿßÿπÿ©ÿå ÿ´ŸÖ ÿßŸÑÿ£ÿ≠ÿØÿ´
  const sortedBooks = [...books].sort((a, b) => {
    if (a.status === "available" && b.status === "sold") return -1;
    if (a.status === "sold" && b.status === "available") return 1;
    return b.id - a.id;
  });

  // ‚úÖ ŸÅŸÑÿ™ÿ±ÿ© ÿ≠ÿ≥ÿ® ÿßŸÑÿ™ÿµŸÜŸäŸÅ
  const filteredBooks = name
    ? sortedBooks.filter((book) => {
        const cat = book.category?.toLowerCase() || "";
        const type = book.type?.toLowerCase() || "";
        const n = name.toLowerCase();

        if (n === "ŸÖÿ™ÿ±ÿ¨ŸÖ") return ["ÿ£ÿ¨ŸÜÿ®Ÿä", "ÿπÿßŸÑŸÖŸä", "ŸÖÿ™ÿ±ÿ¨ŸÖ"].includes(type);
        if (n === "ÿπÿ±ÿ®Ÿä") return type.includes("ÿπÿ±ÿ®Ÿä");
        if (n === "ÿ∫Ÿäÿ± ÿ±Ÿàÿßÿ¶Ÿä") return !cat.includes("ÿ±ŸàÿßŸäÿ©");
        return cat.includes(n);
      })
    : sortedBooks;

  return (
    <div className="max-w-7xl mx-auto px-4 py-10 text-right font-sans space-y-10">
      {/* üè∑Ô∏è ÿπŸÜŸàÿßŸÜ ÿßŸÑÿµŸÅÿ≠ÿ© */}
      <motion.h1
        initial={{ opacity: 0, x: 40 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.6 }}
        className="text-3xl font-extrabold text-blue-800"
      >
        üõçÔ∏è ÿßŸÑÿ™ÿµŸÜŸäŸÅÿßÿ™
      </motion.h1>

      {/* üß≠ ÿ¥ÿ±Ÿäÿ∑ ÿßŸÑÿ™ÿµŸÜŸäŸÅÿßÿ™ */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.2 }}
        className="flex flex-wrap gap-3 justify-end"
      >
        <Link to="/category">
          <CategoryCard title="ÿßŸÑŸÉŸÑ" />
        </Link>

        {categories.map((category) => (
          <Link key={category} to={`/category/${category}`}>
            <CategoryCard title={category} />
          </Link>
        ))}
      </motion.div>

      {/* üìö ÿßŸÑÿπŸÜŸàÿßŸÜ ÿßŸÑŸÅÿ±ÿπŸä */}
      <motion.h2
        initial={{ opacity: 0, x: 40 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.6, delay: 0.3 }}
        className="text-2xl font-semibold text-blue-700 border-r-4 border-blue-600 pr-3"
      >
        üìñ {name ? `ÿ™ÿµŸÜŸäŸÅ: ${name}` : "ÿ¨ŸÖŸäÿπ ÿßŸÑŸÉÿ™ÿ®"}
      </motion.h2>

      {/* üß© ÿ≠ÿßŸÑÿ© ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ */}
      {isLoading ? (
        <div className="grid gap-8 sm:grid-cols-2 lg:grid-cols-3">
          {[...Array(9)].map((_, i) => (
            <div
              key={i}
              className="h-80 bg-gray-200 animate-pulse rounded-lg shadow-sm"
            ></div>
          ))}
        </div>
      ) : filteredBooks.length === 0 ? (
        <p className="text-gray-600 text-lg">
          ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÉÿ™ÿ® ÿ∂ŸÖŸÜ Ÿáÿ∞ÿß ÿßŸÑÿ™ÿµŸÜŸäŸÅ ÿ≠ÿßŸÑŸäÿßŸã.
        </p>
      ) : (
        <motion.div
          initial="hidden"
          animate="visible"
          variants={{
            hidden: { opacity: 0 },
            visible: {
              opacity: 1,
              transition: { staggerChildren: 0.1 },
            },
          }}
          className="grid gap-8 sm:grid-cols-2 lg:grid-cols-3"
        >
          {filteredBooks.map((book) => (
            <motion.div
              key={book.id}
              variants={{
                hidden: { opacity: 0, y: 30 },
                visible: { opacity: 1, y: 0 },
              }}
              className="relative"
            >
              <BookCard book={book} />

              {/* ÿ≤ÿ± ÿßŸÑÿ≥ŸÑÿ© Ÿäÿ∏Ÿáÿ± ŸÅŸÇÿ∑ ŸÑŸÑŸÉÿ™ÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© */}
              {book.status !== "sold" && (
                <button
                  onClick={() =>
                    isInCart(book.id)
                      ? removeFromCart(book.id)
                      : addToCart(book)
                  }
                  className={`mt-3 w-full text-sm px-4 py-2 rounded-lg transition font-medium ${
                    isInCart(book.id)
                      ? "bg-red-100 text-red-600 hover:bg-red-200"
                      : "bg-green-100 text-green-700 hover:bg-green-200"
                  }`}
                >
                  {isInCart(book.id)
                    ? "üóëÔ∏è ÿ•ÿ≤ÿßŸÑÿ© ŸÖŸÜ ÿßŸÑÿ≥ŸÑÿ©"
                    : "‚ûï ÿ£ÿ∂ŸÅ ÿ•ŸÑŸâ ÿßŸÑÿ≥ŸÑÿ©"}
                </button>
              )}
            </motion.div>
          ))}
        </motion.div>
      )}

      <ScrollTopButton />
    </div>
  );
}
