// src/components/BookCard.jsx
import { Link } from "react-router-dom";
import { motion } from "framer-motion";
import BookImage from "./BookImage";

export default function BookCard({ book }) {
    if (!book) return null;

    const isSold = book.status === "sold";

    return (
        <motion.div
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.4 }}
            className={`bg-white rounded-2xl shadow hover:shadow-lg transition duration-300 overflow-hidden text-right border border-gray-100 relative ${isSold ? "opacity-80" : ""
                }`}
        >
            {/* ÿµŸàÿ±ÿ© ÿßŸÑŸÉÿ™ÿßÿ® */}
            <div className="relative">
                <BookImage
                    images={book.images || []}
                    alt={book.title}
                    ratio="h-72"
                    fit="cover"
                    className="rounded-t-2xl"
                />
                {book.isNew && (
                    <span className="absolute top-3 right-3 text-xs bg-red-600 text-white px-2 py-1 rounded-full shadow">
                        ÿ¨ÿØŸäÿØ
                    </span>
                )}
                {isSold && (
                    <span className="absolute bottom-3 left-3 text-xs bg-gray-800 text-white px-3 py-1 rounded-full shadow">
                        ‚ùå ÿ™ŸÖ ÿßŸÑÿ®Ÿäÿπ
                    </span>
                )}
            </div>

            {/* ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑŸÉÿ™ÿßÿ® */}
            <div className="p-4 space-y-3">
                <h3 className="text-lg md:text-xl font-semibold text-gray-800 leading-snug line-clamp-2">
                    {book.title}
                </h3>

                {book.author && (
                    <p className="text-gray-600 text-sm">
                        ‚úçÔ∏è <span className="font-medium">ÿßŸÑŸÖÿ§ŸÑŸÅ:</span> {book.author}
                    </p>
                )}

                {book.price && (
                    <p className="text-sm text-green-700 font-semibold">
                        üíµ ÿßŸÑÿ≥ÿπÿ±: {book.price}
                    </p>
                )}

                {/* ÿ≠ÿßŸÑÿ© ÿßŸÑŸÉÿ™ÿßÿ® */}
                <p
                    className={`text-sm font-semibold ${isSold ? "text-red-600" : "text-green-600"
                        }`}
                >
                    {isSold ? "‚ùå ÿ™ŸÖ ÿßŸÑÿ®Ÿäÿπ" : "‚úÖ ŸÖÿ™ŸàŸÅÿ±"}
                </p>

                {/* ÿ≤ÿ± ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ */}
                <Link
                    to={`/book/${book.id}`}
                    className={`inline-block text-sm px-4 py-2 rounded-lg transition ${isSold
                            ? "bg-gray-400 text-white cursor-not-allowed"
                            : "bg-blue-700 text-white hover:bg-blue-800"
                        }`}
                    onClick={(e) => {
                        if (isSold) e.preventDefault();
                    }}
                >
                    ÿπÿ±ÿ∂ ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ
                </Link>
            </div>
        </motion.div>
    );
}
