// src/pages/BookDetails.jsx
import { useParams, Link } from "react-router-dom";
import { motion } from "framer-motion";
import { useCart } from "../context/CartContext";
import books from "../data/books.json";
import BookImage from "../components/BookImage";

export default function BookDetails() {
    const { id } = useParams();
    const book = books.find((b) => String(b.id) === id);

    const { addToCart, removeFromCart, isInCart } = useCart();

    if (!book) {
        return (
            <div className="max-w-4xl mx-auto p-6 text-center">
                <h2 className="text-xl text-gray-600">‚ùå ÿßŸÑŸÉÿ™ÿßÿ® ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ</h2>
                <Link
                    to="/category"
                    className="mt-4 inline-block px-4 py-2 bg-blue-700 text-white rounded-lg hover:bg-blue-800"
                >
                    ÿßŸÑÿπŸàÿØÿ© ŸÑŸÑÿ™ÿ≥ŸàŸÇ
                </Link>
            </div>
        );
    }

    // ‚úÖ ÿ≠ÿßŸÑÿ™ŸäŸÜ ŸÅŸÇÿ∑
    const isSold = book.status === "sold";
    const notAvailable = isSold;

    return (
        <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.6 }}
            className="max-w-5xl mx-auto px-6 py-10 grid md:grid-cols-2 gap-8 bg-white rounded-xl shadow-lg"
        >
            {/* ÿµŸàÿ±ÿ© ÿßŸÑŸÉÿ™ÿßÿ® */}
            <div>
                <BookImage
                    images={book.images || []}
                    alt={book.title}
                    ratio="h-96"
                    fit="contain"
                    className="rounded-lg"
                />
            </div>

            {/* ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑŸÉÿ™ÿßÿ® */}
            <div className="space-y-5 text-right">
                <h1 className="text-2xl md:text-3xl font-bold text-gray-800">
                    {book.title}
                </h1>

                {book.author && (
                    <p className="text-gray-600 text-lg">
                        ‚úçÔ∏è <span className="font-medium">ÿßŸÑŸÖÿ§ŸÑŸÅ:</span> {book.author}
                    </p>
                )}

                {book.price && (
                    <p className="text-green-700 font-semibold text-lg">
                        üíµ ÿßŸÑÿ≥ÿπÿ±: {book.price}
                    </p>
                )}

                {book.HPaper && (
                    <p className="text-gray-600">
                        üìÑ ÿπÿØÿØ ÿßŸÑÿµŸÅÿ≠ÿßÿ™: {book.HPaper}
                    </p>
                )}

                {/* ÿßŸÑŸàÿµŸÅ */}
                {book.description && (
                    <p className="text-gray-700 leading-relaxed">{book.description}</p>
                )}

                {/* ÿ¥ÿßÿ±ÿ© ÿßŸÑÿ≠ÿßŸÑÿ© */}
                <p
                    className={`inline-block px-3 py-1 rounded-full text-sm font-medium shadow ${
                        isSold
                            ? "bg-gray-700 text-white"
                            : "bg-green-600 text-white"
                    }`}
                >
                    {isSold ? "‚ùå ÿ™ŸÖ ÿßŸÑÿ®Ÿäÿπ" : "‚úÖ ŸÖÿ™ŸàŸÅÿ±"}
                </p>

                {/* ÿ£ÿ≤ÿ±ÿßÿ± ÿßŸÑÿ™ŸÅÿßÿπŸÑ */}
                <div className="pt-4 flex flex-col gap-3">
                    {/* ÿ≤ÿ± ÿßŸÑÿ≥ŸÑÿ© Ÿäÿ∏Ÿáÿ± ŸÅŸÇÿ∑ ŸÑŸà ŸÖÿ™ÿßÿ≠ */}
                    {!notAvailable && (
                        <button
                            onClick={() =>
                                isInCart(book.id)
                                    ? removeFromCart(book.id)
                                    : addToCart(book)
                            }
                            className={`px-6 py-2 rounded-lg font-medium transition-all duration-200 ${
                                isInCart(book.id)
                                    ? "bg-red-100 text-red-600 hover:bg-red-200"
                                    : "bg-blue-700 text-white hover:bg-blue-800"
                            }`}
                        >
                            {isInCart(book.id)
                                ? "üóëÔ∏è ÿ•ÿ≤ÿßŸÑÿ© ŸÖŸÜ ÿßŸÑÿ≥ŸÑÿ©"
                                : "‚ûï ÿ£ÿ∂ŸÅ ÿ•ŸÑŸâ ÿßŸÑÿ≥ŸÑÿ©"}
                        </button>
                    )}

                    {/* ÿ≤ÿ± ÿßŸÑÿ≠ÿ¨ÿ≤ ÿπÿ®ÿ± Ÿàÿßÿ™ÿ≥ÿßÿ® - Ÿäÿ∏Ÿáÿ± ÿØÿßŸäŸÖŸãÿß */}
                    <a
                        href={`https://wa.me/201212145165?text=ŸÖÿ±ÿ≠ÿ®Ÿãÿßÿå ÿ£ÿ±ŸäÿØ ÿ≠ÿ¨ÿ≤ ŸÉÿ™ÿßÿ®: "${book.title}"`}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="px-6 py-2 rounded-lg bg-green-600 text-white font-medium hover:bg-green-700 transition-all duration-200 text-center"
                    >
                        üìå ÿßÿ≠ÿ¨ÿ≤ ÿπÿ®ÿ± Ÿàÿßÿ™ÿ≥ÿßÿ®
                    </a>
                </div>
            </div>
        </motion.div>
    );
}
