// ‚úÖ src/pages/admin/ManageBooks.jsx
import { useEffect, useState } from "react";
import { db } from "../../firebase";
import { ref, get, update } from "firebase/database";
import { getStorage, ref as sRef, uploadBytes, getDownloadURL } from "firebase/storage";
import toast, { Toaster } from "react-hot-toast";
import { useNavigate } from "react-router-dom";

export default function ManageBooks() {
    const [books, setBooks] = useState([]);
    const [filteredBooks, setFilteredBooks] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [loading, setLoading] = useState(true);
    const [savingAll, setSavingAll] = useState(false);
    const navigate = useNavigate();
    const storage = getStorage();

    // ‚úÖ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ
    useEffect(() => {
        const adminLoggedIn = localStorage.getItem("isAdmin");
        if (!adminLoggedIn) navigate("/admin/login");
    }, [navigate]);

    // ‚úÖ ÿ¨ŸÑÿ® ÿßŸÑŸÉÿ™ÿ® ŸÖŸÜ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
    useEffect(() => {
        const fetchBooks = async () => {
            try {
                const snapshot = await get(ref(db, "books"));
                if (snapshot.exists()) {
                    const data = snapshot.val();
                    const booksArray = Object.entries(data).map(([key, value]) => ({
                        key,
                        ...value,
                    }));
                    const sorted = booksArray.sort((a, b) => b.id - a.id);
                    setBooks(sorted);
                    setFilteredBooks(sorted);
                } else {
                    toast.error("üì≠ ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÉÿ™ÿ® ŸÅŸä ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™");
                }
            } catch (err) {
                toast.error("‚ùå ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÉÿ™ÿ®");
                console.error(err);
            } finally {
                setLoading(false);
            }
        };
        fetchBooks();
    }, []);

    // ‚úÖ ÿßŸÑÿ®ÿ≠ÿ´ ŸÅŸä ÿßŸÑÿπŸÜŸàÿßŸÜ ÿ£Ÿà ÿßŸÑŸÖÿ§ŸÑŸÅ
    useEffect(() => {
        const term = searchTerm.toLowerCase();
        const results = books.filter(
            (book) =>
                book.title?.toLowerCase().includes(term) ||
                book.author?.toLowerCase().includes(term)
        );
        setFilteredBooks(results);
    }, [searchTerm, books]);

    // ‚úÖ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑŸÇŸäŸÖ ŸÖÿ≠ŸÑŸäŸãÿß
    const handleChange = (key, field, value) => {
        setBooks((prev) =>
            prev.map((book) => (book.key === key ? { ...book, [field]: value } : book))
        );
    };

    // ‚úÖ ÿ±ŸÅÿπ ÿµŸàÿ±ÿ© ÿ¨ÿØŸäÿØÿ©
    const handleImageUpload = async (key, file) => {
        if (!file) return;
        try {
            const imageRef = sRef(storage, `book_images/${Date.now()}_${file.name}`);
            await uploadBytes(imageRef, file);
            const url = await getDownloadURL(imageRef);
            handleChange(key, "image", url);
            toast.success("üì∑ ÿ™ŸÖ ÿ±ŸÅÿπ ÿßŸÑÿµŸàÿ±ÿ© ÿ®ŸÜÿ¨ÿßÿ≠!");
        } catch (err) {
            console.error(err);
            toast.error("‚ùå ŸÅÿ¥ŸÑ ÿ±ŸÅÿπ ÿßŸÑÿµŸàÿ±ÿ©");
        }
    };

    // ‚úÖ ÿ≠ŸÅÿ∏ ŸÉÿ™ÿßÿ® Ÿàÿßÿ≠ÿØ ŸÅŸÇÿ∑
    const handleSave = async (book) => {
        try {
            await update(ref(db, `books/${book.key}`), {
                title: book.title,
                author: book.author,
                price: book.price,
                description: book.description,
                category: book.category,
                status: book.status,
                image: book.image,
            });
            toast.success(`‚úÖ ÿ™ŸÖ ÿ≠ŸÅÿ∏ "${book.title}" ÿ®ŸÜÿ¨ÿßÿ≠`);
        } catch (err) {
            toast.error("‚ùå ŸÅÿ¥ŸÑ ÿßŸÑÿ≠ŸÅÿ∏ ÿßŸÑŸÅÿ±ÿØŸä");
            console.error(err);
        }
    };

    // ‚úÖ ÿ≠ŸÅÿ∏ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ™ÿπÿØŸäŸÑÿßÿ™ ÿØŸÅÿπÿ© Ÿàÿßÿ≠ÿØÿ©
    const handleSaveAll = async () => {
        try {
            setSavingAll(true);
            const updates = {};
            books.forEach((book) => {
                updates[`books/${book.key}`] = {
                    ...book,
                    title: book.title,
                    author: book.author,
                    price: book.price,
                    description: book.description,
                    category: book.category,
                    status: book.status,
                    image: book.image,
                };
            });
            await update(ref(db), updates);
            toast.success("üíæ ÿ™ŸÖ ÿ≠ŸÅÿ∏ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ™ÿπÿØŸäŸÑÿßÿ™ ÿ®ŸÜÿ¨ÿßÿ≠");
        } catch (err) {
            toast.error("‚ùå ŸÅÿ¥ŸÑ ÿ≠ŸÅÿ∏ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ™ÿπÿØŸäŸÑÿßÿ™");
            console.error(err);
        } finally {
            setSavingAll(false);
        }
    };

    if (loading)
        return (
            <div className="text-center py-20 text-gray-600 text-lg">
                ‚è≥ ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...
            </div>
        );

    return (
        <div className="max-w-7xl mx-auto p-6 bg-white shadow-md rounded-lg mt-10">
            <Toaster />

            {/* üß≠ ÿßŸÑÿπŸÜŸàÿßŸÜ + ÿßŸÑÿ®ÿ≠ÿ´ + ÿ≤ÿ± ÿ≠ŸÅÿ∏ ÿßŸÑŸÉŸÑ */}
            <div className="flex flex-col sm:flex-row justify-between items-center mb-6 gap-4">
                <h2 className="text-2xl font-bold text-blue-700">üõ†Ô∏è ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÉÿ™ÿ®</h2>

                <input
                    type="text"
                    placeholder="üîç ÿßÿ®ÿ≠ÿ´ ÿ®ÿßŸÑÿπŸÜŸàÿßŸÜ ÿ£Ÿà ÿßŸÑŸÖÿ§ŸÑŸÅ..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="border border-gray-300 rounded-lg px-4 py-2 w-full sm:w-80 focus:ring-2 focus:ring-blue-400"
                />

                <button
                    onClick={handleSaveAll}
                    disabled={savingAll}
                    className={`${savingAll
                            ? "bg-gray-400 cursor-not-allowed"
                            : "bg-green-600 hover:bg-green-700"
                        } text-white px-6 py-2 rounded-lg font-semibold shadow transition`}
                >
                    {savingAll ? "üíæ ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ≠ŸÅÿ∏..." : "üíæ ÿ≠ŸÅÿ∏ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ™ÿπÿØŸäŸÑÿßÿ™"}
                </button>
            </div>

            {/* üìö ÿ¨ÿØŸàŸÑ ÿßŸÑŸÉÿ™ÿ® */}
            <div className="overflow-x-auto">
                <table className="min-w-full border border-gray-200 text-right">
                    <thead className="bg-blue-50">
                        <tr>
                            <th className="px-4 py-2 border">üìò ÿßŸÑÿµŸàÿ±ÿ©</th>
                            <th className="px-4 py-2 border">ÿßŸÑÿπŸÜŸàÿßŸÜ</th>
                            <th className="px-4 py-2 border">ÿßŸÑŸÖÿ§ŸÑŸÅ</th>
                            <th className="px-4 py-2 border">ÿßŸÑÿ≥ÿπÿ±</th>
                            <th className="px-4 py-2 border">ÿßŸÑŸàÿµŸÅ</th>
                            <th className="px-4 py-2 border">ÿßŸÑÿ™ÿµŸÜŸäŸÅ</th>
                            <th className="px-4 py-2 border">ÿßŸÑÿ≠ÿßŸÑÿ©</th>
                            <th className="px-4 py-2 border">üíæ</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredBooks.length === 0 ? (
                            <tr>
                                <td colSpan="8" className="text-center text-gray-500 py-6">
                                    üì≠ ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÜÿ™ÿßÿ¶ÿ¨ ŸÖÿ∑ÿßÿ®ŸÇÿ©
                                </td>
                            </tr>
                        ) : (
                            filteredBooks.map((book) => (
                                <tr key={book.key} className="hover:bg-gray-50">
                                    {/* üñºÔ∏è ÿßŸÑÿµŸàÿ±ÿ© */}
                                    <td className="border px-2 py-2 text-center">
                                        {book.image ? (
                                            <img
                                                src={book.image}
                                                alt="book"
                                                className="w-16 h-20 object-cover mx-auto rounded-md"
                                            />
                                        ) : (
                                            <div className="w-16 h-20 bg-gray-200 mx-auto rounded-md"></div>
                                        )}
                                        <input
                                            type="file"
                                            accept="image/*"
                                            onChange={(e) =>
                                                handleImageUpload(book.key, e.target.files[0])
                                            }
                                            className="text-xs mt-1"
                                        />
                                    </td>

                                    {/* üè∑Ô∏è ÿ®ÿßŸÇŸä ÿßŸÑÿ≠ŸÇŸàŸÑ */}
                                    <td className="border px-4 py-2">
                                        <input
                                            type="text"
                                            value={book.title}
                                            onChange={(e) =>
                                                handleChange(book.key, "title", e.target.value)
                                            }
                                            className="w-full border rounded px-2 py-1"
                                        />
                                    </td>
                                    <td className="border px-4 py-2">
                                        <input
                                            type="text"
                                            value={book.author}
                                            onChange={(e) =>
                                                handleChange(book.key, "author", e.target.value)
                                            }
                                            className="w-full border rounded px-2 py-1"
                                        />
                                    </td>
                                    <td className="border px-4 py-2">
                                        <input
                                            type="number"
                                            value={book.price}
                                            onChange={(e) =>
                                                handleChange(book.key, "price", e.target.value)
                                            }
                                            className="w-full border rounded px-2 py-1"
                                        />
                                    </td>
                                    <td className="border px-4 py-2">
                                        <textarea
                                            value={book.description || ""}
                                            onChange={(e) =>
                                                handleChange(
                                                    book.key,
                                                    "description",
                                                    e.target.value
                                                )
                                            }
                                            className="w-full border rounded px-2 py-1 h-20"
                                        />
                                    </td>
                                    <td className="border px-4 py-2">
                                        <select
                                            value={book.category}
                                            onChange={(e) =>
                                                handleChange(book.key, "category", e.target.value)
                                            }
                                            className="border rounded-md px-2 py-1 w-full"
                                        >
                                            <option value="ÿ±ŸàÿßŸäÿ©">ÿ±ŸàÿßŸäÿ©</option>
                                            <option value="ÿ™ÿßÿ±ŸäÿÆ">ÿ™ÿßÿ±ŸäÿÆ</option>
                                            <option value="ŸÅŸÑÿ≥ŸÅÿ©">ŸÅŸÑÿ≥ŸÅÿ©</option>
                                            <option value="ÿπŸÑŸÖ ŸÜŸÅÿ≥">ÿπŸÑŸÖ ŸÜŸÅÿ≥</option>
                                            <option value="ÿ¥ÿπÿ±">ÿ¥ÿπÿ±</option>
                                            <option value="ÿ≥Ÿäÿ±ÿ© ÿ∞ÿßÿ™Ÿäÿ©">ÿ≥Ÿäÿ±ÿ© ÿ∞ÿßÿ™Ÿäÿ©</option>
                                            <option value="ÿ≥Ÿäÿßÿ≥ÿ©">ÿ≥Ÿäÿßÿ≥ÿ©</option>
                                        </select>
                                    </td>
                                    <td className="border px-4 py-2">
                                        <select
                                            value={book.status}
                                            onChange={(e) =>
                                                handleChange(book.key, "status", e.target.value)
                                            }
                                            className={`border rounded-md px-2 py-1 w-full ${book.status === "sold"
                                                    ? "bg-red-100 text-red-700"
                                                    : "bg-green-100 text-green-700"
                                                }`}
                                        >
                                            <option value="available">üìó ŸÖÿ™ÿßÿ≠</option>
                                            <option value="sold">üìï ÿ™ŸÖ ÿßŸÑÿ®Ÿäÿπ</option>
                                        </select>
                                    </td>

                                    {/* üíæ ÿ≠ŸÅÿ∏ ŸÅÿ±ÿØŸä */}
                                    <td className="border px-4 py-2 text-center">
                                        <button
                                            onClick={() => handleSave(book)}
                                            className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded-md transition"
                                        >
                                            ÿ≠ŸÅÿ∏
                                        </button>
                                    </td>
                                </tr>
                            ))
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
}
