// src/context/CartContext.jsx
import { createContext, useContext, useEffect, useState } from "react";
import toast from "react-hot-toast";

const CartContext = createContext();

export function CartProvider({ children }) {
    const [cart, setCart] = useState(() => {
        try {
            const saved = localStorage.getItem("cart");
            return saved ? JSON.parse(saved) : [];
        } catch {
            return [];
        }
    });

    // ÿ≠ŸÅÿ∏ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÅŸä localStorage ÿπŸÜÿØ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´
    useEffect(() => {
        localStorage.setItem("cart", JSON.stringify(cart));
    }, [cart]);

    // ÿ•ÿ∂ÿßŸÅÿ© ŸÉÿ™ÿßÿ® ŸÑŸÑÿ≥ŸÑÿ©
    function addToCart(book) {
        if (!book?.id) return;

        setCart((prev) => {
            if (!prev.some((b) => b.id === book.id)) {
                toast.success(`üõí ÿ™ŸÖÿ™ ÿ•ÿ∂ÿßŸÅÿ© "${book.title}" ÿ•ŸÑŸâ ÿßŸÑÿ≥ŸÑÿ©`);
                return [...prev, { ...book, quantity: 1 }];
            } else {
                toast("‚ö†Ô∏è Ÿáÿ∞ÿß ÿßŸÑŸÉÿ™ÿßÿ® ŸÖŸàÿ¨ŸàÿØ ÿ®ÿßŸÑŸÅÿπŸÑ ŸÅŸä ÿßŸÑÿ≥ŸÑÿ©", { icon: "‚ÑπÔ∏è" });
                return prev;
            }
        });
    }

    // ÿ•ÿ≤ÿßŸÑÿ© ŸÉÿ™ÿßÿ®
    function removeFromCart(bookId) {
        setCart((prev) => prev.filter((b) => b.id !== bookId));
        toast.error("üóëÔ∏è ÿ™ŸÖÿ™ ÿ•ÿ≤ÿßŸÑÿ© ÿßŸÑŸÉÿ™ÿßÿ® ŸÖŸÜ ÿßŸÑÿ≥ŸÑÿ©");
    }

    // ÿ™ÿπÿØŸäŸÑ ÿßŸÑŸÉŸÖŸäÿ©
    function updateQuantity(bookId, qty) {
        if (qty < 1) return;
        setCart((prev) =>
            prev.map((b) => (b.id === bookId ? { ...b, quantity: qty } : b))
        );
    }

    // ÿ™ŸÅÿ±Ÿäÿ∫ ÿßŸÑÿ≥ŸÑÿ©
    function clearCart() {
        setCart([]);
        toast("üßπ ÿ™ŸÖ ÿ•ŸÅÿ±ÿßÿ∫ ÿßŸÑÿ≥ŸÑÿ©");
    }

    return (
        <CartContext.Provider
            value={{ cart, addToCart, removeFromCart, updateQuantity, clearCart }}
        >
            {children}
        </CartContext.Provider>
    );
}

export function useCart() {
    return useContext(CartContext);
}
