// ‚úÖ src/pages/admin/ManageBooks.jsx
import { useEffect, useState } from "react";
import { db } from "../../firebase";
import { ref, get, update, remove } from "firebase/database";
import {
    getStorage,
    ref as sRef,
    uploadBytes,
    getDownloadURL,
    deleteObject,
} from "firebase/storage";
import toast, { Toaster } from "react-hot-toast";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";

export default function ManageBooks() {
    const [books, setBooks] = useState([]);
    const [filteredBooks, setFilteredBooks] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [loading, setLoading] = useState(true);
    const [savingAll, setSavingAll] = useState(false);
    const navigate = useNavigate();
    const storage = getStorage();

    // ‚úÖ ÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ
    useEffect(() => {
        const adminLoggedIn = localStorage.getItem("isAdmin");
        if (!adminLoggedIn) navigate("/admin/login");
    }, [navigate]);

    // ‚úÖ ÿ¨ŸÑÿ® ÿßŸÑŸÉÿ™ÿ® ŸÖŸÜ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
    useEffect(() => {
        const fetchBooks = async () => {
            try {
                const snapshot = await get(ref(db, "books"));
                if (snapshot.exists()) {
                    const data = snapshot.val();
                    const booksArray = Object.entries(data).map(([key, value]) => ({
                        key,
                        ...value,
                    }));
                    setBooks(booksArray.sort((a, b) => b.id - a.id));
                    setFilteredBooks(booksArray);
                } else {
                    toast.error("üì≠ ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÉÿ™ÿ® ÿ®ÿπÿØ");
                }
            } catch (err) {
                console.error(err);
                toast.error("‚ùå ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÉÿ™ÿ®");
            } finally {
                setLoading(false);
            }
        };
        fetchBooks();
    }, []);

    // ‚úÖ ÿßŸÑÿ®ÿ≠ÿ´ ÿßŸÑŸÅŸàÿ±Ÿä
    useEffect(() => {
        const term = searchTerm.toLowerCase();
        setFilteredBooks(
            books.filter(
                (b) =>
                    b.title?.toLowerCase().includes(term) ||
                    b.author?.toLowerCase().includes(term)
            )
        );
    }, [searchTerm, books]);

    // ‚úÖ ÿ™ÿπÿØŸäŸÑ ŸÖÿ≠ŸÑŸä
    const handleChange = (key, field, value) => {
        setBooks((prev) =>
            prev.map((book) => (book.key === key ? { ...book, [field]: value } : book))
        );
    };

    // ‚úÖ ÿ±ŸÅÿπ ÿµŸàÿ±ÿ© ÿ¨ÿØŸäÿØÿ©
    const handleImageUpload = async (key, file) => {
        if (!file) return;
        try {
            const imageRef = sRef(storage, `book_images/${Date.now()}_${file.name}`);
            await uploadBytes(imageRef, file);
            const url = await getDownloadURL(imageRef);
            handleChange(key, "images", url);
            toast.success("üì∑ ÿ™ŸÖ ÿ±ŸÅÿπ ÿßŸÑÿµŸàÿ±ÿ© ÿ®ŸÜÿ¨ÿßÿ≠");
        } catch (err) {
            toast.error("‚ùå ŸÅÿ¥ŸÑ ÿ±ŸÅÿπ ÿßŸÑÿµŸàÿ±ÿ©");
            console.error(err);
        }
    };

    // ‚úÖ ÿ≠ÿ∞ŸÅ ÿßŸÑÿµŸàÿ±ÿ©
    const handleDeleteImage = async (key, imageUrl) => {
        try {
            if (!imageUrl) {
                toast.error("‚ùå ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿµŸàÿ±ÿ© ŸÑÿ≠ÿ∞ŸÅŸáÿß");
                return;
            }

            // ÿ≠ÿ∞ŸÅ ÿßŸÑÿµŸàÿ±ÿ© ŸÖŸÜ Storage (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)
            const imagePath = decodeURIComponent(imageUrl.split("/o/")[1].split("?")[0]);
            const imageRef = sRef(storage, imagePath);
            await deleteObject(imageRef).catch(() => {});

            // ÿ≠ÿ∞ŸÅ ÿßŸÑÿµŸàÿ±ÿ© ŸÖŸÜ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
            await update(ref(db, `books/${key}`), { image: null });

            // ÿ≠ÿ∞ŸÅŸáÿß ŸÖÿ≠ŸÑŸäŸãÿß ŸÖŸÜ ÿßŸÑŸàÿßÿ¨Ÿáÿ©
            setBooks((prev) =>
                prev.map((book) =>
                    book.key === key ? { ...book, image: null } : book
                )
            );

            toast.success("üóëÔ∏è ÿ™ŸÖ ÿ≠ÿ∞ŸÅ ÿßŸÑÿµŸàÿ±ÿ© ÿ®ŸÜÿ¨ÿßÿ≠");
        } catch (err) {
            toast.error("‚ùå ŸÅÿ¥ŸÑ ÿ≠ÿ∞ŸÅ ÿßŸÑÿµŸàÿ±ÿ©");
            console.error(err);
        }
    };

    // ‚úÖ ÿ≠ŸÅÿ∏ ŸÅÿ±ÿØŸä
    const handleSave = async (book) => {
        try {
            await update(ref(db, `books/${book.key}`), book);
            toast.success(`‚úÖ ÿ™ŸÖ ÿ≠ŸÅÿ∏ "${book.title}"`);
        } catch (err) {
            toast.error("‚ùå ŸÅÿ¥ŸÑ ÿßŸÑÿ≠ŸÅÿ∏");
        }
    };

    // ‚úÖ ÿ≠ÿ∞ŸÅ ŸÉÿ™ÿßÿ®
    const handleDelete = async (key, title) => {
        if (!window.confirm(`ŸáŸÑ ÿ£ŸÜÿ™ ŸÖÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ≠ÿ∞ŸÅ "${title}"ÿü`)) return;
        try {
            await remove(ref(db, `books/${key}`));
            setBooks((prev) => prev.filter((b) => b.key !== key));
            toast.success("üóëÔ∏è ÿ™ŸÖ ÿ≠ÿ∞ŸÅ ÿßŸÑŸÉÿ™ÿßÿ®");
        } catch (err) {
            toast.error("‚ùå ŸÅÿ¥ŸÑ ÿßŸÑÿ≠ÿ∞ŸÅ");
        }
    };

    // ‚úÖ ÿ≠ŸÅÿ∏ ÿßŸÑŸÉŸÑ
    const handleSaveAll = async () => {
        try {
            setSavingAll(true);
            const updates = {};
            books.forEach((book) => (updates[`books/${book.key}`] = book));
            await update(ref(db), updates);
            toast.success("üíæ ÿ™ŸÖ ÿ≠ŸÅÿ∏ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ™ÿπÿØŸäŸÑÿßÿ™");
        } catch {
            toast.error("‚ùå ŸÅÿ¥ŸÑ ÿßŸÑÿ≠ŸÅÿ∏ ÿßŸÑŸÉŸÑŸä");
        } finally {
            setSavingAll(false);
        }
    };

    if (loading)
        return (
            <div className="text-center py-20 text-gray-600 text-lg">
                ‚è≥ ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...
            </div>
        );

    return (
        <div className="max-w-7xl mx-auto px-4 py-10 font-sans">
            <Toaster />

            {/* üîç ÿ¥ÿ±Ÿäÿ∑ ÿßŸÑÿ®ÿ≠ÿ´ Ÿàÿ≤ÿ± ÿ≠ŸÅÿ∏ ÿßŸÑŸÉŸÑ */}
            <div className="flex flex-col sm:flex-row justify-between items-center mb-10 gap-4">
                <h2 className="text-3xl font-extrabold text-blue-700">üõ†Ô∏è ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÉÿ™ÿ®</h2>

                <input
                    type="text"
                    placeholder="üîç ÿßÿ®ÿ≠ÿ´ ÿ®ÿßÿ≥ŸÖ ÿßŸÑŸÉÿ™ÿßÿ® ÿ£Ÿà ÿßŸÑŸÖÿ§ŸÑŸÅ..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="border border-gray-300 rounded-lg px-4 py-2 w-full sm:w-80 focus:ring-2 focus:ring-blue-400"
                />

                <button
                    onClick={handleSaveAll}
                    disabled={savingAll}
                    className={`${
                        savingAll
                            ? "bg-gray-400 cursor-not-allowed"
                            : "bg-green-600 hover:bg-green-700"
                    } text-white px-6 py-2 rounded-lg font-semibold shadow transition`}
                >
                    {savingAll ? "üíæ ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ≠ŸÅÿ∏..." : "üíæ ÿ≠ŸÅÿ∏ ÿßŸÑŸÉŸÑ"}
                </button>
            </div>

            {/* üé® ÿ¥ÿ®ŸÉÿ© ÿßŸÑÿ®ÿ∑ÿßŸÇÿßÿ™ */}
            {filteredBooks.length === 0 ? (
                <p className="text-center text-gray-500 py-20 text-lg">
                    üì≠ ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÜÿ™ÿßÿ¶ÿ¨ ŸÖÿ∑ÿßÿ®ŸÇÿ©
                </p>
            ) : (
                <motion.div
                    initial="hidden"
                    animate="visible"
                    variants={{
                        hidden: { opacity: 0 },
                        visible: { opacity: 1, transition: { staggerChildren: 0.05 } },
                    }}
                    className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3"
                >
                    {filteredBooks.map((book) => (
                        <motion.div
                            key={book.key}
                            variants={{
                                hidden: { opacity: 0, y: 20 },
                                visible: { opacity: 1, y: 0 },
                            }}
                            className="bg-white rounded-2xl shadow-md border border-gray-100 overflow-hidden hover:shadow-lg transition flex flex-col"
                        >
                            {/* üñºÔ∏è ÿµŸàÿ±ÿ© ÿßŸÑŸÉÿ™ÿßÿ® + ÿ≤ÿ± ÿßŸÑÿ≠ÿ∞ŸÅ */}
                            <div className="relative h-64 bg-gray-100">
                                {book.images ? (
                                    <>
                                        <img
                                            src={book.images}
                                            alt={book.title}
                                            className="w-full h-full object-cover"
                                        />
                                        <button
                                            onClick={() =>
                                                handleDeleteImage(book.key, book.image)
                                            }
                                            className="absolute top-2 left-2 bg-red-600 text-white px-3 py-1 text-xs rounded-lg hover:bg-red-700 transition"
                                        >
                                            üóëÔ∏è ÿ≠ÿ∞ŸÅ ÿßŸÑÿµŸàÿ±ÿ©
                                        </button>
                                    </>
                                ) : (
                                    <div className="flex items-center justify-center h-full text-gray-400">
                                        ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿµŸàÿ±ÿ©
                                    </div>
                                )}
                                <input
                                    type="file"
                                    accept="image/*"
                                    onChange={(e) =>
                                        handleImageUpload(book.key, e.target.files[0])
                                    }
                                    className="absolute bottom-2 right-2 bg-white bg-opacity-80 text-xs rounded-lg px-2 py-1 cursor-pointer"
                                />
                            </div>

                            {/* üìù ŸÖÿ≠ÿ™ŸàŸâ ÿßŸÑŸÉÿ™ÿßÿ® */}
                            <div className="p-4 flex flex-col flex-1 space-y-3">
                                <input
                                    type="text"
                                    value={book.title || ""}
                                    onChange={(e) =>
                                        handleChange(book.key, "title", e.target.value)
                                    }
                                    className="font-bold text-lg border rounded-lg px-2 py-1"
                                />

                                <input
                                    type="text"
                                    value={book.author || ""}
                                    onChange={(e) =>
                                        handleChange(book.key, "author", e.target.value)
                                    }
                                    className="text-gray-700 border rounded-lg px-2 py-1"
                                />

                                <input
                                    type="number"
                                    value={book.price || ""}
                                    onChange={(e) =>
                                        handleChange(book.key, "price", e.target.value)
                                    }
                                    placeholder="ÿßŸÑÿ≥ÿπÿ±"
                                    className="text-gray-700 border rounded-lg px-2 py-1"
                                />

                                <textarea
                                    value={book.description || ""}
                                    onChange={(e) =>
                                        handleChange(book.key, "description", e.target.value)
                                    }
                                    placeholder="ÿßŸÑŸàÿµŸÅ..."
                                    className="border rounded-lg px-2 py-1 h-20 text-sm resize-none"
                                />

                                <select
                                    value={book.category}
                                    onChange={(e) =>
                                        handleChange(book.key, "category", e.target.value)
                                    }
                                    className="border rounded-lg px-2 py-1"
                                >
                                    <option value="ÿ±ŸàÿßŸäÿ©">ÿ±ŸàÿßŸäÿ©</option>
                                    <option value="ÿ™ÿßÿ±ŸäÿÆ">ÿ™ÿßÿ±ŸäÿÆ</option>
                                    <option value="ŸÅŸÑÿ≥ŸÅÿ©">ŸÅŸÑÿ≥ŸÅÿ©</option>
                                    <option value="ÿπŸÑŸÖ ŸÜŸÅÿ≥">ÿπŸÑŸÖ ŸÜŸÅÿ≥</option>
                                    <option value="ÿ¥ÿπÿ±">ÿ¥ÿπÿ±</option>
                                    <option value="ÿ≥Ÿäÿ±ÿ© ÿ∞ÿßÿ™Ÿäÿ©">ÿ≥Ÿäÿ±ÿ© ÿ∞ÿßÿ™Ÿäÿ©</option>
                                    <option value="ÿ≥Ÿäÿßÿ≥ÿ©">ÿ≥Ÿäÿßÿ≥ÿ©</option>
                                </select>

                                <select
                                    value={book.status}
                                    onChange={(e) =>
                                        handleChange(book.key, "status", e.target.value)
                                    }
                                    className={`border rounded-lg px-2 py-1 ${
                                        book.status === "sold"
                                            ? "bg-red-100 text-red-700"
                                            : "bg-green-100 text-green-700"
                                    }`}
                                >
                                    <option value="available">üìó ŸÖÿ™ÿßÿ≠</option>
                                    <option value="sold">üìï ÿ™ŸÖ ÿßŸÑÿ®Ÿäÿπ</option>
                                </select>
                            </div>

                            {/* ‚öôÔ∏è ÿßŸÑÿ£ÿ≤ÿ±ÿßÿ± ÿßŸÑÿ≥ŸÅŸÑŸäÿ© */}
                            <div className="flex justify-between p-4 border-t">
                                <button
                                    onClick={() => handleSave(book)}
                                    className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-1 rounded-lg transition"
                                >
                                    üíæ ÿ≠ŸÅÿ∏
                                </button>
                                <button
                                    onClick={() => handleDelete(book.key, book.title)}
                                    className="bg-red-500 hover:bg-red-600 text-white px-4 py-1 rounded-lg transition"
                                >
                                    üóëÔ∏è ÿ≠ÿ∞ŸÅ ÿßŸÑŸÉÿ™ÿßÿ®
                                </button>
                            </div>
                        </motion.div>
                    ))}
                </motion.div>
            )}
        </div>
    );
}
