// ‚úÖ src/pages/admin/ManageBooks.jsx
import { useEffect, useState } from "react";
import { db } from "../../firebase";
import { ref, get, update } from "firebase/database";
import toast, { Toaster } from "react-hot-toast";
import { useNavigate } from "react-router-dom";

export default function ManageBooks() {
    const [books, setBooks] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    // ‚úÖ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ
    useEffect(() => {
        const adminLoggedIn = localStorage.getItem("adminAuth");
        if (!adminLoggedIn) navigate("/admin/login");
    }, [navigate]);

    // ‚úÖ ÿ¨ŸÑÿ® ÿßŸÑŸÉÿ™ÿ® ŸÖŸÜ Realtime Database
    useEffect(() => {
        const fetchBooks = async () => {
            try {
                const snapshot = await get(ref(db, "books"));
                if (snapshot.exists()) {
                    const data = snapshot.val();
                    const booksArray = Object.entries(data).map(([key, value]) => ({
                        key,
                        ...value,
                    }));
                    // ÿ™ÿ±ÿ™Ÿäÿ® ÿßŸÑÿ£ÿ≠ÿØÿ´ ÿ£ŸàŸÑŸãÿß
                    setBooks(booksArray.sort((a, b) => b.id - a.id));
                }
            } catch (err) {
                toast.error("‚ùå ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÉÿ™ÿ®");
                console.error(err);
            } finally {
                setLoading(false);
            }
        };
        fetchBooks();
    }, []);

    // ‚úÖ ÿ™ÿπÿØŸäŸÑ ÿßŸÑŸÇŸäŸÖ ŸÖÿ≠ŸÑŸäŸãÿß
    const handleChange = (key, field, value) => {
        setBooks((prev) =>
            prev.map((book) => (book.key === key ? { ...book, [field]: value } : book))
        );
    };

    // ‚úÖ ÿ≠ŸÅÿ∏ ÿßŸÑÿ™ÿπÿØŸäŸÑÿßÿ™ ŸÅŸä Firebase
    const handleSave = async (book) => {
        try {
            await update(ref(db, `books/${book.key}`), {
                category: book.category,
                status: book.status,
            });
            toast.success("‚úÖ ÿ™ŸÖ ÿ≠ŸÅÿ∏ ÿßŸÑÿ™ÿπÿØŸäŸÑÿßÿ™ ÿ®ŸÜÿ¨ÿßÿ≠");
        } catch (err) {
            toast.error("‚ùå ŸÅÿ¥ŸÑ ÿßŸÑÿ≠ŸÅÿ∏");
            console.error(err);
        }
    };

    if (loading)
        return (
            <div className="text-center py-20 text-gray-600 text-lg">
                ‚è≥ ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...
            </div>
        );

    return (
        <div className="max-w-6xl mx-auto p-6 bg-white shadow-md rounded-lg mt-10">
            <Toaster />
            <h2 className="text-2xl font-bold text-center mb-6 text-blue-700">
                üõ†Ô∏è ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÉÿ™ÿ®
            </h2>

            <div className="overflow-x-auto">
                <table className="min-w-full border border-gray-200 text-right">
                    <thead className="bg-blue-50">
                        <tr>
                            <th className="px-4 py-2 border">ÿßŸÑÿπŸÜŸàÿßŸÜ</th>
                            <th className="px-4 py-2 border">ÿßŸÑŸÖÿ§ŸÑŸÅ</th>
                            <th className="px-4 py-2 border">ÿßŸÑÿ™ÿµŸÜŸäŸÅ</th>
                            <th className="px-4 py-2 border">ÿßŸÑÿ≠ÿßŸÑÿ©</th>
                            <th className="px-4 py-2 border">ÿ•ÿ¨ÿ±ÿßÿ°</th>
                        </tr>
                    </thead>
                    <tbody>
                        {books.map((book) => (
                            <tr key={book.key} className="hover:bg-gray-50">
                                <td className="border px-4 py-2">{book.title}</td>
                                <td className="border px-4 py-2">{book.author}</td>

                                {/* üè∑Ô∏è ÿßŸÑÿ™ÿµŸÜŸäŸÅ */}
                                <td className="border px-4 py-2">
                                    <select
                                        value={book.category}
                                        onChange={(e) =>
                                            handleChange(book.key, "category", e.target.value)
                                        }
                                        className="border rounded-md px-2 py-1 w-full"
                                    >
                                        <option value="ÿ±ŸàÿßŸäÿ©">ÿ±ŸàÿßŸäÿ©</option>
                                        <option value="ÿ™ÿßÿ±ŸäÿÆ">ÿ™ÿßÿ±ŸäÿÆ</option>
                                        <option value="ŸÅŸÑÿ≥ŸÅÿ©">ŸÅŸÑÿ≥ŸÅÿ©</option>
                                        <option value="ÿπŸÑŸÖ ŸÜŸÅÿ≥">ÿπŸÑŸÖ ŸÜŸÅÿ≥</option>
                                        <option value="ÿ¥ÿπÿ±">ÿ¥ÿπÿ±</option>
                                        <option value="ÿ≥Ÿäÿ±ÿ© ÿ∞ÿßÿ™Ÿäÿ©">ÿ≥Ÿäÿ±ÿ© ÿ∞ÿßÿ™Ÿäÿ©</option>
                                        <option value="ÿ≥Ÿäÿßÿ≥ÿ©">ÿ≥Ÿäÿßÿ≥ÿ©</option>
                                    </select>
                                </td>

                                {/* üìó ÿßŸÑÿ≠ÿßŸÑÿ© */}
                                <td className="border px-4 py-2">
                                    <select
                                        value={book.status}
                                        onChange={(e) =>
                                            handleChange(book.key, "status", e.target.value)
                                        }
                                        className={`border rounded-md px-2 py-1 w-full ${book.status === "sold"
                                            ? "bg-red-100 text-red-700"
                                            : "bg-green-100 text-green-700"
                                            }`}
                                    >
                                        <option value="available">üìó ŸÖÿ™ÿßÿ≠</option>
                                        <option value="sold">üìï ÿ™ŸÖ ÿßŸÑÿ®Ÿäÿπ</option>
                                    </select>
                                </td>

                                {/* üíæ ÿ≠ŸÅÿ∏ */}
                                <td className="border px-4 py-2 text-center">
                                    <button
                                        onClick={() => handleSave(book)}
                                        className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-1 rounded-md transition"
                                    >
                                        ÿ≠ŸÅÿ∏
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}
