import { useParams } from "react-router-dom"

export default function Category() {
  const { name } = useParams()

  // هنا يمكن لاحقًا ربط التصنيف بقاعدة بيانات أو JSON محلي
  return (
    <div className="space-y-4">
      <h1 className="text-3xl font-bold text-gray-800">تصنيف: {name}</h1>

      <p className="text-gray-600">
        سيتم عرض الكتب التي تنتمي إلى هذا التصنيف هنا لاحقًا.
      </p>

      {/* لاحقًا ستُضاف قائمة بالكتب حسب التصنيف */}
    </div>
  )
}
