// src/components/SearchBar.jsx
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import books from "../data/books.json";

export default function SearchBar() {
    const [filter, setFilter] = useState("all"); // ÿßŸÑŸÇŸäŸÖÿ© ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ© = ÿßŸÑŸÉŸÑ
    const navigate = useNavigate();

    // ÿ™ÿ∑ÿ®ŸäŸÇ ÿßŸÑŸÅŸÑÿ™ÿ±
    const handleSubmit = (e) => {
        e.preventDefault();

        let results = [];

        switch (filter) {
            case "novel": // ÿ±Ÿàÿßÿ¶Ÿä
                results = books.filter((book) => book.category === "ÿ±Ÿàÿßÿ¶Ÿä");
                break;

            case "non-novel": // ÿ∫Ÿäÿ± ÿ±Ÿàÿßÿ¶Ÿä
                results = books.filter((book) => book.category !== "ÿ±Ÿàÿßÿ¶Ÿä");
                break;

            case "translated": // ŸÖÿ™ÿ±ÿ¨ŸÖ
                results = books.filter((book) => book.type === "ŸÖÿ™ÿ±ÿ¨ŸÖ");
                break;

            case "arabic": // ÿπÿ±ÿ®Ÿä
                results = books.filter((book) => book.type === "ÿπÿ±ÿ®Ÿä");
                break;

            default: // ÿßŸÑŸÉŸÑ
                results = books;
                break;
        }

        // ÿßŸÑÿ∞Ÿáÿßÿ® ŸÑÿµŸÅÿ≠ÿ© ÿßŸÑŸÜÿ™ÿßÿ¶ÿ¨
        navigate("/search", {
            state: { results, keyword: filter },
        });
    };

    return (
        <form
            onSubmit={handleSubmit}
            className="flex gap-2 w-full max-w-xl mx-auto"
            aria-label="ÿ¥ÿ±Ÿäÿ∑ ÿßŸÑÿ®ÿ≠ÿ´"
        >
            <select
                value={filter}
                onChange={(e) => setFilter(e.target.value)}
                className="border border-gray-300 p-2 rounded w-full focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
                <option value="all">üìö ÿßŸÑŸÉŸÑ</option>
                <option value="novel">üìñ ÿ±Ÿàÿßÿ¶Ÿä</option>
                <option value="non-novel">üìò ÿ∫Ÿäÿ± ÿ±Ÿàÿßÿ¶Ÿä</option>
                <option value="translated">üåç ŸÖÿ™ÿ±ÿ¨ŸÖ</option>
                <option value="arabic">üá™üá¨ ÿπÿ±ÿ®Ÿä</option>
            </select>

            <button
                type="submit"
                className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded transition"
            >
                ÿπÿ±ÿ∂
            </button>
        </form>
    );
}
