import { useEffect, useState } from "react";
import { useFavorites } from "../context/FavoriteContext";
import booksData from "../data/books.json";
import BookCard from "../components/BookCard";
import { motion } from "framer-motion";

export default function Favorites() {
    const { favorites, removeFavorite, isFavorite } = useFavorites();
    const [favoriteBooks, setFavoriteBooks] = useState([]);

    useEffect(() => {
        const matchedBooks = booksData.filter((book) => favorites.includes(book.id));
        setFavoriteBooks(matchedBooks);
    }, [favorites]);

    if (favoriteBooks.length === 0) {
        return (
            <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                className="text-center mt-24 text-xl text-gray-600 font-medium"
            >
                üì≠ ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÉÿ™ÿ® ŸÖŸÅÿ∂ŸÑÿ© ÿ≠ÿßŸÑŸäÿßŸã
            </motion.div>
        );
    }

    return (
        <div className="max-w-7xl mx-auto px-4 py-10 text-right font-sans">
            {/* ÿßŸÑÿπŸÜŸàÿßŸÜ */}
            <motion.h1
                initial={{ opacity: 0, x: 40 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.6 }}
                className="text-3xl font-extrabold text-blue-800 mb-8"
            >
                üìö ÿßŸÑŸÉÿ™ÿ® ÿßŸÑŸÖŸÅÿ∂ŸÑÿ©
            </motion.h1>

            {/* ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑŸÉÿ™ÿ® */}
            <motion.div
                initial="hidden"
                animate="visible"
                variants={{
                    hidden: { opacity: 0 },
                    visible: {
                        opacity: 1,
                        transition: { staggerChildren: 0.1 },
                    },
                }}
                className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3"
            >
                {favoriteBooks.map((book) => (
                    <motion.div
                        key={book.id}
                        variants={{ hidden: { opacity: 0, y: 30 }, visible: { opacity: 1, y: 0 } }}
                    >
                        <BookCard book={book} />
                        <button
                            onClick={() => removeFavorite(book.id)}
                            className="w-full mt-2 text-center bg-red-100 hover:bg-red-200 text-red-600 font-medium py-2 rounded text-sm transition"
                        >
                            üóëÔ∏è ÿ•ÿ≤ÿßŸÑÿ© ŸÖŸÜ ÿßŸÑŸÖŸÅÿ∂ŸÑÿ©
                        </button>
                    </motion.div>
                ))}
            </motion.div>
        </div>
    );
}
