// src/pages/Admin/AdminDashboard.jsx
import { useState } from "react";
import { getDatabase, ref, push } from "firebase/database";
import { getStorage, ref as sRef, uploadBytes, getDownloadURL } from "firebase/storage";
import { app } from "../../firebase"; // ✅ تأكد أن ملف firebase.js موجود ومهيأ

export default function AdminDashboard() {
    const [book, setBook] = useState({
        title: "",
        author: "",
        type: "",
        category: "",
        price: "",
        HPaper: "",
        description: "",
    });
    const [images, setImages] = useState([]);
    const [loading, setLoading] = useState(false);

    const db = getDatabase(app);
    const storage = getStorage(app);

    const handleChange = (e) => {
        setBook({ ...book, [e.target.name]: e.target.value });
    };

    const handleImageUpload = async (e) => {
        setImages(Array.from(e.target.files));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        try {
            // رفع الصور أولاً
            const urls = [];
            for (const img of images) {
                const storageRef = sRef(storage, `books/${img.name}`);
                await uploadBytes(storageRef, img);
                const url = await getDownloadURL(storageRef);
                urls.push(url);
            }

            // إضافة البيانات إلى Realtime DB
            const newBook = { ...book, images: urls, isNew: true };
            await push(ref(db, "books"), newBook);

            alert("✅ تم إضافة الكتاب بنجاح!");
            setBook({
                title: "",
                author: "",
                type: "",
                category: "",
                price: "",
                HPaper: "",
                description: "",
            });
            setImages([]);
        } catch (err) {
            console.error(err);
            alert("حدث خطأ أثناء رفع البيانات.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="p-6 max-w-2xl mx-auto">
            <h1 className="text-2xl font-bold mb-6 text-center">لوحة إدارة الكتب</h1>

            <form onSubmit={handleSubmit} className="space-y-4">
                {["title", "author", "type", "category", "price", "HPaper", "description"].map((field) => (
                    <input
                        key={field}
                        type="text"
                        name={field}
                        value={book[field]}
                        onChange={handleChange}
                        placeholder={field}
                        className="w-full border rounded-lg p-2"
                    />
                ))}

                <input
                    type="file"
                    multiple
                    onChange={handleImageUpload}
                    className="w-full border rounded-lg p-2"
                />

                <button
                    type="submit"
                    disabled={loading}
                    className="w-full bg-green-600 text-white py-2 rounded-lg hover:bg-green-700"
                >
                    {loading ? "جاري الرفع..." : "إضافة الكتاب"}
                </button>
            </form>
        </div>
    );
}
