import { useParams, Link } from "react-router-dom";
import books from "../data/books.json";

const categories = ["ุฑูุงุฆู", "ููุณูุฉ", "ุนูู ููุณ", "ุชุงุฑูุฎ", "ูุชุฑุฌู", "ุนุฑุจู"];

export default function Category() {
  const { name } = useParams();

  const filteredBooks = books.filter((book) => {
    if (!name) return false;

    // ุงูุชุตูููุงุช ุงูุฎุงุตุฉ ุชุนุชูุฏ ุนูู type
    if (name === "ูุชุฑุฌู" || name === "ุนุฑุจู") {
      return book.type?.toLowerCase() === name.toLowerCase();
    }

    // ุจุงูู ุงูุชุตูููุงุช ุชุนุชูุฏ ุนูู category
    return book.category?.toLowerCase() === name.toLowerCase();
  });

  return (
    <div className="max-w-6xl mx-auto px-4 py-10 text-right space-y-6">
      <h1 className="text-3xl font-bold text-blue-800">๐ ุงูุชุตูููุงุช</h1>

      {/* ุฃุฒุฑุงุฑ ุงูุชุตูููุงุช */}
      <div className="flex flex-wrap gap-3 justify-end">
        {categories.map((category) => (
          <Link
            key={category}
            to={`/category/${category}`}
            className={`px-4 py-2 rounded border ${
              name === category
                ? "bg-blue-600 text-white"
                : "bg-white text-blue-600 hover:bg-blue-50"
            }`}
          >
            {category}
          </Link>
        ))}
      </div>

      {/* ุนุฑุถ ุงููุชุงุฆุฌ */}
      {name && (
        <>
          <h2 className="text-2xl font-semibold text-blue-700">
            ๐ ุชุตููู: {name}
          </h2>

          {filteredBooks.length === 0 ? (
            <p className="text-gray-600">ูุง ุชูุฌุฏ ูุชุจ ูู ูุฐุง ุงูุชุตููู ุญุงููุงู.</p>
          ) : (
            <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
              {filteredBooks.map((book) => (
                <div
                  key={book.id}
                  className="bg-white rounded-lg shadow hover:shadow-md transition overflow-hidden"
                >
                  <img
                    src={book.cover || "/placeholder.png"}
                    alt={book.title}
                    className="w-full h-56 object-cover"
                  />
                  <div className="p-4 space-y-2">
                    <h3 className="text-xl font-bold text-blue-800">
                      {book.title}
                    </h3>
                    <p className="text-gray-600">โ๏ธ {book.author}</p>
                    <p className="text-sm text-gray-700">๐ฐ {book.price}</p>
                    <Link
                      to={`/book/${book.id}`}
                      className="inline-block mt-2 text-sm text-white bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded"
                    >
                      ุนุฑุถ ุงูุชูุงุตูู
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          )}
        </>
      )}
    </div>
  );
}
