import { useState } from "react";

export default function BookImage({
    src,
    alt,
    className = "",
    fit = "cover", // cover | contain
    ratio = "h-72", // الطول الافتراضي (تقدر تغيره)
}) {
    const [loaded, setLoaded] = useState(false);

    if (!src) {
        return (
            <div
                className={`relative overflow-hidden rounded-md bg-gray-200 ${ratio}`}
            >
                <img
                    src="/placeholder.png"
                    alt={alt || "غلاف الكتاب"}
                    className={`w-full h-full object-${fit}`}
                    loading="lazy"
                />
            </div>
        );
    }

    const webpSrc = `/images/webp/${src.replace(/\.(jpg|jpeg|png)$/i, ".webp")}`;
    const originalSrc = `/images/${src}`;

    return (
        <div
            className={`relative overflow-hidden rounded-md bg-gray-100 ${ratio} ${className}`}
        >
            {/* Skeleton */}
            {!loaded && (
                <div className="absolute inset-0 bg-gray-200 animate-pulse rounded-md" />
            )}

            <picture>
                <source srcSet={webpSrc} type="image/webp" />
                <img
                    src={originalSrc}
                    alt={alt || "غلاف الكتاب"}
                    className={`w-full h-full object-${fit} transition-opacity duration-500 ${loaded ? "opacity-100" : "opacity-0"
                        }`}
                    loading="lazy"
                    onLoad={() => setLoaded(true)}
                />
            </picture>
        </div>
    );
}
