        { path: "/contact", label: "ØªÙˆØ§ØµÙ„ Ù…Ø¹Ù†Ø§" },
    ];

    return (
        <nav className="bg-white shadow-sm border-b border-gray-200 sticky top-0 z-50">
            <div className="max-w-7xl mx-auto flex justify-between items-center px-5 py-3">
                {/* ðŸª¶ Ø§Ù„Ø´Ø¹Ø§Ø± */}
                <NavLink to="/" className="flex-row-reverse">
                    <img
                        src="/images/logo.png"
                        alt="Ø´Ø¹Ø§Ø± Ø§Ù„Ù…ÙˆÙ‚Ø¹"
                        className="w-12 h-12 rounded-full border border-gray-200 shadow-sm"
                    />
                </NavLink>

                {/* ðŸ§­ Ø±ÙˆØ§Ø¨Ø· Ø§Ù„ØªÙ†Ù‚Ù„ Ù„Ù„Ø­ÙˆØ§Ø³ÙŠØ¨ */}
                <div className="hidden md:flex items-center gap-6">
                    {navLinks.map((link) => (
                        <NavLink
                            key={link.path}
                            to={link.path}
                            className={({ isActive }) =>
                                `px-3 py-2 rounded-lg transition ${isActive
                                    ? "bg-blue-100 text-blue-700 font-semibold"
                                    : "text-gray-700 hover:bg-gray-100 hover:text-blue-700"
                                }`
                            }
                        >
                            {link.label}
                        </NavLink>
                    ))}

                    {/* ðŸ›’ Ø£ÙŠÙ‚ÙˆÙ†Ø© Ø§Ù„Ø³Ù„Ø© */}
                    <NavLink to="/cart" className="relative">
                        <FiShoppingCart className="text-2xl text-blue-700" />
                        {cart.length > 0 && (
                            <span className="absolute -top-2 -right-2 bg-red-600 text-white text-xs w-5 h-5 flex items-center justify-center rounded-full shadow">
                                {cart.length}
                            </span>
                        )}
                    </NavLink>
                </div>

                {/* ðŸ“± Ø²Ø± Ø§Ù„Ù‚Ø§Ø¦Ù…Ø© Ù„Ù„Ù…ÙˆØ¨Ø§ÙŠÙ„ */}
                <div className="md:hidden flex items-center gap-4">
                    <button
                        onClick={() => setMenuOpen(!menuOpen)}
                        className="text-2xl text-gray-700"
                    >
                        {menuOpen ? <FiX /> : <FiMenu />}
                    </button>
                    <NavLink to="/cart" className="relative">
                        <FiShoppingCart className="text-2xl text-blue-700" />
                        {cart.length > 0 && (
                            <span className="absolute -top-2 -right-2 bg-red-600 text-white text-xs w-5 h-5 flex items-center justify-center rounded-full shadow">
                                {cart.length}
                            </span>
                        )}
                    </NavLink>
                </div>
            </div>

            {/* ðŸ“± Ø§Ù„Ù‚Ø§Ø¦Ù…Ø© Ø§Ù„Ù…Ù†Ø³Ø¯Ù„Ø© Ù„Ù„Ù…ÙˆØ¨Ø§ÙŠÙ„ */}
            {menuOpen && (
                <div className="md:hidden bg-white shadow-inner border-t border-gray-200 px-5 py-3 space-y-2">
                    {navLinks.map((link) => (
                        <NavLink
                            key={link.path}
                            to={link.path}
                            onClick={() => setMenuOpen(false)}
                            className={({ isActive }) =>
                                `block px-3 py-2 rounded-md text-base ${isActive
                                    ? "bg-blue-100 text-blue-700 font-semibold"
                                    : "text-gray-700 hover:bg-gray-100 hover:text-blue-700"
                                }`
                            }
                        >
                            {link.label}
                        </NavLink>
                    ))}
                </div>
            )}
        </nav>
    );
}
