import { useState, useEffect } from "react";
import { ref, push } from "firebase/database";
import { db } from "../../firebase";
import toast, { Toaster } from "react-hot-toast";

export default function AdminDashboard() {
    const [title, setTitle] = useState("");
    const [author, setAuthor] = useState("");
    const [transl, setTransl] = useState("");
    const [type, setType] = useState("ÿπÿ±ÿ®Ÿä");
    const [category, setCategory] = useState("");
    const [price, setPrice] = useState("");
    const [HPaper, setHPaper] = useState("");
    const [description, setDescription] = useState("");
    const [images, setImages] = useState([]);
    const [loading, setLoading] = useState(false);
    const [folderName, setFolderName] = useState("");

    // üîπ ÿ™ŸàŸÑŸäÿØ ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ¨ŸÑÿØ ÿ™ŸÑŸÇÿßÿ¶ŸäŸãÿß ÿ®ÿßŸÑÿ™ÿßÿ±ŸäÿÆ ÿßŸÑÿ≠ÿßŸÑŸä
    useEffect(() => {
        const today = new Date();
        const month = String(today.getMonth() + 1).padStart(2, "0");
        const day = String(today.getDate()).padStart(2, "0");
        setFolderName(`${month}-${day}`);
    }, []);

    // üîπ ÿßŸÑÿ™ÿπÿßŸÖŸÑ ŸÖÿπ ÿßŸÑÿµŸàÿ± ÿßŸÑŸÖÿ≠ŸÑŸäÿ© ŸÅŸÇÿ∑ (ÿ®ÿØŸàŸÜ ÿ±ŸÅÿπ)
    const handleImageSelection = (e) => {
        const files = Array.from(e.target.files);
        if (files.length === 0) return;

        const paths = files.map((file) => `/images/${folderName}/${file.name}`);
        setImages(paths);
        toast.success(`‚úÖ ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿØ ${files.length} ÿµŸàÿ±ÿ© (${folderName}) ÿ®ŸÜÿ¨ÿßÿ≠`);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!title || !author || !category || images.length === 0) {
            toast.error("‚ö†Ô∏è Ÿäÿ±ÿ¨Ÿâ ŸÖŸÑÿ° ÿ¨ŸÖŸäÿπ ÿßŸÑÿ≠ŸÇŸàŸÑ Ÿàÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿµŸàÿ±!");
            return;
        }

        setLoading(true);

        const newBook = {
            id: Date.now(),
            isNew: true,
            title,
            author,
            transl,
            type,
            category,
            images,
            price: price || "ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ",
            HPaper: HPaper || "",
            description: description || "",
            status: "available",
        };

        try {
            await push(ref(db, "books"), newBook);
            toast.success("üìö ÿ™ŸÖ ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑŸÉÿ™ÿßÿ® ÿ®ŸÜÿ¨ÿßÿ≠ ÿ•ŸÑŸâ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™!");
            setTitle("");
            setAuthor("");
            setTransl("");
            setType("ÿπÿ±ÿ®Ÿä");
            setCategory("");
            setPrice("");
            setHPaper("");
            setDescription("");
            setImages([]);
        } catch (err) {
            toast.error("‚ùå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ•ÿ∂ÿßŸÅÿ©: " + err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="max-w-2xl mx-auto p-6 bg-white shadow-md rounded-lg mt-10">
            <Toaster />
            <h2 className="text-2xl font-bold mb-6 text-center text-blue-700">
                üìò ŸÑŸàÿ≠ÿ© ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÉÿ™ÿ®
            </h2>

            <form onSubmit={handleSubmit} className="space-y-4">
                <p className="text-sm text-gray-600">
                    üóÇÔ∏è ŸÖÿ¨ŸÑÿØ ÿßŸÑÿµŸàÿ± ÿßŸÑÿ≠ÿßŸÑŸä:{" "}
                    <span className="font-semibold text-blue-600">{folderName}</span>
                </p>

                <input
                    type="text"
                    placeholder="ÿπŸÜŸàÿßŸÜ ÿßŸÑŸÉÿ™ÿßÿ®"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    className="w-full p-3 border rounded-md"
                />
                <input
                    type="text"
                    placeholder="ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ§ŸÑŸÅ"
                    value={author}
                    onChange={(e) => setAuthor(e.target.value)}
                    className="w-full p-3 border rounded-md"
                />
                <input
                    type="text"
                    placeholder="ÿßŸÑŸÖÿ™ÿ±ÿ¨ŸÖ (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)"
                    value={transl}
                    onChange={(e) => setTransl(e.target.value)}
                    className="w-full p-3 border rounded-md"
                />

                <select
                    value={type}
                    onChange={(e) => setType(e.target.value)}
                    className="w-full p-3 border rounded-md"
                >
                    <option value="ÿπÿ±ÿ®Ÿä">ÿπÿ±ÿ®Ÿä</option>
                    <option value="ÿπÿßŸÑŸÖŸä">ÿπÿßŸÑŸÖŸä</option>
                </select>

                <select
                    value={category}
                    onChange={(e) => setCategory(e.target.value)}
                    className="w-full p-3 border rounded-md"
                >
                    <option value="">ÿßÿÆÿ™ÿ± ÿßŸÑŸÅÿ¶ÿ©</option>
                    <option value="ÿ±ŸàÿßŸäÿ©">ÿ±ŸàÿßŸäÿ©</option>
                    <option value="ÿ™ÿßÿ±ŸäÿÆ">ÿ™ÿßÿ±ŸäÿÆ</option>
                    <option value="ŸÖÿ≥ÿ±ÿ≠Ÿäÿ©">ŸÖÿ≥ÿ±ÿ≠Ÿäÿ©</option>
                    <option value="ÿ≥Ÿäÿ±ÿ© ÿ∞ÿßÿ™Ÿäÿ©">ÿ≥Ÿäÿ±ÿ© ÿ∞ÿßÿ™Ÿäÿ©</option>
                    <option value="ÿ¥ÿπÿ±">ÿ¥ÿπÿ±</option>
                    <option value="ŸÅŸÑÿ≥ŸÅÿ©">ŸÅŸÑÿ≥ŸÅÿ©</option>
                    <option value="ÿπŸÑŸÖ ŸÜŸÅÿ≥">ÿπŸÑŸÖ ŸÜŸÅÿ≥</option>
                    <option value="ÿ≥Ÿäÿßÿ≥ÿ©">ÿ≥Ÿäÿßÿ≥ÿ©</option>
                </select>

                <input
                    type="text"
                    placeholder="ÿßŸÑÿ≥ÿπÿ± (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                    className="w-full p-3 border rounded-md"
                />
                <input
                    type="text"
                    placeholder="ÿπÿØÿØ ÿßŸÑÿµŸÅÿ≠ÿßÿ™ (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)"
                    value={HPaper}
                    onChange={(e) => setHPaper(e.target.value)}
                    className="w-full p-3 border rounded-md"
                />
                <textarea
                    placeholder="ÿßŸÑŸàÿµŸÅ (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    className="w-full p-3 border rounded-md"
                    rows="4"
                ></textarea>

                <input
                    type="file"
                    accept="image/*"
                    multiple
                    onChange={handleImageSelection}
                    className="w-full p-3 border rounded-md bg-gray-50"
                />

                {images.length > 0 && (
