                            className="bg-white rounded-lg shadow hover:shadow-md transition overflow-hidden"
                        >
                            <img
                                src={book.cover || "/placeholder.png"}
                                alt={book.title}
                                className="w-full h-56 object-cover"
                            />
                            <div className="p-4 space-y-2">
