// src/components/SearchBar.jsx
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import books from "../data/books.json";
import toast from "react-hot-toast";

export default function SearchBar() {
  const [keyword, setKeyword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    const trimmed = keyword.trim();
    if (!trimmed) {
      toast.error("‚ö†Ô∏è ŸÖŸÜ ŸÅÿ∂ŸÑŸÉ ÿ£ÿØÿÆŸÑ ŸÉŸÑŸÖÿ© ŸÑŸÑÿ®ÿ≠ÿ´");
      return;
    }

    // ÿßŸÑÿ®ÿ≠ÿ´ ÿ®ÿßŸÑÿπŸÜŸàÿßŸÜ ÿ£Ÿà ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ§ŸÑŸÅ (case-insensitive)
    const results = books.filter((book) => {
      const t = book.title?.toLowerCase() ?? "";
      const a = book.author?.toLowerCase() ?? "";
      return t.includes(trimmed.toLowerCase()) || a.includes(trimmed.toLowerCase());
    });

    if (results.length === 0) {
      toast("‚ùå ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÜÿ™ÿßÿ¶ÿ¨ ŸÑŸáÿ∞ÿß ÿßŸÑÿ®ÿ≠ÿ´", { icon: "üì≠" });
      // ŸÜŸÜÿ™ŸÇŸÑ ŸÖÿπ keyword ŸÅŸÇÿ∑ (ÿµŸÅÿ≠ÿ© ÿßŸÑŸÜÿ™ÿßÿ¶ÿ¨ ÿ≥ÿ™ÿπÿ±ÿ∂ ÿ±ÿ≥ÿßŸÑÿ© ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÜÿ™ÿßÿ¶ÿ¨)
      navigate(`/search?q=${encodeURIComponent(trimmed)}`, { state: { results: [], keyword: trimmed } });
      return;
    }

    // ŸÜŸÖÿ±Ÿëÿ± ÿßŸÑŸÜÿ™ÿßÿ¶ÿ¨ ŸÅŸä ÿßŸÑŸÄ state + ŸÜÿ∂ÿπ q param ÿ≠ÿ™Ÿâ ÿ™ÿ∏ŸÑ ÿßŸÑŸÜÿ™ÿßÿ¶ÿ¨ ÿ®ÿπÿØ refresh
    navigate(`/search?q=${encodeURIComponent(trimmed)}`, {
      state: { results, keyword: trimmed },
    });
  };

  return (
    <form onSubmit={handleSubmit} className="flex gap-2 w-full max-w-xl mx-auto" aria-label="ÿ¥ÿ±Ÿäÿ∑ ÿßŸÑÿ®ÿ≠ÿ´">
      <input
        type="text"
        className="border border-gray-300 p-2 rounded w-full focus:outline-none focus:ring-2 focus:ring-blue-500"
        placeholder="üîç ÿßÿ®ÿ≠ÿ´ ÿ®ÿßÿ≥ŸÖ ÿßŸÑŸÉÿ™ÿßÿ® ÿ£Ÿà ÿßÿ≥ŸÖ ÿßŸÑŸÉÿßÿ™ÿ®..."
        value={keyword}
        onChange={(e) => setKeyword(e.target.value)}
        aria-label="ÿßÿ®ÿ≠ÿ´ ÿ®ÿßÿ≥ŸÖ ÿßŸÑŸÉÿ™ÿßÿ® ÿ£Ÿà ÿßŸÑŸÖÿ§ŸÑŸÅ"
      />
      <button
        type="submit"
        className="bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 text-white px-6 py-2 rounded-lg shadow transition font-medium"
      >
        ÿ®ÿ≠ÿ´
      </button>
    </form>
  );
}
