// src/pages/Category.jsx
import { useParams, Link } from "react-router-dom";
import { useCart } from "../context/CartContext";
import BookCard from "../components/BookCard";
import CategoryCard from "../components/CategoryCard";
import { motion } from "framer-motion";
import { useQuery } from "@tanstack/react-query";

// ÿßŸÑÿ™ÿµŸÜŸäŸÅÿßÿ™ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©
const categories = ["ÿ±Ÿàÿßÿ¶Ÿä", "ÿ∫Ÿäÿ± ÿ±Ÿàÿßÿ¶Ÿä", "ŸÖÿ™ÿ±ÿ¨ŸÖ", "ÿπÿ±ÿ®Ÿä"];

export default function Category() {
    const { name } = useParams();
    const { addToCart, removeFromCart, isInCart } = useCart();

    // ‚úÖ ÿ¨ŸÑÿ® ÿßŸÑŸÉÿ™ÿ® ŸÖŸÜ API ÿ£Ÿà JSON ŸÅŸä public
    const { data: books = [], isLoading, error } = useQuery({
        queryKey: ["books"],
        queryFn: () => fetch("/books.json").then((res) => res.json()),
    });

    if (isLoading)
        return <p className="text-center text-gray-600 py-10">‚è≥ ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÉÿ™ÿ®...</p>;

    if (error)
        return <p className="text-center text-red-600 py-10">‚ùå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™</p>;

    // ‚úÖ ÿ™ÿ±ÿ™Ÿäÿ® ÿßŸÑŸÉÿ™ÿ® (ÿßŸÑÿ£ÿ≠ÿØÿ´ ÿ£ŸàŸÑÿßŸã + ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© ŸÇÿ®ŸÑ ÿßŸÑŸÖÿ≠ÿ¨Ÿàÿ≤ÿ©)
    const sortedBooks = [...books].sort((a, b) => {
        if (a.status === "available" && b.status === "sold") return -1;
        if (a.status === "sold" && b.status === "available") return 1;
        return b.id - a.id;
    });

    // ‚úÖ ŸÅŸÑÿ™ÿ±ÿ© ÿ≠ÿ≥ÿ® ÿßŸÑÿ™ÿµŸÜŸäŸÅ
    const filteredBooks = name
        ? sortedBooks.filter((book) => {
            if (name === "ŸÖÿ™ÿ±ÿ¨ŸÖ" || name === "ÿπÿ±ÿ®Ÿä") {
                return book.type?.toLowerCase() === name.toLowerCase();
            }
            if (name === "ÿ∫Ÿäÿ± ÿ±Ÿàÿßÿ¶Ÿä") {
                return book.category?.toLowerCase() !== "ÿ±Ÿàÿßÿ¶Ÿä";
            }
            return book.category?.toLowerCase() === name.toLowerCase();
        })
        : sortedBooks;

    return (
        <div className="max-w-7xl mx-auto px-4 py-10 text-right font-sans space-y-10">
            {/* ÿπŸÜŸàÿßŸÜ ÿßŸÑÿµŸÅÿ≠ÿ© */}
            <motion.h1
                initial={{ opacity: 0, x: 40 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.6 }}
                className="text-3xl font-extrabold text-blue-800"
            >
                üõçÔ∏è ÿßŸÑÿ™ÿµŸÜŸäŸÅÿßÿ™
            </motion.h1>

            {/* ŸÅŸÑÿ™ÿ± ÿßŸÑÿ™ÿµŸÜŸäŸÅÿßÿ™ */}
            <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.2 }}
                className="flex flex-wrap gap-3 justify-end"
            >
                <Link to="/category">
                    <CategoryCard title="ÿßŸÑŸÉŸÑ" />
                </Link>

                {categories.map((category) => (
                    <Link key={category} to={`/category/${category}`}>
                        <CategoryCard title={category} />
                    </Link>
                ))}
            </motion.div>

            {/* ÿßŸÑÿπŸÜŸàÿßŸÜ ÿßŸÑŸÅÿ±ÿπŸä */}
            <motion.h2
                initial={{ opacity: 0, x: 40 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.6, delay: 0.3 }}
                className="text-2xl font-semibold text-blue-700 border-r-4 border-blue-600 pr-3"
            >
                üìñ {name ? `ÿ™ÿµŸÜŸäŸÅ: ${name}` : "ÿ¨ŸÖŸäÿπ ÿßŸÑŸÉÿ™ÿ®"}
            </motion.h2>

            {/* ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑŸÉÿ™ÿ® */}
            {filteredBooks.length === 0 ? (
                <p className="text-gray-600 text-lg">ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÉÿ™ÿ® ÿ∂ŸÖŸÜ Ÿáÿ∞ÿß ÿßŸÑÿ™ÿµŸÜŸäŸÅ ÿ≠ÿßŸÑŸäÿßŸã.</p>
            ) : (
                <motion.div
                    initial="hidden"
                    animate="visible"
                    variants={{
                        hidden: { opacity: 0 },
                        visible: {
                            opacity: 1,
                            transition: { staggerChildren: 0.1 },
                        },
                    }}
                    className="grid gap-8 sm:grid-cols-2 lg:grid-cols-3"
                >
                    {filteredBooks.map((book) => (
                        <motion.div
                            key={book.id}
                            variants={{
                                hidden: { opacity: 0, y: 30 },
                                visible: { opacity: 1, y: 0 },
                            }}
                            className="relative"
                        >
                            <BookCard book={book} />

                            {/* ÿ≤ÿ± ÿßŸÑÿ≥ŸÑÿ© Ÿäÿ∏Ÿáÿ± ŸÅŸÇÿ∑ ŸÑŸÑŸÉÿ™ÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© */}
                            {book.status !== "sold" && (
                                <button
                                    onClick={() =>
                                        isInCart(book.id)
                                            ? removeFromCart(book.id)
                                            : addToCart(book)
                                    }
                                    className={`mt-3 w-full text-sm px-4 py-2 rounded-lg transition font-medium ${isInCart(book.id)
                                            ? "bg-red-100 text-red-600 hover:bg-red-200"
                                            : "bg-green-100 text-green-700 hover:bg-green-200"
                                        }`}
                                >
                                    {isInCart(book.id) ? "üóëÔ∏è ÿ•ÿ≤ÿßŸÑÿ© ŸÖŸÜ ÿßŸÑÿ≥ŸÑÿ©" : "‚ûï ÿ£ÿ∂ŸÅ ÿ•ŸÑŸâ ÿßŸÑÿ≥ŸÑÿ©"}
                                </button>
                            )}
                        </motion.div>
                    ))}
                </motion.div>
            )}
        </div>
    );
}
