// src/components/SearchBar.jsx
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import books from "../data/books.json";
import toast from "react-hot-toast";

export default function SearchBar() {
    const [keyword, setKeyword] = useState("");
    const navigate = useNavigate();

    // ÿØÿßŸÑÿ© ÿßŸÑÿ®ÿ≠ÿ´
    const handleSubmit = (e) => {
        e.preventDefault();
        const trimmed = keyword.trim();

        // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ•ÿØÿÆÿßŸÑ
        if (!trimmed) {
            toast.error("‚ö†Ô∏è ŸÖŸÜ ŸÅÿ∂ŸÑŸÉ ÿ£ÿØÿÆŸÑ ŸÉŸÑŸÖÿ© ŸÑŸÑÿ®ÿ≠ÿ´");
            return;
        }

        // ÿßŸÑÿ®ÿ≠ÿ´ ŸÅŸä ÿßŸÑÿπŸÜŸàÿßŸÜ + ÿßŸÑŸÖÿ§ŸÑŸÅ
        const results = books.filter(
            (book) =>
                book.title.toLowerCase().includes(trimmed.toLowerCase()) ||
                book.author?.toLowerCase().includes(trimmed.toLowerCase())
        );

        // ÿ•ÿ∞ÿß ŸÖŸÅŸäÿ¥ ŸÜÿ™ÿßÿ¶ÿ¨
        if (results.length === 0) {
            toast("‚ùå ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÜÿ™ÿßÿ¶ÿ¨ ŸÑŸáÿ∞ÿß ÿßŸÑÿ®ÿ≠ÿ´", { icon: "üì≠" });
            return;
        }

        // ÿßŸÑÿ∞Ÿáÿßÿ® ŸÑÿµŸÅÿ≠ÿ© ÿßŸÑŸÜÿ™ÿßÿ¶ÿ¨
        navigate("/search", {
            state: { results, keyword: trimmed },
        });
    };

    return (
        <form
            onSubmit={handleSubmit}
            className="flex gap-2 w-full max-w-xl mx-auto"
            aria-label="ÿ¥ÿ±Ÿäÿ∑ ÿßŸÑÿ®ÿ≠ÿ´"
        >
            <input
                type="text"
                className="border border-gray-300 p-2 rounded w-full focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="üîç ÿßÿ®ÿ≠ÿ´ ÿπŸÜ ŸÉÿ™ÿßÿ® ÿ£Ÿà ŸÖÿ§ŸÑŸÅ..."
                value={keyword}
                onChange={(e) => setKeyword(e.target.value)}
            />
            <button
                type="submit"
                className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded transition"
            >
                ÿ®ÿ≠ÿ´
            </button>
        </form>
    );
}
