// src/components/CategoryCard.jsx
import { motion } from "framer-motion";

export default function CategoryCard({ title = "بدون عنوان", onClick = () => { } }) {
    return (
        <motion.button
            onClick={onClick}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            className="w-full p-3 rounded bg-gray-100 text-center hover:bg-blue-100 transition duration-200 text-gray-800 font-medium shadow-sm"
        >
            {title}
        </motion.button>
    );
}
