import { useState } from "react";
import { ref, push } from "firebase/database";
import { db } from "../../firebase";
import toast, { Toaster } from "react-hot-toast";

export default function AdminDashboard() {
    const [title, setTitle] = useState("");
    const [author, setAuthor] = useState("");
    const [transl, setTransl] = useState("");
    const [type, setType] = useState("ÿπÿ±ÿ®Ÿä");
    const [category, setCategory] = useState("");
    const [price, setPrice] = useState("");
    const [HPaper, setHPaper] = useState("");
    const [description, setDescription] = useState("");
    const [images, setImages] = useState([]);
    const [preview, setPreview] = useState([]);
    const [loading, setLoading] = useState(false);

    // ‚úÖ ÿßŸÑÿµŸàÿ± ŸÖÿ≠ŸÑŸäÿ© ŸÅŸÇÿ∑ (ŸÖŸÜ ŸÖÿ¨ŸÑÿØ public/images/books)
    const handleLocalImages = (files) => {
        const selectedImages = [];
        const previews = [];

        for (const file of files) {
            const localPath = `/images/books/${file.name}`;
            selectedImages.push(localPath);
            previews.push(URL.createObjectURL(file));
        }

        setImages(selectedImages);
        setPreview(previews);
        toast.success("‚úÖ ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿØ ÿßŸÑÿµŸàÿ± ÿ®ŸÜÿ¨ÿßÿ≠!");
    };

    // ‚úÖ ÿßŸÑÿ≠ŸÅÿ∏ ŸÅŸä Firebase Realtime Database
    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!title || !author || !category || images.length === 0) {
            toast.error("‚ö†Ô∏è Ÿäÿ±ÿ¨Ÿâ ŸÖŸÑÿ° ÿ¨ŸÖŸäÿπ ÿßŸÑÿ≠ŸÇŸàŸÑ Ÿàÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿµŸàÿ±!");
            return;
        }

        setLoading(true);

        const newBook = {
            id: Date.now(),
            isNew: true,
            title,
            author,
            transl,
            type,
            category,
            images, // üîπ ÿ±Ÿàÿßÿ®ÿ∑ ÿßŸÑÿµŸàÿ± ÿßŸÑŸÖÿ≠ŸÑŸäÿ©
            price: price || "ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ",
            HPaper: HPaper || "",
            description: description || "",
            status: "available",
        };

        try {
            await push(ref(db, "books"), newBook);
            toast.success("üìö ÿ™ŸÖ ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑŸÉÿ™ÿßÿ® ÿ®ŸÜÿ¨ÿßÿ≠ ÿ•ŸÑŸâ Firebase!");
            // üîπ ÿ™ŸÅÿ±Ÿäÿ∫ ÿßŸÑÿ≠ŸÇŸàŸÑ ÿ®ÿπÿØ ÿßŸÑÿ•ÿ∂ÿßŸÅÿ©
            setTitle("");
            setAuthor("");
            setTransl("");
            setType("ÿπÿ±ÿ®Ÿä");
            setCategory("");
            setPrice("");
            setHPaper("");
            setDescription("");
            setImages([]);
            setPreview([]);
        } catch (err) {
            toast.error("‚ùå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ•ÿ∂ÿßŸÅÿ©: " + err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="max-w-2xl mx-auto p-6 bg-white shadow-md rounded-lg mt-10">
            <Toaster />
            <h2 className="text-2xl font-bold mb-6 text-center text-blue-700">
                üìò ŸÑŸàÿ≠ÿ© ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÉÿ™ÿ®
            </h2>

            <form onSubmit={handleSubmit} className="space-y-4">
                <input
                    type="text"
                    placeholder="ÿπŸÜŸàÿßŸÜ ÿßŸÑŸÉÿ™ÿßÿ®"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    className="w-full p-3 border rounded-md"
                />

                <input
                    type="text"
                    placeholder="ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ§ŸÑŸÅ"
                    value={author}
                    onChange={(e) => setAuthor(e.target.value)}
                    className="w-full p-3 border rounded-md"
                />

                <input
                    type="text"
                    placeholder="ÿßŸÑŸÖÿ™ÿ±ÿ¨ŸÖ (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)"
                    value={transl}
                    onChange={(e) => setTransl(e.target.value)}
                    className="w-full p-3 border rounded-md"
                />

                <select
                    value={type}
                    onChange={(e) => setType(e.target.value)}
                    className="w-full p-3 border rounded-md"
                >
                    <option value="ÿπÿ±ÿ®Ÿä">ÿπÿ±ÿ®Ÿä</option>
                    <option value="ÿπÿßŸÑŸÖŸä">ÿπÿßŸÑŸÖŸä</option>
                </select>

                <select
                    value={category}
                    onChange={(e) => setCategory(e.target.value)}
                    className="w-full p-3 border rounded-md"
                >
                    <option value="">ÿßÿÆÿ™ÿ± ÿßŸÑŸÅÿ¶ÿ©</option>
                    <option value="ÿ±ŸàÿßŸäÿ©">ÿ±ŸàÿßŸäÿ©</option>
                    <option value="ÿ™ÿßÿ±ŸäÿÆ">ÿ™ÿßÿ±ŸäÿÆ</option>
                    <option value="ŸÖÿ≥ÿ±ÿ≠Ÿäÿ©">ŸÖÿ≥ÿ±ÿ≠Ÿäÿ©</option>
                    <option value="ÿ≥Ÿäÿ± ÿ∞ÿßÿ™Ÿäÿ©">ÿ≥Ÿäÿ± ÿ∞ÿßÿ™Ÿäÿ©</option>
                    <option value="ÿ¥ÿπÿ±">ÿ¥ÿπÿ±</option>
                    <option value="ŸÅŸÑÿ≥ŸÅÿ©">ŸÅŸÑÿ≥ŸÅÿ©</option>
                    <option value="ÿπŸÑŸÖ ŸÜŸÅÿ≥">ÿπŸÑŸÖ ŸÜŸÅÿ≥</option>
                    <option value="ÿ≥Ÿäÿßÿ≥ÿ©">ÿ≥Ÿäÿßÿ≥ÿ©</option>
                </select>

                <input
                    type="text"
                    placeholder="ÿßŸÑÿ≥ÿπÿ± (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                    className="w-full p-3 border rounded-md"
                />

                <input
                    type="text"
                    placeholder="ÿπÿØÿØ ÿßŸÑÿµŸÅÿ≠ÿßÿ™ (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)"
                    value={HPaper}
                    onChange={(e) => setHPaper(e.target.value)}
                    className="w-full p-3 border rounded-md"
                />

                <textarea
                    placeholder="ÿßŸÑŸàÿµŸÅ (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    className="w-full p-3 border rounded-md"
                    rows="4"
                ></textarea>

                <input
                    type="file"
                    accept="image/*"
                    multiple
                    onChange={(e) => handleLocalImages(e.target.files)}
                    className="w-full p-3 border rounded-md bg-gray-50"
                />

                {preview.length > 0 && (
                    <div className="flex gap-2 flex-wrap mt-2">
                        {preview.map((src, i) => (
                            <img
                                key={i}
                                src={src}
                                alt={`Preview ${i}`}
                                className="w-24 h-24 object-cover rounded-md border"
                            />
                        ))}
                    </div>
                )}

                {loading && <p className="text-blue-600 text-sm">‚è≥ ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ•ÿ∂ÿßŸÅÿ©...</p>}

                <button
                    type="submit"
                    disabled={loading}
                    className="w-full py-3 bg-blue-600 text-white font-semibold rounded-md hover:bg-blue-700 transition"
                >
                    {loading ? "ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ•ÿ∂ÿßŸÅÿ©..." : "ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑŸÉÿ™ÿßÿ®"}
                </button>
            </form>
        </div>
    );
}
