// src/pages/AdminDashboard.jsx
import { useState } from "react";
import { ref, push } from "firebase/database";
import { db } from "../../firebase";
import toast, { Toaster } from "react-hot-toast";

export default function AdminDashboard() {
    const [title, setTitle] = useState("");
    const [author, setAuthor] = useState("");
    const [transl, setTransl] = useState("");
    const [type, setType] = useState("عربي");
    const [category, setCategory] = useState("");
    const [price, setPrice] = useState("");
    const [HPaper, setHPaper] = useState("");
    const [description, setDescription] = useState("");
    const [images, setImages] = useState([]); // array of final URLs like /images/10-30/file.jpg
    const [loading, setLoading] = useState(false);
    const [manualFolder, setManualFolder] = useState(""); // fallback folder name if needed

    /**
     * handleFiles:
     * - يدعم اختيار مجلد كامل (webkitdirectory) -> يستخرج file.webkitRelativePath
     * - لو webkitRelativePath غير متوفر => يستخدم manualFolder + file.name (fallback)
     *
     * ملاحظة: webkitdirectory يسمح للمستخدم باختيار مجلد داخل public/images/...
     * ثم نكوّن URL مثل: /images/<webkitRelativePath>
     */
    const handleFiles = (fileList) => {
        const files = Array.from(fileList || []);
        if (files.length === 0) {
            toast.error("لم يتم اختيار أي صور.");
            return;
        }

        // حاول استخراج المسارات النسبية من webkitRelativePath
        const urls = files.map((file) => {
            // webkitRelativePath مثال: "10-30/mybook.jpg" أو "10-30/subdir/photo.jpg"
            const rel = file.webkitRelativePath || file.relativePath || "";
            if (rel && rel.length > 0) {
                // تأكد من استبدال backslashes إلى slashes على Windows
                const normalized = rel.replace(/\\/g, "/");
                return normalized.startsWith("images/") || normalized.startsWith("/images/")
                    ? (normalized.startsWith("/") ? normalized : "/" + normalized)
                    : "/images/" + (normalized.startsWith("/") ? normalized.slice(1) : normalized);
            }

            // fallback: لو المستخدم لم يختار مجلداً، نستخدم اسم المجلد اليدوي إن وُجد
            if (manualFolder && manualFolder.trim().length > 0) {
                const fn = file.name.replace(/\\/g, "/");
                return `/images/${manualFolder}/${fn}`;
            }

            // لو لا يوجد أي طريقة لاستخراج المسار، نخزن اسم الملف فقط (لن يعمل كـURL حقيقي لكن يُظهر للمشرف)
            return file.name;
        });

        setImages(urls);
        toast.success(`تم تحديد ${urls.length} ملف(/ملفات).`);
    };

    // يتنادى عند اختيار الملفات من input[type=file]
    const onFileInputChange = (e) => {
        handleFiles(e.target.files);
    };

    // إرسال الكتاب إلى Realtime Database
    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!title || !author || !category || images.length === 0) {
            toast.error("يرجى ملء الحقول الأساسية (العنوان، المؤلف، الفئة) وتحديد الصور.");
            return;
        }

        setLoading(true);
        const newBook = {
            id: Date.now(),
            isNew: true,
            title,
            author,
            transl,
            type,
            category,
            images, // مصفوفة من روابط مثل /images/10-30/name.jpg
            price: price || "غير محدد",
            HPaper: HPaper || "",
            description: description || "",
            status: "available",
            createdAt: Date.now(),
        };

        try {
            await push(ref(db, "books"), newBook);
            toast.success("تمت إضافة الكتاب إلى قاعدة البيانات!");
            // إعادة التهيئة
            setTitle("");
            setAuthor("");
            setTransl("");
            setType("عربي");
            setCategory("");
            setPrice("");
            setHPaper("");
            setDescription("");
            setImages([]);
            setManualFolder("");
        } catch (err) {
            toast.error("حدث خطأ أثناء الحفظ: " + (err.message || err));
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="max-w-2xl mx-auto p-6 bg-white shadow-md rounded-lg mt-8">
            <Toaster />
            <h2 className="text-2xl font-bold mb-4 text-center text-blue-700">لوحة إدارة الكتب</h2>

            <form onSubmit={handleSubmit} className="space-y-4">
                <div className="text-sm text-gray-600">
                    اختر المجلد الكامل داخل <code>public/images</code> باستخدام زر "اختيار مجلد"
                    (مستحسن) أو أدخل اسم المجلد يدوياً كبديل.
                </div>

                {/* input لاختيار مجلد + ملفات (webkitdirectory) */}
                <div>
                    <label className="block text-sm font-medium text-gray-700">اختيار مجلد (مفضل)</label>
                    <input
                        type="file"
                        // @ts-ignore: webkitdirectory is a non-standard attribute but supported in chromium
                        webkitdirectory="true"
                        directory="true"
                        multiple
                        onChange={onFileInputChange}
                        className="mt-1"
                    />
                    <p className="text-xs text-gray-500 mt-1">
                        هذا الخيار يتيح اختيار مجلد كامل (مثال: public/images/10-30) وسيُستخدم المسار الداخلي.
                    </p>
                </div>

                {/* fallback: اسم المجلد إذا لم تُستخدم webkitdirectory */}
                <div>
                    <label className="block text-sm font-medium text-gray-700">اسم المجلد (اختياري - بديل)</label>
                    <input
                        type="text"
                        value={manualFolder}
                        onChange={(e) => setManualFolder(e.target.value)}
                        placeholder="مثال: 10-30"
                        className="w-full mt-1 p-2 border rounded"
                    />
                    <p className="text-xs text-gray-500 mt-1">
                        لو لم تستخدم اختيار المجلد، ضع اسم المجلد داخل <code>public/images/</code>.
                    </p>
                </div>

                {/* بيانات الكتاب */}
                <input
                    type="text"
                    placeholder="عنوان الكتاب"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    className="w-full p-2 border rounded"
                />
                <input
                    type="text"
                    placeholder="اسم المؤلف"
                    value={author}
                    onChange={(e) => setAuthor(e.target.value)}
                    className="w-full p-2 border rounded"
                />
                <input
                    type="text"
                    placeholder="المترجم (اختياري)"
                    value={transl}
                    onChange={(e) => setTransl(e.target.value)}
                    className="w-full p-2 border rounded"
                />
                <select value={type} onChange={(e) => setType(e.target.value)} className="w-full p-2 border rounded">
                    <option value="عربي">عربي</option>
                    <option value="عالمي">عالمي</option>
                </select>
                <select value={category} onChange={(e) => setCategory(e.target.value)} className="w-full p-2 border rounded">
                    <option value="">اختر الفئة</option>
                    <option value="رواية">رواية</option>
                    <option value="تاريخ">تاريخ</option>
                    <option value="شعر">شعر</option>
                    <option value="مسرحية">مسرحية</option>
                    <option value="سيرة ذاتية">سيرة ذاتية</option>
                </select>
                <input type="text" placeholder="السعر (اختياري)" value={price} onChange={(e) => setPrice(e.target.value)} className="w-full p-2 border rounded" />
                <input type="text" placeholder="عدد الصفحات (اختياري)" value={HPaper} onChange={(e) => setHPaper(e.target.value)} className="w-full p-2 border rounded" />
                <textarea placeholder="الوصف (اختياري)" value={description} onChange={(e) => setDescription(e.target.value)} className="w-full p-2 border rounded" rows="4" />

                {/* عرض قائمة المسارات النهائية */}
                {images.length > 0 && (
                    <div className="p-3 bg-gray-50 border rounded">
                        <div className="text-sm font-medium mb-2">الروابط التي ستحفظ في قاعدة البيانات:</div>
                        <ul className="list-disc list-inside text-sm text-gray-700">
                            {images.map((u, idx) => (
                                <li key={idx}>{u}</li>
                            ))}
                        </ul>
                    </div>
                )}

                <div>
                    <button type="submit" disabled={loading} className="w-full py-2 bg-blue-600 text-white rounded">
                        {loading ? "جاري الحفظ..." : "إضافة الكتاب"}
                    </button>
                </div>
            </form>
        </div>
    );
}
