// src/pages/AdminDashboard.jsx
import { useState } from "react";
import { ref, push } from "firebase/database";
import { ref as storageRef, uploadBytes, getDownloadURL } from "firebase/storage";
import { db, storage } from "../../firebase";
import toast, { Toaster } from "react-hot-toast";

export default function AdminDashboard() {
    const [title, setTitle] = useState("");
    const [author, setAuthor] = useState("");
    const [type, setType] = useState("ÿπÿ±ÿ®Ÿä");
    const [category, setCategory] = useState("");
    const [price, setPrice] = useState("");
    const [HPaper, setHPaper] = useState("");
    const [description, setDescription] = useState("");
    const [images, setImages] = useState([]);
    const [loading, setLoading] = useState(false);

    const handleImageUpload = async (files) => {
        setLoading(true);
        const urls = [];

        for (const file of files) {
            const imageRef = storageRef(storage, `books/${Date.now()}-${file.name}`);
            await uploadBytes(imageRef, file);
            const url = await getDownloadURL(imageRef);
            urls.push(url);
        }

        setImages(urls);
        setLoading(false);
        toast.success("‚úÖ ÿ™ŸÖ ÿ±ŸÅÿπ ÿßŸÑÿµŸàÿ± ÿ®ŸÜÿ¨ÿßÿ≠!");
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!title || !author || !category || images.length === 0) {
            toast.error("‚ö†Ô∏è Ÿäÿ±ÿ¨Ÿâ ŸÖŸÑÿ° ÿ¨ŸÖŸäÿπ ÿßŸÑÿ≠ŸÇŸàŸÑ Ÿàÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿµŸàÿ±!");
            return;
        }

        setLoading(true);

        const newBook = {
            id: Date.now(),
            isNew: true,
            title,
            author,
            type,
            category,
            images,
            price: price || "ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ",
            HPaper: HPaper || "",
            description: description || "",
            status: "available",
        };

        try {
            await push(ref(db, "books"), newBook);
            toast.success("üìö ÿ™ŸÖ ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑŸÉÿ™ÿßÿ® ÿ®ŸÜÿ¨ÿßÿ≠ ÿ•ŸÑŸâ Firebase!");
            setTitle("");
            setAuthor("");
            setType("ÿπÿ±ÿ®Ÿä");
            setCategory("");
            setPrice("");
            setHPaper("");
            setDescription("");
            setImages([]);
        } catch (err) {
            toast.error("‚ùå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ•ÿ∂ÿßŸÅÿ©: " + err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="max-w-2xl mx-auto p-6 bg-white shadow-md rounded-lg mt-10">
            <Toaster />
            <h2 className="text-2xl font-bold mb-6 text-center text-blue-700">üìò ŸÑŸàÿ≠ÿ© ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÉÿ™ÿ®</h2>

            <form onSubmit={handleSubmit} className="space-y-4">
                <input
                    type="text"
                    placeholder="ÿπŸÜŸàÿßŸÜ ÿßŸÑŸÉÿ™ÿßÿ®"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    className="w-full p-3 border rounded-md"
                />
                <input
                    type="text"
                    placeholder="ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ§ŸÑŸÅ"
                    value={author}
                    onChange={(e) => setAuthor(e.target.value)}
                    className="w-full p-3 border rounded-md"
                />
                <select
                    value={type}
                    onChange={(e) => setType(e.target.value)}
                    className="w-full p-3 border rounded-md"
                >
                    <option value="ÿπÿ±ÿ®Ÿä">ÿπÿ±ÿ®Ÿä</option>
                    <option value="ÿπÿßŸÑŸÖŸä">ÿπÿßŸÑŸÖŸä</option>
                </select>
                <select
                    value={category}
                    onChange={(e) => setType(e.target.value)}
                    className="w-full p-3 border rounded-md"
                >
                    <option value="ÿ±ŸàÿßŸäÿ©">ÿ±ŸàÿßŸäÿ©</option>
                    <option value="ÿ™ÿßÿ±ŸäÿÆ">ÿ™ÿßÿ±ŸäÿÆ</option>
                    <option value="ŸÖÿ≥ÿ±ÿ≠">ŸÖÿ≥ÿ±ÿ≠</option>
                    <option value="ÿ¥ÿπÿ±">ÿ¥ÿπÿ±</option>
                    <option value="ŸÅŸÑÿ≥ŸÅÿ©">ŸÅŸÑÿ≥ŸÅÿ©</option>
                    <option value="ÿπŸÑŸÖ ŸÜŸÅÿ≥">ÿπŸÑŸÖ ŸÜŸÅÿ≥</option>
                    <option value="ÿ≥Ÿäÿßÿ≥ÿ©">ÿ≥Ÿäÿßÿ≥ÿ©</option>
                </select>
                {/* <input
                    type="text"
                    placeholder="ÿßŸÑŸÅÿ¶ÿ© (ÿ±ŸàÿßŸäÿ©ÿå ÿ™ÿßÿ±ŸäÿÆÿå ÿ¥ÿπÿ±...)"
                    value={category}
                    onChange={(e) => setCategory(e.target.value)}
                    className="w-full p-3 border rounded-md"
                /> */}
                <input
                    type="text"
                    placeholder="ÿßŸÑÿ≥ÿπÿ± (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                    className="w-full p-3 border rounded-md"
                />
                <input
                    type="text"
                    placeholder="ÿπÿØÿØ ÿßŸÑÿµŸÅÿ≠ÿßÿ™ (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)"
                    value={HPaper}
                    onChange={(e) => setHPaper(e.target.value)}
                    className="w-full p-3 border rounded-md"
                />
                <textarea
                    placeholder="ÿßŸÑŸàÿµŸÅ (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    className="w-full p-3 border rounded-md"
                    rows="4"
                ></textarea>

                <input
                    type="file"
                    accept="image/*"
                    multiple
                    onChange={(e) => handleImageUpload(e.target.files)}
                    className="w-full p-3 border rounded-md bg-gray-50"
                />

                {loading && <p className="text-blue-600 text-sm">‚è≥ ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ...</p>}

                <button
                    type="submit"
                    disabled={loading}
                    className="w-full py-3 bg-blue-600 text-white font-semibold rounded-md hover:bg-blue-700 transition"
                >
                    {loading ? "ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ•ÿ∂ÿßŸÅÿ©..." : "ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑŸÉÿ™ÿßÿ®"}
                </button>
            </form>
        </div>
    );
}
