// src/pages/Category.jsx
import { useParams } from "react-router-dom";
import { useQuery } from "@tanstack/react-query";
import axios from "axios";
import BookCard from "../components/BookCard";
import { motion } from "framer-motion";

export default function Category() {
    const { name } = useParams();

    const { data: books = [], isLoading, isError } = useQuery({
        queryKey: ["books"],
        queryFn: async () => {
            const res = await axios.get("/books.json");
            return res.data;
        },
    });

    if (isLoading)
        return (
            <div className="text-center text-blue-600 font-semibold py-10">
                ‚è≥ ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÉÿ™ÿ®...
            </div>
        );

    if (isError)
        return (
            <div className="text-center text-red-600 font-semibold py-10">
                ‚ùå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
            </div>
        );

    // ‚úÖ ÿ™ÿ±ÿ™Ÿäÿ® ÿßŸÑŸÉÿ™ÿ® ÿ®ÿ≠Ÿäÿ´ ÿßŸÑÿ£ÿ≠ÿØÿ´ (isNew) ÿ£ŸàŸÑÿßŸã
    const sortedBooks = [...books].sort((a, b) => b.isNew - a.isNew);

    // ‚úÖ ŸÅŸÑÿ™ÿ±ÿ© ÿ≠ÿ≥ÿ® ÿßŸÑÿ™ÿµŸÜŸäŸÅ ÿ£Ÿà ÿßŸÑŸÜŸàÿπ (ÿ®ŸÖÿ±ŸàŸÜÿ©)
    const filteredBooks = name
        ? sortedBooks.filter((book) => {
              const cat = book.category?.toLowerCase() || "";
              const type = book.type?.toLowerCase() || "";
              const n = name.toLowerCase();

              if (n === "ŸÖÿ™ÿ±ÿ¨ŸÖ") {
                  return ["ÿ£ÿ¨ŸÜÿ®Ÿä", "ÿπÿßŸÑŸÖŸä", "ŸÖÿ™ÿ±ÿ¨ŸÖ"].includes(type);
              }
              if (n === "ÿπÿ±ÿ®Ÿä") {
                  return type.includes("ÿπÿ±ÿ®Ÿä");
              }
              if (n === "ÿ∫Ÿäÿ± ÿ±Ÿàÿßÿ¶Ÿä") {
                  return !cat.includes("ÿ±ŸàÿßŸäÿ©");
              }
              return cat.includes(n);
          })
        : sortedBooks;

    return (
        <main className="px-4 md:px-12 py-8 text-right font-sans">
            <motion.h1
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
                className="text-3xl font-bold text-blue-800 mb-6"
            >
                {name ? `üìö ŸÅÿ¶ÿ©: ${name}` : "ŸÉŸÑ ÿßŸÑŸÉÿ™ÿ®"}
            </motion.h1>

            {filteredBooks.length > 0 ? (
                <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ duration: 0.6 }}
                    className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6"
                >
                    {filteredBooks.map((book) => (
                        <BookCard key={book.id} book={book} />
                    ))}
                </motion.div>
            ) : (
                <p className="text-gray-600 text-lg text-center py-10">
                    üì≠ ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÉÿ™ÿ® ŸÅŸä Ÿáÿ∞Ÿá ÿßŸÑŸÅÿ¶ÿ© ÿ≠ÿßŸÑŸäŸãÿß.
                </p>
            )}
        </main>
    );
}
