import { useState } from "react";
import { LazyLoadImage } from "react-lazy-load-image-component";
import { motion, AnimatePresence } from "framer-motion";
import "react-lazy-load-image-component/src/effects/blur.css";

export default function BookImage({ images = [], alt = "غلاف الكتاب" }) {
    const [index, setIndex] = useState(0);
    const [loaded, setLoaded] = useState(false);

    if (!images || images.length === 0) {
        return (
            <div className="relative bg-gradient-to-br from-gray-200 to-gray-100 rounded-2xl h-80 flex items-center justify-center text-gray-500 text-sm">
                لا توجد صورة
            </div>
        );
    }

    const currentImage = images[index];

    return (
        <div className="relative group overflow-hidden rounded-2xl bg-gradient-to-br from-blue-50 to-gray-50 border border-gray-200 shadow-md hover:shadow-xl transition-all duration-300">
            {/* تأثير التحميل */}
            {!loaded && (
                <div className="absolute inset-0 bg-gray-200 animate-pulse rounded-2xl" />
            )}

            <AnimatePresence mode="wait">
                <motion.div
                    key={index}
                    initial={{ opacity: 0, scale: 1.05 }}
                    animate={{ opacity: 1, scale: 1 }}
                    exit={{ opacity: 0, scale: 0.98 }}
                    transition={{ duration: 0.4 }}
                    className="relative"
                >
                    <LazyLoadImage
                        src={currentImage}
                        alt={alt}
                        effect="blur"
                        afterLoad={() => setLoaded(true)}
                        className="w-full h-80 object-contain group-hover:scale-105 transition-transform duration-500"
                    />
                </motion.div>
            </AnimatePresence>

            {/* تنقل الصور إن وُجد أكثر من واحدة */}
            {images.length > 1 && (
                <>
                    <button
                        onClick={() =>
                            setIndex((i) => (i - 1 + images.length) % images.length)
                        }
                        className="absolute left-3 top-1/2 -translate-y-1/2 bg-black/40 text-white p-2 rounded-full hover:bg-black/60 transition"
                    >
                        ◀
                    </button>
                    <button
                        onClick={() => setIndex((i) => (i + 1) % images.length)}
                        className="absolute right-3 top-1/2 -translate-y-1/2 bg-black/40 text-white p-2 rounded-full hover:bg-black/60 transition"
                    >
                        ▶
                    </button>

                    {/* النقاط أسفل الصورة */}
                    <div className="absolute bottom-3 left-1/2 -translate-x-1/2 flex gap-2">
                        {images.map((_, i) => (
                            <span
                                key={i}
                                className={`w-2.5 h-2.5 rounded-full transition-all ${i === index ? "bg-blue-600 scale-110" : "bg-gray-300"
                                    }`}
                            />
                        ))}
                    </div>
                </>
            )}
        </div>
    );
}
