import { Link } from "react-router-dom";

export default function BookCard({ book }) {
    return (
        <div className="bg-white rounded-2xl shadow-sm hover:shadow-lg transition duration-300 ease-in-out overflow-hidden text-right border border-gray-200">
            {/* Book Cover */}
            <img
                src={book.cover || "/placeholder.png"}
                alt={book.title}
                className="w-full h-72 object-cover rounded-t-2xl"
            />

            {/* Book Details */}
            <div className="p-4 space-y-2">
                <h3 className="text-lg md:text-xl font-semibold text-blue-800 leading-snug line-clamp-2">
                    {book.title}
                </h3>

                <p className="text-gray-500 text-sm md:text-base">
                    ✍️ المؤلف: {book.author}
                </p>

                <p className="text-green-600 font-bold">السعر: {book.price}</p>

                <Link
                    to={`/book/${book.id}`}
                    className="inline-block text-sm bg-blue-600 text-white px-4 py-2 mt-3 rounded-lg hover:bg-blue-700 transition"
                >
                    عرض التفاصيل
                </Link>
            </div>
        </div>
    );
}
