// src/pages/Category.jsx
import { useParams, Link } from "react-router-dom";
import { useCart } from "../context/CartContext";
import BookCard from "../components/BookCard";
import ScrollTopButton from "../components/ScrollTopButton";
import CategoryCard from "../components/CategoryCard";
import { motion } from "framer-motion";
import { useQuery } from "@tanstack/react-query";
import { useState, useMemo } from "react";

const categories = ["ÿ±ŸàÿßŸäÿ©", "ÿ∫Ÿäÿ± ÿ±Ÿàÿßÿ¶Ÿä", "ŸÖÿ™ÿ±ÿ¨ŸÖ", "ÿπÿ±ÿ®Ÿä"];

export default function Category() {
    const { name } = useParams();
    const { addToCart, removeFromCart, isInCart } = useCart();
    const [search, setSearch] = useState("");

    const { data: books = [], isLoading, error } = useQuery({
        queryKey: ["books"],
        queryFn: () => fetch("/books.json").then((res) => res.json()),
    });

    if (error)
        return (
            <p className="text-center text-red-600 py-10 text-lg">
                ‚ùå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
            </p>
        );

    // ‚úÖ ÿ™ÿ±ÿ™Ÿäÿ® ÿßŸÑŸÉÿ™ÿ®
    const sortedBooks = [...books].sort((a, b) => {
        if (a.status === "available" && b.status === "sold") return -1;
        if (a.status === "sold" && b.status === "available") return 1;
        return b.id - a.id;
    });

    // ‚úÖ ŸÅŸÑÿ™ÿ±ÿ© ÿ≠ÿ≥ÿ® ÿßŸÑÿ™ÿµŸÜŸäŸÅ + ÿßŸÑÿ®ÿ≠ÿ´
    const filteredBooks = useMemo(() => {
        let list = name
            ? sortedBooks.filter((book) => {
                  const cat = book.category?.toLowerCase() || "";
                  const type = book.type?.toLowerCase() || "";
                  const n = name.toLowerCase();

                  if (n === "ŸÖÿ™ÿ±ÿ¨ŸÖ") return ["ÿ£ÿ¨ŸÜÿ®Ÿä", "ÿπÿßŸÑŸÖŸä", "ŸÖÿ™ÿ±ÿ¨ŸÖ"].includes(type);
                  if (n === "ÿπÿ±ÿ®Ÿä") return type.includes("ÿπÿ±ÿ®Ÿä");
                  if (n === "ÿ∫Ÿäÿ± ÿ±Ÿàÿßÿ¶Ÿä") return !cat.includes("ÿ±ŸàÿßŸäÿ©");
                  return cat.includes(n);
              })
            : sortedBooks;

        if (search.trim() !== "") {
            list = list.filter((book) =>
                book.title?.toLowerCase().includes(search.toLowerCase())
            );
        }

        return list;
    }, [sortedBooks, name, search]);

    return (
        <div className="max-w-7xl mx-auto px-4 py-10 text-right font-sans space-y-12">
            {/* üè∑Ô∏è ÿßŸÑÿπŸÜŸàÿßŸÜ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä */}
            <motion.h1
                initial={{ opacity: 0, y: -20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6 }}
                className="text-4xl font-extrabold text-blue-800 text-center mb-4"
            >
                üõçÔ∏è ÿ™ÿµŸÅŸëÿ≠ ŸÖÿ¨ŸÖŸàÿπÿ™ŸÜÿß ÿßŸÑŸÖŸÖŸäÿ≤ÿ© ŸÖŸÜ ÿßŸÑŸÉÿ™ÿ®
            </motion.h1>

            {/* üîç ÿßŸÑÿ®ÿ≠ÿ´ + ÿßŸÑÿ™ÿµŸÜŸäŸÅÿßÿ™ (ÿ¥ÿ±Ÿäÿ∑ ÿ´ÿßÿ®ÿ™) */}
            <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.3 }}
                className="sticky top-20 z-40 bg-white/80 backdrop-blur-md border border-blue-100 shadow-sm rounded-xl px-4 py-4 flex flex-wrap items-center justify-between gap-3"
            >
                {/* üîç ŸÖÿ±ÿ®ÿπ ÿßŸÑÿ®ÿ≠ÿ´ */}
                <div className="flex items-center gap-2 w-full md:w-1/3">
                    <input
                        type="text"
                        placeholder="üîç ÿßÿ®ÿ≠ÿ´ ÿπŸÜ ŸÉÿ™ÿßÿ®..."
                        value={search}
                        onChange={(e) => setSearch(e.target.value)}
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 placeholder-gray-400 text-gray-700"
                    />
                </div>

                {/* üß≠ ÿßŸÑÿ™ÿµŸÜŸäŸÅÿßÿ™ */}
                <div className="flex flex-wrap justify-center md:justify-end gap-2 w-full md:w-2/3">
                    <Link to="/category">
                        <CategoryCard title="ÿßŸÑŸÉŸÑ" />
                    </Link>
                    {categories.map((category) => (
                        <Link key={category} to={`/category/${category}`}>
                            <CategoryCard title={category} />
                        </Link>
                    ))}
                </div>
            </motion.div>

            {/* üìñ ÿßŸÑÿπŸÜŸàÿßŸÜ ÿßŸÑŸÅÿ±ÿπŸä */}
            <motion.h2
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 0.6, delay: 0.2 }}
                className="text-2xl font-semibold text-blue-700 border-r-4 border-blue-600 pr-3"
            >
                {name ? `üìö ÿ™ÿµŸÜŸäŸÅ: ${name}` : "üìö ÿ¨ŸÖŸäÿπ ÿßŸÑŸÉÿ™ÿ®"}
            </motion.h2>

            {/* üí´ ŸÖÿ≠ÿ™ŸàŸâ ÿßŸÑÿµŸÅÿ≠ÿ© */}
            {isLoading ? (
                <div className="grid gap-8 sm:grid-cols-2 lg:grid-cols-3">
                    {[...Array(9)].map((_, i) => (
                        <div
                            key={i}
                            className="h-80 bg-gradient-to-br from-gray-200 to-gray-100 animate-pulse rounded-2xl shadow-sm"
                        ></div>
                    ))}
                </div>
            ) : filteredBooks.length === 0 ? (
                <motion.p
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    className="text-gray-600 text-lg text-center py-10"
                >
                    üì≠ ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÉÿ™ÿ® ŸÖÿ∑ÿßÿ®ŸÇÿ© ŸÑÿ®ÿ≠ÿ´ŸÉ ÿ≠ÿßŸÑŸäÿßŸã.
                </motion.p>
            ) : (
                <motion.div
                    initial="hidden"
                    animate="visible"
                    variants={{
                        hidden: { opacity: 0 },
                        visible: {
                            opacity: 1,
                            transition: { staggerChildren: 0.08 },
                        },
                    }}
                    className="grid gap-8 sm:grid-cols-2 lg:grid-cols-3"
                >
                    {filteredBooks.map((book) => (
                        <motion.div
                            key={book.id}
                            variants={{
                                hidden: { opacity: 0, y: 40 },
                                visible: { opacity: 1, y: 0 },
                            }}
                            whileHover={{ scale: 1.02 }}
                            transition={{ type: "spring", stiffness: 150 }}
                            className="relative"
                        >
                            <BookCard book={book} />

                            {book.status !== "sold" && (
                                <motion.button
                                    whileTap={{ scale: 0.95 }}
                                    onClick={() =>
                                        isInCart(book.id)
                                            ? removeFromCart(book.id)
                                            : addToCart(book)
                                    }
                                    className={`mt-3 w-full text-sm px-4 py-2 rounded-xl font-medium shadow transition-all duration-200 ${
                                        isInCart(book.id)
                                            ? "bg-red-50 text-red-600 border border-red-200 hover:bg-red-100"
                                            : "bg-green-50 text-green-700 border border-green-200 hover:bg-green-100"
                                    }`}
                                >
                                    {isInCart(book.id)
                                        ? "üóëÔ∏è ÿ•ÿ≤ÿßŸÑÿ© ŸÖŸÜ ÿßŸÑÿ≥ŸÑÿ©"
                                        : "üõí ÿ£ÿ∂ŸÅ ÿ•ŸÑŸâ ÿßŸÑÿ≥ŸÑÿ©"}
                                </motion.button>
                            )}
                        </motion.div>
                    ))}
                </motion.div>
            )}

            <ScrollTopButton />
        </div>
    );
}
