// src/pages/Cart.jsx
import { useCart } from "../context/CartContext";
import { motion } from "framer-motion";

export default function Cart() {
    const { cart, removeFromCart, updateQuantity, clearCart } = useCart();

    const totalPrice = cart.reduce((sum, book) => {
        const price = parseFloat(book.price) || 0;
        return sum + price * book.quantity;
    }, 0);

    // ‚úÖ ÿ™ŸÜÿ≥ŸäŸÇ ÿ±ÿ≥ÿßŸÑÿ© Ÿàÿßÿ™ÿ≥ÿßÿ® ŸÖŸÜÿ∏ŸÖÿ© ŸàŸàÿßÿ∂ÿ≠ÿ©
    const whatsappMessage = [
        "üìö ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑŸÉÿ™ÿ® ÿßŸÑŸÖÿ∑ŸÑŸàÿ®ÿ©:",
        "------------------------------",
        ...cart.map(
            (book) =>
                `#${book.id} - ${book.title}\n` +
                `‚úçÔ∏è ÿßŸÑŸÖÿ§ŸÑŸÅ: ${book.author || "ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ"}\n` +
                `üíµ ÿßŸÑÿ≥ÿπÿ±: ${book.price}\n` +
                `üì¶ ÿßŸÑŸÉŸÖŸäÿ©: ${book.quantity}\n------------------------------`
        ),
        `üí∞ ÿßŸÑÿ•ÿ¨ŸÖÿßŸÑŸä: ${totalPrice.toFixed(2)} ÿ¨.ŸÖ`,
    ].join("\n");

    const whatsappUrl = `https://wa.me/2001034345458?text=${encodeURIComponent(
        whatsappMessage
    )}`;

    return (
        <main className="max-w-7xl mx-auto px-4 py-10 text-right font-sans">
            <motion.h1
                initial={{ opacity: 0, y: -20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6 }}
                className="text-3xl font-bold text-blue-800 mb-8 text-center"
            >
                üõí ÿ≥ŸÑÿ© ÿßŸÑÿ™ÿ≥ŸàŸÇ
            </motion.h1>

            {cart.length === 0 ? (
                <p className="text-gray-600 text-lg text-center py-10">
                    üì≠ ÿßŸÑÿ≥ŸÑÿ© ŸÅÿßÿ±ÿ∫ÿ© ÿ≠ÿßŸÑŸäÿßŸã.
                </p>
            ) : (
                <div className="space-y-8">
                    {/* ÿ¥ÿ®ŸÉÿ© ÿßŸÑŸÉÿ™ÿ® */}
                    <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
                        {cart.map((book) => {
                            const singleBookMessage = 
                                `üìñ ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑŸÉÿ™ÿßÿ® ÿßŸÑŸÖÿ∑ŸÑŸàÿ®:\n` +
                                `------------------------------\n` +
                                `#${book.id} - ${book.title}\n` +
                                `‚úçÔ∏è ÿßŸÑŸÖÿ§ŸÑŸÅ: ${book.author || "ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ"}\n` +
                                `üíµ ÿßŸÑÿ≥ÿπÿ±: ${book.price}\n` +
                                `üì¶ ÿßŸÑŸÉŸÖŸäÿ©: ${book.quantity}\n` +
                                `------------------------------`;

                            const singleBookUrl = `https://wa.me/2001034345458?text=${encodeURIComponent(
                                singleBookMessage
                            )}`;

                            return (
                                <motion.div
                                    key={book.id}
                                    initial={{ opacity: 0, y: 20 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    transition={{ duration: 0.4 }}
                                    className="bg-white rounded-xl shadow-md hover:shadow-lg overflow-hidden flex flex-col"
                                >
                                    <img
                                        src={book.images?.[0] || "/placeholder.png"}
                                        alt={book.title}
                                        className="w-full h-56 object-cover"
                                    />
                                    <div className="flex-1 p-4 flex flex-col justify-between space-y-3">
                                        <div>
                                            <h2 className="text-lg font-semibold text-blue-800 mb-1">
                                                {book.title}
                                            </h2>
                                            <p className="text-gray-600 text-sm">
                                                ‚úçÔ∏è {book.author}
                                            </p>
                                            <p className="text-green-700 font-semibold mt-1">
                                                üíµ {book.price}
                                            </p>
                                            <p className="text-xs text-gray-500">
                                                üÜî ÿ±ŸÇŸÖ ÿßŸÑŸÉÿ™ÿßÿ®: {book.id}
                                            </p>
                                        </div>

                                        {/* ÿßŸÑÿ™ÿ≠ŸÉŸÖ ŸÅŸä ÿßŸÑŸÉŸÖŸäÿ© */}
                                        <div className="flex items-center gap-2">
                                            <label className="text-gray-700 text-sm">ÿßŸÑŸÉŸÖŸäÿ©:</label>
                                            <input
                                                type="number"
                                                min="1"
                                                value={book.quantity}
                                                onChange={(e) =>
                                                    updateQuantity(
                                                        book.id,
                                                        parseInt(e.target.value)
                                                    )
                                                }
                                                className="w-16 border rounded p-1 text-center"
                                            />
                                        </div>

                                        {/* ÿßŸÑÿ£ÿ≤ÿ±ÿßÿ± */}
                                        <div className="flex justify-between items-center gap-2 pt-2">
                                            <a
                                                href={singleBookUrl}
                                                target="_blank"
                                                rel="noopener noreferrer"
                                                className="flex-1 text-sm bg-green-500 hover:bg-green-600 text-white px-3 py-2 rounded shadow text-center"
                                            >
                                                üì≤ ÿ≠ÿ¨ÿ≤ ÿßŸÑŸÉÿ™ÿßÿ®
                                            </a>
                                            <button
                                                onClick={() => removeFromCart(book.id)}
                                                className="text-sm bg-red-100 hover:bg-red-200 text-red-600 px-3 py-2 rounded shadow"
                                            >
                                                üóëÔ∏è ÿ•ÿ≤ÿßŸÑÿ©
                                            </button>
                                        </div>
                                    </div>
                                </motion.div>
                            );
                        })}
                    </div>

                    {/* ‚úÖ ŸÖŸÑÿÆÿµ ÿßŸÑÿ≥ŸÑÿ© */}
                    <div className="bg-gray-50 border-t-4 border-blue-600 rounded-xl shadow p-6 flex flex-col md:flex-row justify-between items-center gap-4">
                        <p className="text-2xl font-bold text-blue-800">
                            üí∞ ÿßŸÑÿ•ÿ¨ŸÖÿßŸÑŸä: {totalPrice.toFixed(2)} ÿ¨.ŸÖ
                        </p>
                        <div className="flex gap-4">
                            <button
                                onClick={clearCart}
                                className="bg-red-500 hover:bg-red-600 text-white px-5 py-2 rounded-lg shadow"
                            >
                                üßπ ÿ•ŸÅÿ±ÿßÿ∫ ÿßŸÑÿ≥ŸÑÿ©
                            </button>
                            <a
                                href={whatsappUrl}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="bg-green-600 hover:bg-green-700 text-white px-6 py-2 rounded-lg shadow"
                            >
                                üì¶ ÿ≠ÿ¨ÿ≤ ÿßŸÑŸÖÿ¨ŸÖŸàÿπÿ© ŸÉÿßŸÖŸÑÿ©
                            </a>
                        </div>
                    </div>
                </div>
            )}
        </main>
    );
}
