        {
            label: "Ÿàÿßÿ™ÿ≥ÿßÿ®",
            value: "01034345458",
            link: "https://wa.me/2001034345458",
            icon: "üìû",
            color: "text-green-600",
        },
        {
            label: "ŸÅŸäÿ≥ÿ®ŸàŸÉ",
            value: "harafesh.books",
            link: "https://www.facebook.com/share/1ACxYSibvC/",
            icon: "üìò",
            color: "text-blue-600",
        },
    ];

    return (
        <motion.footer
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="bg-gray-100 text-right p-4 mt-10 text-sm text-gray-600 border-t"
        >
            <div className="max-w-5xl mx-auto space-y-3">
                {/* ÿßŸÑÿ≠ŸÇŸàŸÇ */}
                <p>
                    &copy; {new Date().getFullYear()} ÿ¨ŸÖŸäÿπ ÿßŸÑÿ≠ŸÇŸàŸÇ ŸÖÿ≠ŸÅŸàÿ∏ÿ© ŸÑŸÖŸÉÿ™ÿ®ÿ©
                    ÿßŸÑÿ≠ÿ±ÿßŸÅŸäÿ¥ ŸÑŸÑŸÉÿ™ÿ®
                </p>

                {/* Ÿàÿ≥ÿßÿ¶ŸÑ ÿßŸÑÿ™ŸàÿßÿµŸÑ */}
                <div className="flex flex-col sm:flex-row sm:justify-between sm:items-center gap-3 sm:gap-6">
                    {contacts.map((item, idx) => (
                        <p key={idx} className="flex items-center gap-1">
                            <span>{item.icon}</span>
                            <a
                                href={item.link}
                                target="_blank"
                                rel="noopener noreferrer"
                                className={`${item.color} hover:underline`}
                            >
                                {item.value}
                            </a>
                        </p>
                    ))}
                </div>
            </div>
        </motion.footer>
    );
}
