// src/App.jsx
import { Routes, Route } from "react-router-dom";

// Components
import Navbar from "./components/Navbar.jsx";
import Footer from "./components/Footer.jsx";
import SearchBar from "./components/SearchBar.jsx";
import ScrollToTopButton from "./components/ScrollTopButton.jsx";

// Pages – عامة
import Home from "./pages/Home.jsx";
import Category from "./pages/Category.jsx";
import BookDetails from "./pages/BookDetails.jsx";
import Cart from "./pages/Cart.jsx";
import Contact from "./pages/Contact.jsx";
import SearchResults from "./pages/SearchResults.jsx";
import NotFound from "./pages/NotFound.jsx";

// Pages – إدارة
import AdminLogin from "./pages/admin/AdminLogin.jsx";
import ManageBooks from "./pages/admin/ManageBooks.jsx";
import ProtectedRoute from "./pages/admin/ProtectedRoute.jsx";

export default function App() {
    return (
        <div
            className="font-sans bg-gray-50 min-h-screen flex flex-col"
            dir="rtl"
        >
            <Routes>
                {/* ━━━━━━━━━━━ صفحات الإدارة ━━━━━━━━━━━ */}
                <Route path="/admin/login" element={<AdminLogin />} />
                <Route
                    path="/admin/manage-books"
                    element={
                        <ProtectedRoute>
                            <ManageBooks />
                        </ProtectedRoute>
                    }
                />

                {/* ━━━━━━━━━━━ الصفحات العامة ━━━━━━━━━━━ */}
                <Route
                    path="*"
                    element={
                        <>
                            <Navbar />
                            <div className="bg-white shadow-sm border-b border-gray-200 py-4 px-6 sticky top-0 z-40">
                                <SearchBar />
                            </div>

                            <main className="flex-1 pb-10">
                                <Routes>
                                    <Route path="/" element={<Home />} />
                                    <Route
                                        path="/book/:id"
                                        element={<BookDetails />}
                                    />
                                    <Route
                                        path="/category"
                                        element={<Category />}
                                    />
                                    <Route
                                        path="/category/:name"
                                        element={<Category />}
                                    />
                                    <Route path="/cart" element={<Cart />} />
                                    <Route
                                        path="/contact"
                                        element={<Contact />}
                                    />
                                    <Route
                                        path="/search"
                                        element={<SearchResults />}
                                    />
                                    <Route path="*" element={<NotFound />} />
                                </Routes>
                            </main>

                            <Footer />
                            <ScrollToTopButton />
                        </>
                    }
                />
            </Routes>
        </div>
    );
}
