// src/pages/BookDetails.jsx
import { useParams, Link } from "react-router-dom";
import { motion } from "framer-motion";
import { useCart } from "../context/CartContext";
import BookImage from "../components/BookImage";
import { useQuery } from "@tanstack/react-query";

export default function BookDetails() {
    const { id } = useParams();
    const { addToCart, removeFromCart, isInCart } = useCart();

    // ‚úÖ ÿ¨ŸÑÿ® ÿßŸÑŸÉÿ™ÿ® ŸÖŸÜ API ÿ£Ÿà JSON
    const { data: books = [], isLoading, error } = useQuery({
        queryKey: ["books"],
        queryFn: () => fetch("/books.json").then((res) => res.json()),
    });

    if (isLoading)
        return <p className="text-center text-gray-600 py-10">‚è≥ ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ...</p>;

    if (error)
        return <p className="text-center text-red-600 py-10">‚ùå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™</p>;

    const book = books.find((b) => String(b.id) === id);

    if (!book) {
        return (
            <div className="max-w-4xl mx-auto p-6 text-center">
                <h2 className="text-xl text-gray-600">‚ùå ÿßŸÑŸÉÿ™ÿßÿ® ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ</h2>
                <Link
                    to="/category"
                    className="mt-4 inline-block px-4 py-2 bg-blue-700 text-white rounded-lg hover:bg-blue-800"
                >
                    ÿßŸÑÿπŸàÿØÿ© ŸÑŸÑÿ™ÿ≥ŸàŸÇ
                </Link>
            </div>
        );
    }

    // ‚úÖ ÿßŸÑÿ≠ÿßŸÑÿßÿ™
    const isSold = book.status === "sold";
    const notAvailable = isSold;

    // ‚úÖ ÿ±ÿßÿ®ÿ∑ Ÿàÿßÿ™ÿ≥ÿßÿ® ŸÑŸÑÿ≠ÿ¨ÿ≤
    const whatsappUrl = `https://wa.me/201034345458?text=ŸÖÿ±ÿ≠ÿ®Ÿãÿßÿå ÿ£ŸàÿØ ÿ≠ÿ¨ÿ≤ ÿßŸÑŸÉÿ™ÿßÿ®:\nüìñ ${book.title}\nüíµ ${book.price}`;

    return (
        <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.6 }}
            className="max-w-5xl mx-auto px-6 py-10 grid md:grid-cols-2 gap-8 bg-white rounded-xl shadow-lg"
        >
            {/* ÿµŸàÿ±ÿ© ÿßŸÑŸÉÿ™ÿßÿ® */}
            <div>
                <BookImage
                    images={book.images || []}
                    alt={book.title}
                    ratio="h-96"
                    fit="contain"
                    className="rounded-lg"
                />
            </div>

            {/* ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑŸÉÿ™ÿßÿ® */}
            <div className="space-y-5 text-right">
                <h1 className="text-2xl md:text-3xl font-bold text-gray-800">
                    {book.title}
                </h1>

                {book.author && (
                    <p className="text-gray-600 text-lg">
                        ‚úçÔ∏è <span className="font-medium">ÿßŸÑŸÖÿ§ŸÑŸÅ:</span> {book.author}
                    </p>
                )}

                {book.price && (
                    <p className="text-green-700 font-semibold text-lg">
                        üíµ ÿßŸÑÿ≥ÿπÿ±: {book.price}
                    </p>
                )}

                {book.HPaper && (
                    <p className="text-gray-600">üìÑ ÿπÿØÿØ ÿßŸÑÿµŸÅÿ≠ÿßÿ™: {book.HPaper}</p>
                )}

                {book.description && (
                    <p className="text-gray-700 leading-relaxed">{book.description}</p>
                )}

                {/* ÿ¥ÿßÿ±ÿ© ÿßŸÑÿ≠ÿßŸÑÿ© */}
                <p
                    className={`inline-block px-3 py-1 rounded-full text-sm font-medium shadow ${isSold ? "bg-gray-700 text-white" : "bg-green-600 text-white"
                        }`}
                >
                    {isSold ? "‚ùå ÿ™ŸÖ ÿßŸÑÿ®Ÿäÿπ" : "‚úÖ ŸÖÿ™ŸàŸÅÿ±"}
                </p>

                {/* ÿ£ÿ≤ÿ±ÿßÿ± ÿßŸÑÿ™ŸÅÿßÿπŸÑ */}
                <div className="pt-4 flex flex-col gap-3">
                    {notAvailable ? (
                        <p className="text-red-600 font-semibold">
                            ‚ö†Ô∏è Ÿáÿ∞ÿß ÿßŸÑŸÉÿ™ÿßÿ® ÿ∫Ÿäÿ± ŸÖÿ™ÿßÿ≠ ÿ≠ÿßŸÑŸäÿßŸã
                        </p>
                    ) : (
                        <>
                            <button
                                onClick={() =>
                                    isInCart(book.id) ? removeFromCart(book.id) : addToCart(book)
                                }
                                className={`px-6 py-2 rounded-lg font-medium transition-all duration-200 ${isInCart(book.id)
                                        ? "bg-red-100 text-red-600 hover:bg-red-200"
                                        : "bg-blue-700 text-white hover:bg-blue-800"
                                    }`}
                            >
                                {isInCart(book.id) ? "üóëÔ∏è ÿ•ÿ≤ÿßŸÑÿ© ŸÖŸÜ ÿßŸÑÿ≥ŸÑÿ©" : "‚ûï ÿ£ÿ∂ŸÅ ÿ•ŸÑŸâ ÿßŸÑÿ≥ŸÑÿ©"}
                            </button>

                            {/* ‚úÖ ÿ≤ÿ± ÿßŸÑÿ≠ÿ¨ÿ≤ ÿπÿ®ÿ± Ÿàÿßÿ™ÿ≥ÿßÿ® */}
                            <a
                                href={whatsappUrl}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="px-6 py-2 rounded-lg bg-green-600 hover:bg-green-700 text-white font-medium shadow transition"
                            >
                                üì≤ ÿ≠ÿ¨ÿ≤ ÿπÿ®ÿ± Ÿàÿßÿ™ÿ≥ÿßÿ®
                            </a>
                        </>
                    )}
                </div>
            </div>
        </motion.div>
    );
}
