export default function BookCard({ book }) {
  return (
    <div className="border rounded-lg p-4 shadow hover:shadow-lg transition text-right">
      <img src={book.image} alt={book.title} className="w-full h-48 object-cover mb-2 rounded" />
      <h3 className="font-semibold text-lg">{book.title}</h3>
      <p className="text-gray-600">بواسطة: {book.author}</p>
    </div>
  );
}
