import { useParams, Link } from "react-router-dom";
import books from "../data/books.json";

const categories = ["ÿ±Ÿàÿßÿ¶Ÿä", "ŸÅŸÑÿ≥ŸÅÿ©", "ÿπŸÑŸÖ ŸÜŸÅÿ≥", "ÿ™ÿßÿ±ŸäÿÆ", "ŸÖÿ™ÿ±ÿ¨ŸÖ", "ÿπÿ±ÿ®Ÿä"];

export default function Category() {
  const { name } = useParams();

  // ‚úÖ ÿ™ÿ±ÿ™Ÿäÿ® ÿßŸÑŸÉÿ™ÿ® ŸÖŸÜ ÿßŸÑÿ£ÿ≠ÿØÿ´ ÿ•ŸÑŸâ ÿßŸÑÿ£ŸÇÿØŸÖ ÿ≠ÿ≥ÿ® id
  const sortedBooks = [...books].sort((a, b) => b.id - a.id);

  // ‚úÖ ŸÅŸÑÿ™ÿ±ÿ© ÿ≠ÿ≥ÿ® ÿßŸÑÿ™ÿµŸÜŸäŸÅ ŸÅŸÇÿ∑ ÿ•ÿ∞ÿß ŸÉÿßŸÜ ŸÖŸàÿ¨ŸàÿØ
  const filteredBooks = name
    ? sortedBooks.filter((book) => {
        if (name === "ŸÖÿ™ÿ±ÿ¨ŸÖ" || name === "ÿπÿ±ÿ®Ÿä") {
          return book.type?.toLowerCase() === name.toLowerCase();
        }
        return book.category?.toLowerCase() === name.toLowerCase();
      })
    : sortedBooks; // ÿ®ÿØŸàŸÜ ŸÅŸÑÿ™ÿ±ÿå ÿßÿπÿ±ÿ∂ ŸÉŸÑ ÿßŸÑŸÉÿ™ÿ®

  return (
    <div className="max-w-6xl mx-auto px-4 py-10 text-right space-y-6">
      <h1 className="text-3xl font-bold text-blue-800">üìö ÿßŸÑÿ™ÿµŸÜŸäŸÅÿßÿ™</h1>

      {/* ‚úÖ ÿ£ÿ≤ÿ±ÿßÿ± ÿßŸÑÿ™ÿµŸÜŸäŸÅÿßÿ™ */}
      <div className="flex flex-wrap gap-3 justify-end">
        <Link
          to="/category"
          className={`px-4 py-2 rounded border ${
            !name
              ? "bg-blue-600 text-white"
              : "bg-white text-blue-600 hover:bg-blue-50"
          }`}
        >
          ÿßŸÑŸÉŸÑ
        </Link>

        {categories.map((category) => (
          <Link
            key={category}
            to={`/category/${category}`}
            className={`px-4 py-2 rounded border ${
              name === category
                ? "bg-blue-600 text-white"
                : "bg-white text-blue-600 hover:bg-blue-50"
            }`}
          >
            {category}
          </Link>
        ))}
      </div>

      {/* ‚úÖ ÿßŸÑÿπŸÜŸàÿßŸÜ ÿßŸÑŸÅÿ±ÿπŸä ÿ≠ÿ≥ÿ® ÿßŸÑŸÅŸÑÿ™ÿ± */}
      <h2 className="text-2xl font-semibold text-blue-700">
        üìñ {name ? `ÿ™ÿµŸÜŸäŸÅ: ${name}` : "ÿ¨ŸÖŸäÿπ ÿßŸÑŸÉÿ™ÿ®"}
      </h2>

      {/* ‚úÖ ÿπÿ±ÿ∂ ÿßŸÑŸÉÿ™ÿ® */}
      {filteredBooks.length === 0 ? (
        <p className="text-gray-600">ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÉÿ™ÿ® ŸÅŸä Ÿáÿ∞ÿß ÿßŸÑÿ™ÿµŸÜŸäŸÅ ÿ≠ÿßŸÑŸäÿßŸã.</p>
      ) : (
        <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
          {filteredBooks.map((book) => (
            <div
              key={book.id}
              className="bg-white rounded-lg shadow hover:shadow-md transition overflow-hidden"
            >
              <img
                src={book.cover || "/placeholder.png"}
                alt={book.title}
                className="w-full h-56 object-cover"
              />
              <div className="p-4 space-y-2">
                <h3 className="text-xl font-bold text-blue-800">
                  {book.title}
                </h3>
                <p className="text-gray-600">‚úçÔ∏è {book.author}</p>
                <p className="text-sm text-gray-700">üí∞ {book.price}</p>
                <Link
                  to={`/book/${book.id}`}
                  className="inline-block mt-2 text-sm text-white bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded"
                >
                  ÿπÿ±ÿ∂ ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ
                </Link>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
