import { Link } from "react-router-dom";
import { useFavorites } from "../context/FavoriteContext";

export default function Favorites() {
    const { favorites, removeFavorite } = useFavorites();

    return (
        <div className="max-w-6xl mx-auto px-4 py-10 text-right">
            <h1 className="text-3xl font-bold text-red-700 mb-6">‚ù§Ô∏è ÿßŸÑŸÉÿ™ÿ® ÿßŸÑŸÖŸÅÿ∂ŸÑÿ©</h1>

            {favorites.length === 0 ? (
                <p className="text-gray-600 text-lg">ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÉÿ™ÿ® ŸÅŸä ÿßŸÑŸÖŸÅÿ∂ŸÑÿ© ÿ≠ÿßŸÑŸäÿßŸã.</p>
            ) : (
                <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
                    {favorites.map((book) => (
                        <div
                            key={book.id}
                            className="bg-white rounded-lg shadow hover:shadow-md transition overflow-hidden flex flex-col"
                        >
                            <img
                                src={book.cover || "/placeholder.png"}
                                alt={book.title}
                                className="w-full h-56 object-cover"
                            />
                            <div className="p-4 space-y-2 flex-grow flex flex-col justify-between">
                                <div>
                                    <h3 className="text-xl font-bold text-blue-800">{book.title}</h3>
                                    <p className="text-gray-600">‚úçÔ∏è {book.author}</p>
                                    <p className="text-sm text-gray-700">üí∞ {book.price}</p>
                                    {book.description && (
                                        <p className="text-sm text-gray-600 mt-2 line-clamp-3">
                                            üìñ {book.description}
                                        </p>
                                    )}
                                </div>

                                <div className="mt-4 space-y-2">
                                    <button
                                        onClick={() => removeFavorite(book.id)}
                                        className="w-full text-sm bg-red-100 text-red-600 hover:bg-red-200 px-3 py-1 rounded"
                                    >
                                        üóëÔ∏è ÿ•ÿ≤ÿßŸÑÿ© ŸÖŸÜ ÿßŸÑŸÖŸÅÿ∂ŸÑÿ©
                                    </button>

                                    <Link
                                        to={book.facebookPost}
                                        target="_blank"
                                        rel="noopener noreferrer"
                                        className="block text-center text-sm text-white bg-green-600 hover:bg-green-700 px-4 py-2 rounded"
                                    >
                                        üõí ÿ≠ÿ¨ÿ≤ ÿßŸÑŸÉÿ™ÿßÿ®
                                    </Link>

                                    {/* ÿ≤ÿ± ÿßŸÑÿ≠ÿ¨ÿ≤ */}
                                    <button
                                        onClick={() => alert(`ÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ∑ŸÑÿ® ÿ≠ÿ¨ÿ≤ "${book.title}" ‚úÖ`)}
                                        className="w-full text-sm bg-green-600 text-white hover:bg-green-700 px-3 py-2 rounded"
                                    >
                                        üõí ÿ≠ÿ¨ÿ≤ ÿßŸÑŸÉÿ™ÿßÿ®
                                    </button>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}
