export default function BookDetails() {
  return (
    <div className="text-right">
      <h1 className="text-3xl font-bold mb-4">عنوان الكتاب</h1>
      <p>تفاصيل الكتاب، المؤلف، المترجم، دار النشر، السعر، الملخص، …</p>
      <a
        href="#"
        className="inline-block mt-4 px-4 py-2 bg-blue-600 text-white rounded"
      >
        احجز عبر فيسبوك
      </a>
    </div>
  );
}
