import { Link } from "react-router-dom";

export default function BookCard({ book }) {
    return (
        <div className="bg-white rounded-lg shadow hover:shadow-md transition overflow-hidden text-right">
            <img
                src={book.image || "/placeholder.png"}
                alt={book.title}
                className="w-full h-56 object-cover"
            />
            <div className="p-4 space-y-2">
                <h3 className="text-xl font-bold text-blue-800">{book.title}</h3>
                <p className="text-gray-600">✍️ {book.author}</p>

                <Link
                    to={`/book/${book.id}`}
                    className="inline-block mt-2 text-sm text-white bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded"
                >
                    عرض التفاصيل
                </Link>
            </div>
        </div>
    );
}
