import { Link } from "react-router-dom";
import { motion } from "framer-motion";
import BookImage from "./BookImage";
import { useCart } from "../context/CartContext";
import { FiShoppingCart, FiTrash2 } from "react-icons/fi";

export default function BookCard({ book }) {
    const { addToCart, removeFromCart, isInCart } = useCart();

    if (!book) return null;
    const isSold = book.status === "sold";
    const inCart = isInCart(book.id);

    return (
        <motion.div
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.4 }}
            whileHover={{ y: -6, scale: 1.02 }}
            className="group bg-white rounded-2xl shadow-md hover:shadow-2xl transition-all duration-500 overflow-hidden text-right border border-gray-100 relative"
        >
            {/* üñºÔ∏è ÿµŸàÿ±ÿ© ÿßŸÑŸÉÿ™ÿßÿ® */}
            <div className="relative overflow-hidden">
                <BookImage
                    images={book.images || []}
                    alt={book.title}
                    ratio="h-72"
                    fit="cover"
                    className="rounded-t-2xl group-hover:scale-105 transition-transform duration-700"
                />

                {/* üè∑Ô∏è ÿ¥ÿßÿ±ÿ© ÿ¨ÿØŸäÿØ */}
                {book.isNew && (
                    <span className="absolute top-3 right-3 text-xs bg-red-600 text-white px-3 py-1 rounded-full shadow-md">
                        ÿ¨ÿØŸäÿØ
                    </span>
                )}

                {/* üîò ÿ¥ÿßÿ±ÿ© ÿßŸÑÿ≠ÿßŸÑÿ© */}
                <span
                    className={`absolute top-3 left-3 text-xs font-medium px-3 py-1 rounded-full shadow-md ${
                        isSold
                            ? "bg-gray-600 text-white"
                            : "bg-green-600 text-white"
                    }`}
                >
                    {isSold ? "‚ùå ÿ™ŸÖ ÿßŸÑÿ≠ÿ¨ÿ≤" : "‚úÖ ŸÖÿ™ŸàŸÅÿ±"}
                </span>

                {/* ÿ∑ÿ®ŸÇÿ© ÿ∏ŸÑ ÿ£ŸÜŸäŸÇÿ© ÿπŸÜÿØ ÿßŸÑŸÖÿ±Ÿàÿ± */}
                <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-500" />
            </div>

            {/* üìò ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑŸÉÿ™ÿßÿ® */}
            <div className="p-5 space-y-3">
                <h3 className="text-lg md:text-xl font-bold text-gray-800 leading-snug line-clamp-2 group-hover:text-blue-700 transition-colors duration-300">
                    {book.title}
                </h3>

                {book.author && (
                    <p className="text-gray-500 text-sm line-clamp-1">
                        ‚úçÔ∏è {book.author}
                    </p>
                )}

                {book.price && (
                    <p className="text-green-700 text-base font-semibold">
                        üíµ {book.price}
                    </p>
                )}

                {/* üìÑ ÿ≤ÿ± ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ */}
                <Link
                    to={`/book/${book.id}`}
                    className={`block w-full text-center text-sm md:text-base px-4 py-2.5 rounded-lg font-medium transition-all duration-300 ${
                        isSold
                            ? "bg-gray-400 text-white cursor-not-allowed opacity-80"
                            : "bg-blue-700 hover:bg-blue-800 text-white shadow-sm hover:shadow-md"
                    }`}
                >
                    {isSold ? "ÿ∫Ÿäÿ± ŸÖÿ™ÿßÿ≠ ÿßŸÑÿ¢ŸÜ" : "ÿπÿ±ÿ∂ ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ"}
                </Link>

                {/* üõí ÿ≤ÿ± ÿßŸÑÿ≥ŸÑÿ© (Ÿäÿ∏Ÿáÿ± ŸÅŸÇÿ∑ ŸÑŸà ŸÖÿ™ÿßÿ≠) */}
                {!isSold && (
                    <button
                        onClick={() =>
                            inCart ? removeFromCart(book.id) : addToCart(book)
                        }
                        className={`mt-2 w-full flex items-center justify-center gap-2 text-sm md:text-base px-4 py-2.5 rounded-lg font-medium transition-all duration-300 ${
                            inCart
                                ? "bg-red-100 text-red-600 hover:bg-red-200"
                                : "bg-green-100 text-green-700 hover:bg-green-200"
                        }`}
                    >
                        {inCart ? (
                            <>
                                <FiTrash2 /> ÿ•ÿ≤ÿßŸÑÿ© ŸÖŸÜ ÿßŸÑÿ≥ŸÑÿ©
                            </>
                        ) : (
                            <>
                                <FiShoppingCart /> ÿ£ÿ∂ŸÅ ÿ•ŸÑŸâ ÿßŸÑÿ≥ŸÑÿ©
                            </>
                        )}
                    </button>
                )}
            </div>
        </motion.div>
    );
}
