import { Link } from "react-router-dom";
import { motion } from "framer-motion";
import BookImage from "./BookImage";
import { useCart } from "../context/CartContext";

export default function BookCard({ book }) {
    // ‚úÖ ŸÑÿßÿ≤ŸÖ ŸáŸÜÿß ŸÅŸä ÿßŸÑÿ®ÿØÿßŸäÿ©
    const { addToCart, removeFromCart, isInCart } = useCart();

    if (!book) return null;

    // ÿ≠ÿßŸÑÿ© ÿßŸÑŸÉÿ™ÿßÿ®
    const isSold = book.status === "sold";
    const isUnavailable = book.status === "unavailable";

    return (
        <motion.div
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.4 }}
            className="bg-white rounded-2xl shadow-md hover:shadow-lg transition duration-300 overflow-hidden text-right border border-gray-100 relative"
        >
            {/* ÿµŸàÿ±ÿ© ÿßŸÑŸÉÿ™ÿßÿ® */}
            <div className="relative">
                <BookImage
                    images={book.images || []}
                    alt={book.title}
                    ratio="h-72"
                    fit="cover"
                    className="rounded-t-2xl"
                />

                {/* ÿ¥ÿßÿ±ÿ© ÿ¨ÿØŸäÿØ */}
                {book.isNew && (
                    <span className="absolute top-3 right-3 text-xs bg-red-600 text-white px-2 py-1 rounded-full shadow-md">
                        ÿ¨ÿØŸäÿØ
                    </span>
                )}

                {/* ÿ¥ÿßÿ±ÿ© ÿßŸÑÿ≠ÿßŸÑÿ© */}
                <span
                    className={`absolute top-3 left-3 text-xs px-2 py-1 rounded-full shadow-md ${
                        isSold || isUnavailable
                            ? "bg-gray-700 text-white"
                            : "bg-green-600 text-white"
                    }`}
                >
                    {isSold
                        ? "‚ùå ÿ™ŸÖ ÿßŸÑÿ®Ÿäÿπ"
                        : isUnavailable
                        ? "üö´ ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÅÿ±"
                        : "‚úÖ ŸÖÿ™ŸàŸÅÿ±"}
                </span>
            </div>

            {/* ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑŸÉÿ™ÿßÿ® */}
            <div className="p-4 space-y-3">
                <h3 className="text-lg md:text-xl font-semibold text-gray-800 leading-snug line-clamp-2">
                    {book.title}
                </h3>

                {book.author && (
                    <p className="text-gray-600 text-sm">
                        ‚úçÔ∏è <span className="font-medium">ÿßŸÑŸÖÿ§ŸÑŸÅ:</span> {book.author}
                    </p>
                )}

                {book.price && (
                    <p className="text-sm text-green-700 font-semibold">
                        üíµ ÿßŸÑÿ≥ÿπÿ±: {book.price}
                    </p>
                )}

                {/* ÿ≤ÿ± ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ */}
                <Link
                    to={`/book/${book.id}`}
                    className={`inline-block text-sm px-4 py-2 rounded-lg font-medium transition-all duration-200 ${
                        isSold || isUnavailable
                            ? "bg-gray-400 text-white cursor-not-allowed opacity-80"
                            : "bg-blue-700 text-white hover:bg-blue-800"
                    }`}
                    onClick={(e) => {
                        if (isSold || isUnavailable) e.preventDefault();
                    }}
                >
                    {isSold || isUnavailable ? "ÿ∫Ÿäÿ± ŸÖÿ™ÿßÿ≠" : "ÿπÿ±ÿ∂ ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ"}
                </Link>

                {/* ÿ≤ÿ± ÿßŸÑÿ≥ŸÑÿ© Ÿäÿ∏Ÿáÿ± ŸÅŸÇÿ∑ ŸÑŸà ÿßŸÑŸÉÿ™ÿßÿ® ŸÖÿ™ŸàŸÅÿ± */}
                {!isSold && !isUnavailable && (
                    <button
                        onClick={() =>
                            isInCart(book.id)
                                ? removeFromCart(book.id)
                                : addToCart(book)
                        }
                        className={`mt-2 w-full text-sm px-4 py-2 rounded-lg transition font-medium ${
                            isInCart(book.id)
                                ? "bg-red-100 text-red-600 hover:bg-red-200"
                                : "bg-green-100 text-green-700 hover:bg-green-200"
                        }`}
                    >
                        {isInCart(book.id)
                            ? "üóëÔ∏è ÿ•ÿ≤ÿßŸÑÿ© ŸÖŸÜ ÿßŸÑÿ≥ŸÑÿ©"
                            : "‚ûï ÿ£ÿ∂ŸÅ ÿ•ŸÑŸâ ÿßŸÑÿ≥ŸÑÿ©"}
                    </button>
                )}
            </div>
        </motion.div>
    );
}
