    const { favorites, removeFavorite } = useFavorites();

    return (
        <main className="px-4 md:px-12 py-10 text-right font-sans min-h-screen bg-gray-50">
            {/* ÿßŸÑÿπŸÜŸàÿßŸÜ */}
            <motion.h1
                initial={{ opacity: 0, y: -20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6 }}
                className="text-3xl md:text-4xl font-extrabold text-blue-800 mb-10 border-r-4 border-blue-600 pr-3"
            >
                ‚ù§Ô∏è ŸÉÿ™ÿ®Ÿä ÿßŸÑŸÖŸÅÿ∂ŸÑÿ©
            </motion.h1>

            {favorites.length > 0 ? (
                <motion.div
                    initial="hidden"
                    animate="show"
                    variants={{
                        hidden: { opacity: 0 },
                        show: { opacity: 1, transition: { staggerChildren: 0.15 } },
                    }}
                    className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8"
                >
                    {favorites.map((book) => (
                        <motion.div
                            key={book.id}
                            variants={{
                                hidden: { opacity: 0, y: 20 },
                                show: { opacity: 1, y: 0 },
                            }}
                            className="flex flex-col"
                        >
                            {/* ÿ®ÿ∑ÿßŸÇÿ© ÿßŸÑŸÉÿ™ÿßÿ® */}
                            <BookCard book={book} />

                            {/* ÿ≤ÿ± ÿ•ÿ≤ÿßŸÑÿ© ŸÖŸÜ ÿßŸÑŸÖŸÅÿ∂ŸÑÿ© */}
                            <button
                                onClick={() => removeFavorite(book.id)}
                                className="mt-3 w-full text-sm px-4 py-2 rounded-lg font-medium bg-red-50 text-red-600 border border-red-200 hover:bg-red-100 transition"
                            >
                                ‚ùå ÿ•ÿ≤ÿßŸÑÿ© ŸÖŸÜ ÿßŸÑŸÖŸÅÿ∂ŸÑÿ©
                            </button>
                        </motion.div>
                    ))}
                </motion.div>
            ) : (
                <motion.p
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ duration: 0.6 }}
                    className="text-gray-600 text-lg md:text-xl text-center py-16 bg-white rounded-xl shadow-sm"
                >
                    üì≠ ŸÑŸÖ ÿ™ŸÇŸÖ ÿ®ÿ•ÿ∂ÿßŸÅÿ© ÿ£Ÿä ŸÉÿ™ÿßÿ® ÿ•ŸÑŸâ ÿßŸÑŸÖŸÅÿ∂ŸÑÿ© ÿ≠ÿ™Ÿâ ÿßŸÑÿ¢ŸÜ.
                </motion.p>
            )}
        </main>
    );
}
