// src/context/FavoriteContext.jsx
import { createContext, useContext, useEffect, useState } from "react";
import toast from "react-hot-toast";

// ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑŸÄ Context
const FavoriteContext = createContext();

// ŸÖÿ≤ŸàÿØ ÿßŸÑŸÖŸÅÿ∂ŸÑÿ©
export function FavoriteProvider({ children }) {
    const [favorites, setFavorites] = useState(() => {
        // ÿ¨ŸÑÿ® ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÖŸÜ localStorage
        const saved = localStorage.getItem("favorites");
        return saved ? JSON.parse(saved) : [];
    });

    // ÿ™ÿ≠ÿØŸäÿ´ localStorage ÿπŸÜÿØ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÖŸÅÿ∂ŸÑÿ©
    useEffect(() => {
        localStorage.setItem("favorites", JSON.stringify(favorites));
    }, [favorites]);

    // ÿ•ÿ∂ÿßŸÅÿ© ŸÉÿ™ÿßÿ® ŸÑŸÑŸÖŸÅÿ∂ŸÑÿ©
   function addFavorite(book) {
    setFavorites((prev) => {
        if (!prev.some((b) => b.id === book.id)) {
            toast.success(`‚ù§Ô∏è ÿ™ŸÖÿ™ ÿ•ÿ∂ÿßŸÅÿ© "${book.title}" ÿ•ŸÑŸâ ÿßŸÑŸÖŸÅÿ∂ŸÑÿ©`);
            return [...prev, book]; // ŸÜÿÆÿ≤ŸÜ ÿßŸÑŸÉÿ™ÿßÿ® ŸÉŸÑŸá
        }
        return prev;
    });
}

function removeFavorite(bookId) {
    setFavorites((prev) => {
        const updated = prev.filter((b) => b.id !== bookId);
        toast.error("üóëÔ∏è ÿ™ŸÖÿ™ ÿ•ÿ≤ÿßŸÑÿ© ÿßŸÑŸÉÿ™ÿßÿ® ŸÖŸÜ ÿßŸÑŸÖŸÅÿ∂ŸÑÿ©");
        return updated;
    });
}

function isFavorite(bookId) {
    return favorites.some((b) => b.id === bookId);
}

    return (
        <FavoriteContext.Provider
            value={{ favorites, addFavorite, removeFavorite, isFavorite }}
        >
            {children}
        </FavoriteContext.Provider>
    );
}

// Hook ŸÑÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑŸÖŸÅÿ∂ŸÑÿ© ÿ®ÿ≥ŸáŸàŸÑÿ©
export function useFavorites() {
    return useContext(FavoriteContext);
}
