// src/components/BookImage.jsx
import { useState } from "react";
import { LazyLoadImage } from "react-lazy-load-image-component";
import "react-lazy-load-image-component/src/effects/blur.css";

export default function BookImage({
    src,
    alt,
    className = "",
    fit = "cover", // cover | contain
    ratio = "h-72", // مثال: h-72 أو aspect-[3/4]
}) {
    const [loaded, setLoaded] = useState(false);

    if (!src) {
        return (
            <div
                className={`relative overflow-hidden rounded-md bg-gray-200 ${ratio}`}
            >
                <img
                    src="/placeholder.png"
                    alt={alt || "غلاف الكتاب"}
                    className={`w-full h-full object-${fit}`}
                    loading="lazy"
                />
            </div>
        );
    }

    // توليد نسخة WebP + الأصلية
    const webpSrc = src.replace(/\.(jpg|jpeg|png)$/i, ".webp");
    const originalSrc = src;

    return (
        <div
            className={`relative overflow-hidden rounded-md bg-gray-100 ${ratio} ${className}`}
        >
            {/* Skeleton قبل التحميل */}
            {!loaded && (
                <div className="absolute inset-0 bg-gray-200 animate-pulse rounded-md" />
            )}

            <picture>
                <source srcSet={webpSrc} type="image/webp" />
                <LazyLoadImage
                    src={originalSrc}
                    alt={alt || "غلاف الكتاب"}
                    effect="blur"
                    afterLoad={() => setLoaded(true)}
                    className={`w-full h-full object-${fit} transition-opacity duration-500 ${loaded ? "opacity-100" : "opacity-0"
                        }`}
                />
            </picture>
        </div>
    );
}
