import { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { FiChevronRight, FiChevronLeft } from "react-icons/fi";

export default function BookImage({ images = [], alt, ratio = "h-72", fit = "cover", className = "" }) {
    const [index, setIndex] = useState(0);

    if (!images.length) {
        return (
            <div
                className={`flex items-center justify-center bg-gray-100 text-gray-400 text-sm ${ratio}`}
            >
                ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿµŸàÿ±ÿ©
            </div>
        );
    }

    const nextImage = () => {
        setIndex((prev) => (prev + 1) % images.length);
    };

    const prevImage = () => {
        setIndex((prev) => (prev - 1 + images.length) % images.length);
    };

    return (
        <div className={`relative overflow-hidden rounded-lg ${ratio}`}>
            {/* ‚úÖ ÿπÿ±ÿ∂ ÿßŸÑÿµŸàÿ±ÿ© ÿßŸÑÿ≠ÿßŸÑŸäÿ© ŸÖÿπ ÿ≠ÿ±ŸÉÿ© ÿßŸÜÿ™ŸÇÿßŸÑŸäÿ© ÿ£ŸÜŸäŸÇÿ© */}
            <AnimatePresence mode="wait">
                <motion.img
                    key={index}
                    src={images[index]}
                    alt={alt || "Book"}
                    className={`w-full h-full object-${fit} ${className}`}
                    initial={{ opacity: 0, scale: 1.02 }}
                    animate={{ opacity: 1, scale: 1 }}
                    exit={{ opacity: 0, scale: 0.98 }}
                    transition={{ duration: 0.4 }}
                />
            </AnimatePresence>

            {/* üîò ÿ£ÿ≤ÿ±ÿßÿ± ÿßŸÑÿ™ŸÜŸÇŸÑ (ÿ™ÿ∏Ÿáÿ± ŸÅŸÇÿ∑ ŸÑŸà ÿ£ŸÉÿ´ÿ± ŸÖŸÜ ÿµŸàÿ±ÿ©) */}
            {images.length > 1 && (
                <>
                    <button
                        onClick={prevImage}
                        className="absolute left-2 top-1/2 -translate-y-1/2 bg-white/80 text-gray-700 p-1.5 rounded-full hover:bg-white shadow-md transition"
                    >
                        <FiChevronLeft size={18} />
                    </button>
                    <button
                        onClick={nextImage}
                        className="absolute right-2 top-1/2 -translate-y-1/2 bg-white/80 text-gray-700 p-1.5 rounded-full hover:bg-white shadow-md transition"
                    >
                        <FiChevronRight size={18} />
                    </button>

                    {/* üîò ÿßŸÑŸÜŸÇÿßÿ∑ ÿ£ÿ≥ŸÅŸÑ ÿßŸÑÿµŸàÿ±ÿ© */}
                    <div className="absolute bottom-3 left-1/2 -translate-x-1/2 flex gap-1">
                        {images.map((_, i) => (
                            <span
                                key={i}
                                className={`w-2 h-2 rounded-full transition ${i === index ? "bg-blue-600" : "bg-gray-300"}`}
                            ></span>
                        ))}
                    </div>
                </>
            )}
        </div>
    );
}
