import React, { useEffect, useState } from "react";
import booksData from "../data/books.json"; // تأكد أن الملف موجود في هذا المسار

const Favorites = () => {
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    const storedFavorites = JSON.parse(localStorage.getItem("favorites")) || [];
    const favoriteBooks = booksData.filter(book => storedFavorites.includes(book.id));
    setFavorites(favoriteBooks);
  }, []);

  if (favorites.length === 0) {
    return <div className="text-center mt-10 text-xl">لا توجد كتب مفضلة حالياً</div>;
  }

  return (
    <div className="p-6 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
      {favorites.map(book => (
        <div
          key={book.id}
          className="border rounded-lg shadow-sm p-4 flex flex-col items-center"
        >
          <img src={book.cover} alt={book.title} className="w-40 h-60 object-cover mb-4" />
          <h3 className="text-lg font-semibold mb-1">{book.title}</h3>
          <p className="text-sm text-gray-500 mb-1">{book.author}</p>
          <p className="text-sm text-gray-500 mb-1">{book.category}</p>
          <p className="text-sm text-gray-600 mb-2">{book.price}</p>
          <p className="text-sm text-gray-700 text-center mb-3">{book.description}</p>
          <a
            href={book.facebookPost}
            target="_blank"
            rel="noopener noreferrer"
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            احجز الآن
          </a>
        </div>
      ))}
    </div>
  );
};

export default Favorites;
