import { useState } from "react";
import { ref, push } from "firebase/database";
import { db } from "../../firebase";
import toast, { Toaster } from "react-hot-toast";
import { motion } from "framer-motion";

export default function AdminDashboard() {
    const [title, setTitle] = useState("");
    const [author, setAuthor] = useState("");
    const [transl, setTransl] = useState("");
    const [type, setType] = useState("ÿπÿ±ÿ®Ÿä");
    const [category, setCategory] = useState("");
    const [price, setPrice] = useState("");
    const [HPaper, setHPaper] = useState("");
    const [description, setDescription] = useState("");
    const [folder, setFolder] = useState(""); // ŸÖÿ¨ŸÑÿØ ÿßŸÑÿµŸàÿ± ÿØÿßÿÆŸÑ public/images
    const [images, setImages] = useState([]); // ÿ±Ÿàÿßÿ®ÿ∑ ÿßŸÑÿµŸàÿ±
    const [loading, setLoading] = useState(false);

    // ‚úÖ ÿπŸÜÿØ ÿßÿÆÿ™Ÿäÿßÿ± ÿßŸÑÿµŸàÿ±
    const handleImageSelect = (files) => {
        if (!folder.trim()) {
            toast.error("‚ö†Ô∏è Ÿäÿ±ÿ¨Ÿâ ŸÉÿ™ÿßÿ®ÿ© ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ¨ŸÑÿØ ÿØÿßÿÆŸÑ public/images ÿ£ŸàŸÑÿßŸã (ŸÖÿ´ŸÑ 10-30)");
            return;
        }

        const urls = Array.from(files).map(
            (file) => `/images/${folder}/${file.name}`
        );
        setImages(urls);
        toast.success(`‚úÖ ÿ™ŸÖ ÿßÿÆÿ™Ÿäÿßÿ± ${urls.length} ÿµŸàÿ±ÿ©`);
    };

    // ‚úÖ ÿπŸÜÿØ ÿßŸÑÿ•ÿ±ÿ≥ÿßŸÑ
    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!title || !author || !category || images.length === 0) {
            toast.error("‚ö†Ô∏è Ÿäÿ±ÿ¨Ÿâ ŸÖŸÑÿ° ÿ¨ŸÖŸäÿπ ÿßŸÑÿ≠ŸÇŸàŸÑ ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ© Ÿàÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿµŸàÿ±!");
            return;
        }

        setLoading(true);

        const newBook = {
            id: Date.now(),
            title,
            author,
            transl,
            type,
            category,
            price: price || "ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ",
            HPaper: HPaper || "",
            description: description || "",
            images,
            isNew: true,
            status: "available",
            createdAt: Date.now(),
        };

        try {
            await push(ref(db, "books"), newBook);
            toast.success("üìö ÿ™ŸÖ ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑŸÉÿ™ÿßÿ® ÿ®ŸÜÿ¨ÿßÿ≠!");

            // ÿ•ÿπÿßÿØÿ© ÿ™ÿπŸäŸäŸÜ ÿßŸÑÿ≠ŸÇŸàŸÑ
            setTitle("");
            setAuthor("");
            setTransl("");
            setType("ÿπÿ±ÿ®Ÿä");
            setCategory("");
            setPrice("");
            setHPaper("");
            setDescription("");
            setImages([]);
            setFolder("");
        } catch (err) {
            toast.error("‚ùå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ•ÿ∂ÿßŸÅÿ©: " + err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="max-w-3xl mx-auto p-8 bg-white shadow-xl rounded-2xl mt-10">
            <Toaster />
            <motion.h2
                initial={{ opacity: 0, y: -20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
                className="text-3xl font-extrabold text-center text-blue-700 mb-6"
            >
                üìò ŸÑŸàÿ≠ÿ© ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÉÿ™ÿ®
            </motion.h2>

            <form onSubmit={handleSubmit} className="space-y-5">
                {/* üìö ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÉÿ™ÿßÿ® */}
                <div className="grid sm:grid-cols-2 gap-4">
                    <input
                        type="text"
                        placeholder="ÿπŸÜŸàÿßŸÜ ÿßŸÑŸÉÿ™ÿßÿ®"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        className="p-3 border rounded-md"
                        required
                    />
                    <input
                        type="text"
                        placeholder="ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ§ŸÑŸÅ"
                        value={author}
                        onChange={(e) => setAuthor(e.target.value)}
                        className="p-3 border rounded-md"
                        required
                    />
                </div>

                <input
                    type="text"
                    placeholder="ÿßŸÑŸÖÿ™ÿ±ÿ¨ŸÖ (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)"
                    value={transl}
                    onChange={(e) => setTransl(e.target.value)}
                    className="w-full p-3 border rounded-md"
                />

                <div className="grid sm:grid-cols-2 gap-4">
                    <select
                        value={type}
                        onChange={(e) => setType(e.target.value)}
                        className="p-3 border rounded-md"
                    >
