// ‚úÖ src/pages/Category.jsx
import { useParams, Link } from "react-router-dom";
import { motion } from "framer-motion";
import { useState, useEffect } from "react";
import {
    collection,
    query,
    orderBy,
    limit,
    startAfter,
    getDocs,
    where,
} from "firebase/firestore";
import { db } from "../firebase"; // ÿ™ÿ£ŸÉÿØ ŸÖŸÜ Ÿàÿ¨ŸàÿØ ÿ•ÿπÿØÿßÿØ Firebase ŸáŸÜÿß
import BookCard from "../components/BookCard";
import CategoryCard from "../components/CategoryCard";
import ScrollTopButton from "../components/ScrollTopButton";

const categories = ["ÿ±ŸàÿßŸäÿ©", "ÿ∫Ÿäÿ± ÿ±Ÿàÿßÿ¶Ÿä", "ŸÖÿ™ÿ±ÿ¨ŸÖ", "ÿπÿ±ÿ®Ÿä"];

export default function Category() {
    const { name } = useParams();
    const [books, setBooks] = useState([]);
    const [lastDoc, setLastDoc] = useState(null);
    const [loading, setLoading] = useState(false);
    const [hasMore, setHasMore] = useState(true);

    // üîπ ŸÅŸÑÿ™ÿ±ÿ© ÿßŸÑŸÅÿ¶ÿ©
    const normalizeCategory = (cat) => {
        if (!cat) return null;
        const n = cat.toLowerCase();
        if (n === "ŸÖÿ™ÿ±ÿ¨ŸÖ") return ["ÿ£ÿ¨ŸÜÿ®Ÿä", "ÿπÿßŸÑŸÖŸä", "ŸÖÿ™ÿ±ÿ¨ŸÖ"];
        if (n === "ÿπÿ±ÿ®Ÿä") return ["ÿπÿ±ÿ®Ÿä"];
        if (n === "ÿ∫Ÿäÿ± ÿ±Ÿàÿßÿ¶Ÿä") return ["ÿ∫Ÿäÿ± ÿ±Ÿàÿßÿ¶Ÿä", "ŸÅŸÉÿ±", "ÿ™ÿßÿ±ŸäÿÆ", "ÿØŸäŸÜ"];
        return [cat];
    };

    // üîπ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÉÿ™ÿ® (ÿ®ŸÄ pagination)
    const fetchBooks = async (isLoadMore = false) => {
        if (loading || (!hasMore && isLoadMore)) return;
        setLoading(true);

        try {
            const booksRef = collection(db, "books");
            const categoryFilter = normalizeCategory(name);
            let q;

            if (categoryFilter) {
                q = query(
                    booksRef,
                    where("category", "in", categoryFilter),
                    orderBy("status"), // available -> sold
                    orderBy("id", "desc"),
                    limit(20),
                    ...(lastDoc ? [startAfter(lastDoc)] : [])
                );
            } else {
                q = query(
                    booksRef,
                    orderBy("status"),
                    orderBy("id", "desc"),
                    limit(20),
                    ...(lastDoc ? [startAfter(lastDoc)] : [])
                );
            }

            const snapshot = await getDocs(q);

            if (!snapshot.empty) {
                const newBooks = snapshot.docs.map((doc) => ({
                    id: doc.id,
                    ...doc.data(),
                }));

                setBooks((prev) => (isLoadMore ? [...prev, ...newBooks] : newBooks));
                setLastDoc(snapshot.docs[snapshot.docs.length - 1]);
                if (snapshot.size < 20) setHasMore(false);
            } else {
                setHasMore(false);
            }
        } catch (error) {
            console.error("‚ùå ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ¨ŸÑÿ® ÿßŸÑŸÉÿ™ÿ®:", error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        // ÿ•ÿπÿßÿØÿ© ÿ∂ÿ®ÿ∑ ÿπŸÜÿØ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÅÿ¶ÿ©
        setBooks([]);
        setLastDoc(null);
        setHasMore(true);
        fetchBooks();
    }, [name]);

    return (
        <div className="max-w-7xl mx-auto px-4 py-10 text-right font-sans space-y-10">
            {/* üè∑Ô∏è ÿπŸÜŸàÿßŸÜ ÿßŸÑÿµŸÅÿ≠ÿ© */}
            <motion.h1
                initial={{ opacity: 0, x: 40 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.5 }}
                className="text-3xl font-extrabold text-blue-800 mb-2"
            >
                üõçÔ∏è ÿßŸÑÿ™ÿµŸÜŸäŸÅÿßÿ™
            </motion.h1>

            {/* üß≠ ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ™ÿµŸÜŸäŸÅÿßÿ™ */}
            <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.2 }}
                className="flex flex-wrap gap-3 justify-end"
            >
                <Link to="/category">
                    <CategoryCard title="ÿßŸÑŸÉŸÑ" />
                </Link>
                {categories.map((category) => (
                    <Link key={category} to={`/category/${category}`}>
                        <CategoryCard title={category} />
                    </Link>
                ))}
            </motion.div>

            {/* üìö ÿπŸÜŸàÿßŸÜ ÿßŸÑŸÇÿ≥ŸÖ */}
            <motion.h2
                initial={{ opacity: 0, x: 40 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.6, delay: 0.3 }}
                className="text-2xl font-semibold text-blue-700 border-r-4 border-blue-600 pr-3"
            >
                üìñ {name ? `ÿ™ÿµŸÜŸäŸÅ: ${name}` : "ÿ¨ŸÖŸäÿπ ÿßŸÑŸÉÿ™ÿ®"}
            </motion.h2>

            {/* üß© ÿπÿ±ÿ∂ ÿßŸÑŸÉÿ™ÿ® */}
            {books.length === 0 && !loading ? (
                <p className="text-gray-600 text-lg text-center py-10">
                    üì≠ ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÉÿ™ÿ® ÿ∂ŸÖŸÜ Ÿáÿ∞ÿß ÿßŸÑÿ™ÿµŸÜŸäŸÅ ÿ≠ÿßŸÑŸäŸãÿß.
                </p>
            ) : (
                <motion.div
                    initial="hidden"
                    animate="visible"
                    variants={{
                        hidden: { opacity: 0 },
                        visible: {
                            opacity: 1,
                            transition: { staggerChildren: 0.1 },
                        },
                    }}
                    className="grid gap-8 sm:grid-cols-2 lg:grid-cols-3"
                >
                    {books.map((book) => (
                        <motion.div
                            key={book.id}
                            variants={{
                                hidden: { opacity: 0, y: 30 },
                                visible: { opacity: 1, y: 0 },
                            }}
                        >
                            <BookCard book={book} />
                        </motion.div>
                    ))}
                </motion.div>
            )}

            {/* ‚è≥ ÿ≤ÿ± ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÖÿ≤ŸäÿØ */}
            {hasMore && (
                <div className="flex justify-center py-10">
                    <button
                        onClick={() => fetchBooks(true)}
                        disabled={loading}
                        className="bg-blue-600 text-white px-6 py-3 rounded-xl hover:bg-blue-700 transition-all shadow-md disabled:bg-gray-400"
                    >
                        {loading ? "ÿ¨ÿßÿ±Ÿê ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ..." : "ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÖÿ≤ŸäÿØ üìö"}
                    </button>
                </div>
            )}

            {/* üîù ÿ≤ÿ± ÿßŸÑÿ±ÿ¨Ÿàÿπ ŸÑŸÑÿ£ÿπŸÑŸâ */}
            <ScrollTopButton />
        </div>
    );
}
