// src/pages/Admin/AdminLogin.jsx
import { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function AdminLogin() {
    const [password, setPassword] = useState("");
    const navigate = useNavigate();

    // كلمة السر بسيطة (لاحقًا نربطها بفirebase Auth)
    const correctPassword = import.meta.env.VITE_ADMIN_PASS || "admin123";

    const handleLogin = (e) => {
        e.preventDefault();
        if (password === correctPassword) {
            localStorage.setItem("isAdmin", "true");
            navigate("/admin/dashboard");
        } else {
            alert("❌ كلمة المرور غير صحيحة");
        }
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-100">
            <form
                onSubmit={handleLogin}
                className="bg-white shadow-lg rounded-2xl p-8 w-full max-w-sm"
            >
                <h2 className="text-xl font-semibold text-center mb-6">
                    تسجيل دخول المشرف
                </h2>
                <input
                    type="password"
                    placeholder="كلمة المرور"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className="border border-gray-300 rounded-lg p-2 w-full mb-4 focus:outline-none focus:ring-2 focus:ring-blue-400"
                />
                <button
                    type="submit"
                    className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 transition"
                >
                    دخول
                </button>
            </form>
        </div>
    );
}
